import dotenv from 'dotenv';
import { getWellness } from '../src/lib/intervals';

dotenv.config();

async function checkApi() {
    const apiKey = process.env.INTERVALS_API_KEY;
    const athleteId = process.env.INTERVALS_ATHLETE_ID;

    if (!apiKey || !athleteId) {
        console.error("❌ Missing INTERVALS_API_KEY or INTERVALS_ATHLETE_ID in .env");
        process.exit(1);
    }

    console.log(`Connecting to Intervals.icu for Athlete ${athleteId}...`);

    try {
        const today = new Date().toISOString().split('T')[0];
        const data = await getWellness(today, apiKey, athleteId);

        if (!data) {
            console.log("⚠️ No wellness data found for today.");
            return;
        }

        console.log("✅ Data Received!");
        console.log("--- Fields Present ---");

        // Check specific fields requested
        const keys = Object.keys(data); // Assuming normalized or raw
        // getWellness returns a normalized object if we look at lib/intervals.ts
        // Wait, getWellness calls API and returns... let's check lib/intervals via imports?
        // The script imports from src/lib/intervals.

        // Check for GPE Requirements
        const required = ['restingHR', 'hrv', 'sleepScore', 'comments', 'spO2', 'bodyBattery'];
        const missing = required.filter(k => !(k in data) && !(data as any)[k]);

        if (missing.length === 0) {
            console.log("✅ All Core GPE fields present.");
        } else {
            console.log("⚠️ Missing Fields:", missing.join(', '));
        }

        console.log("--- Sample Data ---");
        console.log(JSON.stringify(data, null, 2));

    } catch (e) {
        console.error("❌ API Call Failed", e);
    }
}

checkApi();

"use server";

import prisma from "@/lib/prisma";
import { revalidatePath } from "next/cache";
import { z } from "zod";
import { createClient } from "@/utils/supabase/server";

// --- Types & Schemas ---
const CreateGuildSchema = z.object({
  name: z.string().min(3).max(20),
  description: z.string().optional(),
});

async function getAuthenticatedUser() {
  const supabase = await createClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) throw new Error("Unauthorized");
  return user;
}

/**
 * Create a new guild and assign the creator as a member.
 */
export async function createGuildAction(data: { name: string; description?: string }) {
  try {
    const user = await getAuthenticatedUser();
    const validated = CreateGuildSchema.parse(data);

    const guild = await prisma.$transaction(async (tx) => {
      const g = await tx.guild.create({
        data: {
          name: validated.name,
          tag: validated.name.substring(0, 4).toUpperCase(), // Auto-generate tag to satisfy schema
          description: validated.description,
          leaderId: user.id,
          isPublic: true,
          minLevel: 1,
          xp: 0,
        },
      });

      await tx.user.update({
        where: { id: user.id },
        data: { guildId: g.id },
      });

      return g;
    });

    revalidatePath("/dashboard");
    return { success: true, guild };
  } catch (_error) {
    return { success: false, error: "Failed to create guild" };
  }
}

/**
 * Join an existing guild.
 */
export async function joinGuildAction(guildId: string) {
  try {
    const user = await getAuthenticatedUser();

    await prisma.user.update({
      where: { id: user.id },
      data: { guildId },
    });

    revalidatePath("/dashboard");
    return { success: true };
  } catch (e) {
    console.error(e);
    return { success: false, error: "Failed to join guild" };
  }
}

/**
 * Get Guild details including current Raid.
 */
export async function getGuildAction() {
  // We handle auth gracefully here returning null if no user
  const supabase = await createClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();
  if (!user) return null;

  const dbUser = await prisma.user.findUnique({
    where: { id: user.id },
    include: { guild: { include: { raids: true, members: { take: 10 } } } },
  });

  if (!dbUser?.guild) return null;

  // Find active raid
  // Simple logic: the one that ends in future
  const activeRaid = dbUser.guild.raids.find(
    (r) => new Date(r.endDate) > new Date(),
  );

  return {
    ...dbUser.guild,
    activeRaid,
  };
}

/**
 * Start a new Raid (Admin/Leader function, or auto-generated).
 */
export async function startRaidAction(
  guildId: string,
  bossName: string,
  hp: number,
  durationDays: number = 7,
) {
  try {
    const user = await getAuthenticatedUser();

    // Verify user is leader of the guild
    const guild = await prisma.guild.findUnique({
      where: { id: guildId },
      select: { leaderId: true },
    });

    if (!guild) {
      return { success: false, error: "Guild not found" };
    }

    if (guild.leaderId !== user.id) {
      return { success: false, error: "Unauthorized: Only guild leader can start raids" };
    }

    const endDate = new Date();
    endDate.setDate(endDate.getDate() + durationDays);

    const raid = await prisma.guildRaid.create({
      data: {
        guildId,
        bossName,
        totalHp: hp,
        currentHp: hp,
        endDate,
      },
    });

    revalidatePath("/dashboard");
    return { success: true, raid };
  } catch (_error) {
    console.error("Failed to start raid:", _error);
    return { success: false, error: "Failed to start raid" };
  }
}

/**
 * Contribute damage to the active raid.
 */
export async function contributeToRaidAction(raidId: string, damage: number) {
  try {
    const user = await getAuthenticatedUser();

    const raid = await prisma.guildRaid.findUnique({ where: { id: raidId } });
    if (!raid || Number(raid.currentHp) <= 0)
      return { success: false, error: "Raid ended or invalid" };

    // Assuming currentHp is BigInt in Prisma schema?
    // In previous view it was accessed as raid.currentHp <= 0.
    // If it is BigInt, we need BigInt literals or conversion.
    // Let's assume it is Int or BigInt.
    // If Int, Math.max works. If BigInt, need logic.
    // Usually HP is Int unless huge.
    // Let's safe cast.

    // Check types from view_file:
    // 97: totalHp: hp,
    // 115: const newHp = Math.max(0, raid.currentHp - damage);
    // This suggests it is number, not BigInt.
    // If it was BigInt, Math.max would fail or need explicit handling.
    // I will keep it as number to match previous code style unless I know schema.

    const newHp = Math.max(0, Number(raid.currentHp) - damage);

    await prisma.$transaction([
      prisma.guildRaidContribution.create({
        data: {
          raidId,
          userId: user.id,
          damage,
        },
      }),
      prisma.guildRaid.update({
        where: { id: raidId },
        data: { currentHp: newHp },
      }),
    ]);

    if (newHp === 0) {
      if (raid.currentHp > 0) {
        // Boss killed logic
        const { checkAchievementsAction } =
          await import("@/actions/progression/achievements");
        await checkAchievementsAction(user.id);
      }
    }

    revalidatePath("/dashboard");
    return { success: true, damageDealt: damage, bossDead: newHp === 0 };
  } catch (e) {
    console.error(e);
    return { success: false, error: "Attack failed" };
  }
}

"use server";

import {
  getWellness,
  getActivities,
  getEvents,
  getAthleteSettings,
} from "@/lib/intervals";
import { createClient } from "@/utils/supabase/server";
import prisma from "@/lib/prisma";
import { IntervalsWellness, IntervalsActivity, IntervalsEvent } from "@/types";

async function getIntervalsCredentials() {
  const supabase = await createClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) {
    throw new Error("Unauthorized");
  }

  const dbUser = await prisma.user.findUnique({
    where: { id: user.id },
    select: { intervalsApiKey: true, intervalsAthleteId: true },
  });

  if (!dbUser?.intervalsApiKey || !dbUser?.intervalsAthleteId) {
    throw new Error("Intervals.icu not connected");
  }

  return {
    apiKey: dbUser.intervalsApiKey,
    athleteId: dbUser.intervalsAthleteId,
  };
}

export async function getWellnessAction(
  date: string,
): Promise<IntervalsWellness> {
  try {
    const { apiKey, athleteId } = await getIntervalsCredentials();
    const data = await getWellness(date, apiKey, athleteId);

    if (!data || Array.isArray(data)) return {} as IntervalsWellness;

    // Map Lib type (WellnessData) to App type (IntervalsWellness)
    // lib/intervals.ts uses 'readiness' for bodyBattery, type uses 'bodyBattery'
    return {
      id: data?.id,
      hrv: data?.hrv,
      restingHR: data?.restingHR,
      sleepScore: data?.sleepScore,
      sleepSecs: data?.sleepSecs,
      bodyBattery: data?.readiness,
      vo2max: data?.vo2max,
      ctl: data?.ctl,
      atl: data?.atl,
      tsb: data?.tsb,
      ramp_rate: data?.rampRate,
    } as IntervalsWellness;
  } catch (error: any) {
    console.warn("Server Action Intervals Wellness Error:", error.message);
    return {} as IntervalsWellness;
  }
}

export async function getWellnessRangeAction(
  startDate: string,
  endDate: string,
): Promise<IntervalsWellness[]> {
  try {
    const { apiKey, athleteId } = await getIntervalsCredentials();
    const data = await getWellness(startDate, apiKey, athleteId, endDate);

    if (!Array.isArray(data)) return [];

    return data.map(d => ({
      id: d.id,
      hrv: d.hrv,
      restingHR: d.restingHR,
      sleepScore: d.sleepScore,
      sleepSecs: d.sleepSecs,
      bodyBattery: d.readiness,
      vo2max: d.vo2max,
      ctl: d.ctl,
      atl: d.atl,
      tsb: d.tsb,
      ramp_rate: d.rampRate,
    } as IntervalsWellness));
  } catch (error: any) {
    console.error("Server Action Intervals Wellness Range Error:", error.message);
    return [];
  }
}

export async function getActivitiesAction(
  startDate: string,
  endDate: string,
): Promise<IntervalsActivity[]> {
  try {
    const { apiKey, athleteId } = await getIntervalsCredentials();
    const data = await getActivities(startDate, endDate, apiKey, athleteId);
    return data as unknown as IntervalsActivity[];
  } catch (error: any) {
    console.error("Server Action Intervals Activities Error:", error.message);
    return [];
  }
}

export async function getEventsAction(
  startDate: string,
  endDate: string,
): Promise<IntervalsEvent[]> {
  try {
    const { apiKey, athleteId } = await getIntervalsCredentials();
    const data = await getEvents(startDate, endDate, apiKey, athleteId);
    return data as unknown as IntervalsEvent[];
  } catch (_error: any) {
    return [];
  }
}

export async function getAthleteSettingsAction() {
  try {
    const { apiKey, athleteId } = await getIntervalsCredentials();
    const data = await getAthleteSettings(apiKey, athleteId);
    return data;
  } catch (error: any) {
    console.error("Server Action Intervals Settings Error:", error.message);
    return null;
  }
}

"use server";

/**
 * Strava Integration Server Actions
 *
 * Handles the OAuth2 flow, token management (exchange & refresh),
 * and data synchronization between Strava and IronForge.
 *
 * @module actions/strava
 */
import { createClient } from "@/utils/supabase/server";
import prisma from "@/lib/prisma";
import axios from "axios";
import { revalidatePath } from "next/cache";
import {
  StravaTokenResponse,
  StravaActivity,
  mapStravaActivityToCardioLog,
} from "@/lib/strava";
import { processUserCardioActivity } from "@/actions/pvp/duel";

// Constants removed for lazy evaluation
// const STRAVA_CLIENT_ID = process.env.STRAVA_CLIENT_ID;
// const STRAVA_CLIENT_SECRET = process.env.STRAVA_CLIENT_SECRET;

/**
 * Generates the Strava OAuth2 authorization URL.
 *
 * dynamically determines the redirect URI based on the host header,
 * enabling support for both local development and production environments.
 *
 * @returns {Promise<string>} The full authorization URL.
 * @throws {Error} If STRAVA_CLIENT_ID is missing from environment variables.
 */
export async function getStravaAuthUrlAction() {
  const STRAVA_CLIENT_ID = process.env.STRAVA_CLIENT_ID;
  if (!STRAVA_CLIENT_ID) throw new Error("Missing STRAVA_CLIENT_ID");

  const scope = "read,activity:read_all,activity:write";
  // Dynamically determining host might be unreliable in server actions without headers(),
  // but we can try.
  const { headers } = await import("next/headers");
  const headersList = await headers();
  const host = headersList.get("host") || "localhost:3000";
  const protocol = host.includes("localhost") ? "http" : "https";
  const redirectUri = `${protocol}://${host}/settings`;

  const url = `https://www.strava.com/oauth/authorize?client_id=${STRAVA_CLIENT_ID}&response_type=code&redirect_uri=${encodeURIComponent(redirectUri)}&approval_prompt=force&scope=${scope}`;
  return url;
}

/**
 * Exchanges a Strava authorization code for an access token.
 *
 * Persists the access token, refresh token, and expiration time to the user's profile.
 *
 * @param {string} code - The authorization code returned from Strava.
 * @returns {Promise<{success: boolean, error?: string}>} Success status or error message.
 */
export async function exchangeStravaTokenAction(code: string) {
  const STRAVA_CLIENT_ID = process.env.STRAVA_CLIENT_ID;
  const STRAVA_CLIENT_SECRET = process.env.STRAVA_CLIENT_SECRET;

  if (!STRAVA_CLIENT_ID || !STRAVA_CLIENT_SECRET) {
    return {
      success: false,
      error: "Strava credentials not configured on server",
    };
  }

  const supabase = await createClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) return { success: false, error: "Unauthorized" };

  try {
    const response = await axios.post<StravaTokenResponse>(
      "https://www.strava.com/oauth/token",
      {
        client_id: STRAVA_CLIENT_ID,
        client_secret: STRAVA_CLIENT_SECRET,
        code,
        grant_type: "authorization_code",
      },
    );

    const { access_token, refresh_token, expires_at, athlete } = response.data;

    await prisma.user.update({
      where: { id: user.id },
      data: {
        stravaAccessToken: access_token,
        stravaRefreshToken: refresh_token,
        stravaExpiresAt: expires_at,
        stravaAthleteId: athlete.id.toString(),
      },
    });

    revalidatePath("/settings");
    return { success: true };
  } catch (error: unknown) {
    if (axios.isAxiosError(error)) {
      console.error("Strava Token Exchange Error:", error.response?.data || error.message);
    } else {
      console.error("Strava Token Exchange Error:", error);
    }
    return { success: false, error: "Failed to exchange token with Strava" };
  }
}

export async function disconnectStravaAction() {
  const supabase = await createClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) return { success: false, error: "Unauthorized" };

  try {
    const dbUser = await prisma.user.findUnique({ where: { id: user.id } });

    if (dbUser?.stravaAccessToken) {
      // Optional: Revoke token
      try {
        await axios.post("https://www.strava.com/oauth/deauthorize", {
          access_token: dbUser.stravaAccessToken,
        });
      } catch (_e) {
        // Ignore deauth failure
      }
    }

    await prisma.user.update({
      where: { id: user.id },
      data: {
        stravaAccessToken: null,
        stravaRefreshToken: null,
        stravaExpiresAt: null,
        stravaAthleteId: null,
      },
    });

    revalidatePath("/settings");
    return { success: true };
  } catch (error: any) {
    return { success: false, error: error.message };
  }
}

async function getValidAccessToken(userId: string) {
  const user = await prisma.user.findUnique({ where: { id: userId } });
  if (!user || !user.stravaAccessToken) return null;

  const now = Math.floor(Date.now() / 1000);

  // If expired or expiring soon (within 5 mins), refresh
  if (user.stravaExpiresAt && user.stravaExpiresAt < now + 300) {
    if (!user.stravaRefreshToken) return null;

    try {
      const response = await axios.post<StravaTokenResponse>(
        "https://www.strava.com/oauth/token",
        {
          client_id: process.env.STRAVA_CLIENT_ID,
          client_secret: process.env.STRAVA_CLIENT_SECRET,
          grant_type: "refresh_token",
          refresh_token: user.stravaRefreshToken,
        },
      );

      const { access_token, refresh_token, expires_at } = response.data;

      await prisma.user.update({
        where: { id: userId },
        data: {
          stravaAccessToken: access_token,
          stravaRefreshToken: refresh_token,
          stravaExpiresAt: expires_at,
        },
      });

      return access_token;
    } catch (error) {
      console.error("RefreshToken Error:", error);
      return null;
    }
  }

  return user.stravaAccessToken;
}

/**
 * Syncs recent activities from Strava to IronForge.
 *
 * Fetches the last 30 activities. Checks for duplicates using `intervalsId` (prefixed with 'strava_').
 * Creates new `CardioLog` entries for new activities and awards rewards (XP, Gold, Kinetic Energy).
 *
 * @returns {Promise<{success: boolean, count?: number, error?: string}>} Sync result and count of new activities.
 */
export async function syncStravaActivitiesAction() {
  const supabase = await createClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) return { success: false, error: "Unauthorized" };

  const token = await getValidAccessToken(user.id);
  if (!token)
    return { success: false, error: "Strava not connected or token invalid" };

  try {
    // Fetch last 30 activities
    const response = await axios.get<StravaActivity[]>(
      "https://www.strava.com/api/v3/athlete/activities",
      {
        headers: { Authorization: `Bearer ${token}` },
        params: { per_page: 30 },
      },
    );

    const activities = response.data;
    let syncedCount = 0;

    for (const activity of activities) {
      // Check if exists
      const existing = await prisma.cardioLog.findUnique({
        where: { intervalsId: `strava_${activity.id}` }, // Reusing this field for external ID
      });

      if (!existing) {
        const logData = mapStravaActivityToCardioLog(activity, user.id);

        // Create Log
        await prisma.cardioLog.create({
          data: logData,
        });

        // Trigger Duel Updates
        // distance is in meters in logData? mapStravaActivityToCardioLog likely returns it.
        // Assuming logData structure matches CardioLog which doesn't have distance? 
        // Wait, CardioLog schema doesn't have distance column (only type, duration, load).
        // Let me check schema again. CardioLog has duration. Duel needs distance.
        // I need to fetch distance from activity.
        const distanceKm = activity.distance / 1000;
        const durationMin = activity.moving_time / 60;
        await processUserCardioActivity(user.id, activity.type, distanceKm, durationMin);

        // Award Rewards (Simple: 1 XP per minute, 1 Gold per minute)
        // This logic should ideally be centralized in a 'RewardService'
        const minutes = Math.floor(logData.duration / 60);
        if (minutes > 0) {
          await prisma.user.update({
            where: { id: user.id },
            data: {
              totalExperience: { increment: minutes },
              gold: { increment: minutes },
              kineticEnergy: { increment: Math.floor(minutes / 2) },
            },
          });
        }

        syncedCount++;
      }
    }

    revalidatePath("/");
    return { success: true, count: syncedCount };
  } catch (error: unknown) {
    console.error("Sync Error:", error);
    const message = error instanceof Error ? error.message : "Sync failed";
    return { success: false, error: message };
  }
}

export async function uploadToStravaAction(formData: FormData) {
  const supabase = await createClient();
  const {
    data: { user },
  } = await supabase.auth.getUser();

  if (!user) return { success: false, error: "Unauthorized" };

  const token = await getValidAccessToken(user.id);
  if (!token)
    return { success: false, error: "Strava not connected or token invalid" };

  const file = formData.get("file") as File;
  if (!file) return { success: false, error: "No file provided" };

  try {
    // Convert File to Blob/Buffer for Axios
    const arrayBuffer = await file.arrayBuffer();
    const buffer = Buffer.from(arrayBuffer);

    // Append raw data. Note: In Node environment with axios + form-data,
    // passing a buffer often requires a filename.
    // But since we are using native FormData in Next.js Server Actions (Node 18+),
    // we might need to handle the axios post carefully.
    // Actually, let's use standard fetch for the upload to handle FormData natively if possible,
    // or construct properly for axios.

    // Simpler approach with Axios:
    const form = new FormData();
    form.append("file", new Blob([buffer]), file.name);
    form.append("data_type", file.name.endsWith(".fit") ? "fit" : "gpx");

    // NOTE: Node's FormData (undici) might behave differently with axios.
    // Let's use the native fetch which Next.js polyfills/supports well.

    const stravaFormData = new FormData();
    stravaFormData.append("file", new Blob([buffer]), file.name);
    stravaFormData.append(
      "data_type",
      file.name.endsWith(".fit") ? "fit" : "gpx",
    );

    const response = await fetch("https://www.strava.com/api/v3/uploads", {
      method: "POST",
      headers: {
        Authorization: `Bearer ${token}`,
      },
      body: stravaFormData,
    });

    if (!response.ok) {
      const err = await response.json();
      throw new Error(err.message || "Upload failed");
    }

    const result = await response.json();
    return { success: true, uploadId: result.id, status: result.status };
  } catch (error: any) {
    console.error("Upload Error:", error);
    return { success: false, error: error.message };
  }
}

"use server";

import { z } from "zod";
import prisma from "@/lib/prisma";
import { GeminiService } from "@/services/gemini";
import { revalidatePath } from "next/cache";

// --- Types ---



// --- Actions ---

/**
 * Sends a message to the Oracle and gets a response.
 * Persists conversation to DB (OracleMessage).
 */
export async function chatWithOracleAction(userId: string, message: string) {
    try {
        if (!userId) throw new Error("Unauthorized");

        // 1. Fetch Context
        const user = await prisma.user.findUnique({
            where: { id: userId },
            include: {
                titan: true,
                wardensManifest: true,
            }
        });

        if (!user) throw new Error("User not found");

        // 2. Fetch recent conversation history
        const recentHistory = await prisma.oracleMessage.findMany({
            where: { userId },
            orderBy: { timestamp: "desc" },
            take: 10,
        });
        // Reverse to chronological order
        const history = recentHistory.reverse().map(m => ({
            role: m.role,
            content: m.content
        }));

        // 3. Save User Message
        await prisma.oracleMessage.create({
            data: {
                userId,
                role: "user",
                content: message,
            },
        });

        // 4. Generate Bio Context for Oracle
        const bioContext = `
      Hero: ${user.heroName || "Titan"} (Lvl ${user.level})
      Faction: ${user.faction}
      Path: ${user.activePath}
      Injured: ${user.titan?.isInjured ? "YES" : "NO"}
      Energy: ${user.titan?.currentEnergy || 0}/${user.titan?.maxEnergy || 100}
      Mood: ${user.titan?.mood || "NEUTRAL"}
    `;

        // 5. Call Gemini
        const responseText = await GeminiService.chat(message, history, bioContext);

        // 6. Save Oracle Response
        await prisma.oracleMessage.create({
            data: {
                userId,
                role: "agent",
                content: responseText,
            },
        });

        revalidatePath("/oracle");
        return { success: true, response: responseText };

    } catch (error: any) {
        console.error("Oracle Chat Error:", error);
        return { success: false, error: error.message };
    }
}

/**
 * Retrieves conversation history for the UI.
 */
export async function getOracleConversationAction(userId: string, limit = 50) {
    try {
        const messages = await prisma.oracleMessage.findMany({
            where: { userId },
            orderBy: { timestamp: "asc" },
            take: limit,
        });
        return { success: true, data: messages };
    } catch (error: any) {
        return { success: false, error: error.message };
    }
}

/**
 * Clears the conversation history (context reset).
 */
export async function clearOracleHistoryAction(userId: string) {
    try {
        await prisma.oracleMessage.deleteMany({
            where: { userId },
        });
        revalidatePath("/oracle");
        return { success: true };
    } catch (error: any) {
        return { success: false, error: error.message };
    }
}

'use server';

import prisma from '@/lib/prisma';
import { createClient } from '@/utils/supabase/server';
import { revalidatePath } from 'next/cache';
import { TrainingContextService } from '@/services/data/TrainingContextService';
import { GeminiService } from '@/services/gemini';

/**
 * Sends a message to the Oracle (Gemini) and persists the interaction.
 */
export async function chatWithOracleAction(message: string) {
    try {
        const supabase = await createClient();
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) throw new Error("Unauthorized");

        // 1. Persist User Message
        await prisma.oracleMessage.create({
            data: {
                userId: user.id,
                role: 'user',
                content: message
            }
        });

        // 2. Fetch Context
        const context = await TrainingContextService.getTrainingContext(user.id);
        const bioString = `
            Readiness: ${context.readiness}
            CNS Fatigue: ${context.cnsFatigue}
            Cardio Stress: ${context.cardioStress}
            Warnings: ${context.warnings.join(", ") || "None"}
        `;

        // 3. Get History
        const messages = await prisma.oracleMessage.findMany({
            where: { userId: user.id },
            orderBy: { timestamp: 'desc' },
            take: 6
        });
        const history = messages.reverse().map(m => ({
            role: m.role,
            content: m.content
        }));

        // 4. Call Generative AI
        const oracleResponse = await GeminiService.chat(message, history, bioString);

        // 5. Persist Oracle Response
        await prisma.oracleMessage.create({
            data: {
                userId: user.id,
                role: 'oracle',
                content: oracleResponse
            }
        });

        revalidatePath('/oracle');
        return { success: true, message: oracleResponse };

    } catch (error) {
        console.error("Oracle Chat Error:", error);
        return { success: false, message: "The Oracle is silent." };
    }
}

/**
 * Retrieves the recent chat history for the user.
 */
export async function getOracleHistoryAction(limit = 20) {
    try {
        const supabase = await createClient();
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) return [];

        const messages = await prisma.oracleMessage.findMany({
            where: { userId: user.id },
            orderBy: { timestamp: 'desc' },
            take: limit
        });

        return messages.reverse();
    } catch (_error) {
        return [];
    }
}

