"use server";

import { prisma } from "@/lib/prisma";

interface BackupData {
  version: string;
  createdAt: string;
  userId: string;
  heroName: string;
  profile: unknown;
  titan: unknown;
  workoutHistory: unknown[];
  achievements: string[];
  settings: unknown;
  equipment: unknown[];
}

interface RestoreResult {
  success: boolean;
  message: string;
  itemsRestored?: {
    workouts: number;
    achievements: number;
    equipment: number;
  };
}

const BACKUP_VERSION = "1.0.0";

/**
 * Create a full backup of user data.
 */
export async function createBackupAction(
  userId: string,
): Promise<BackupData | null> {
  try {
    const user = await prisma.user.findUnique({
      where: { id: userId },
      include: {
        titan: true,
      },
    });

    if (!user) return null;

    const exerciseLogs = await prisma.exerciseLog.findMany({
      where: { userId },
      orderBy: { date: "desc" },
    });

    const cardioLogs = await prisma.cardioLog.findMany({
      where: { userId },
      orderBy: { date: "desc" },
    });

    const backup: BackupData = {
      version: BACKUP_VERSION,
      createdAt: new Date().toISOString(),
      userId: user.id,
      heroName: user.heroName || "Unknown",
      profile: {
        level: user.level,
        totalXp: user.totalExperience,
        currentStreak: user.titan?.streak || 0,
        createdAt: user.createdAt,
      },
      titan: user.titan
        ? {
          name: user.titan.name,
          strength: user.titan.strength,
          vitality: user.titan.vitality,
          endurance: user.titan.endurance,
          agility: user.titan.agility,
          willpower: user.titan.willpower,
        }
        : null,
      workoutHistory: [
        ...exerciseLogs.map((log) => ({
          type: "STRENGTH",
          exerciseId: log.exerciseId,
          date: log.date,
          sets: log.sets,
          notes: log.notes,
        })),
        ...cardioLogs.map((log) => ({
          type: "CARDIO",
          activityType: log.type,
          date: log.date,
          duration: log.duration,
        })),
      ],
      achievements: [], // Would fetch from achievements table
      settings: {
        // User preferences
      },
      equipment: [], // Would fetch from equipment table
    };

    return backup;
  } catch (error) {
    console.error("Error creating backup:", error);
    return null;
  }
}

/**
 * Restore user data from backup.
 */
export async function restoreBackupAction(
  userId: string,
  backupJson: string,
): Promise<RestoreResult> {
  try {
    const backup: BackupData = JSON.parse(backupJson);

    // Validate backup version
    if (!backup.version || backup.version !== BACKUP_VERSION) {
      return {
        success: false,
        message: `Incompatible backup version: ${backup.version}. Expected: ${BACKUP_VERSION}`,
      };
    }

    // In production, restore data to database
    console.log(`Restoring backup for ${userId} from ${backup.createdAt}`);
    console.log(`Workouts to restore: ${backup.workoutHistory.length}`);

    return {
      success: true,
      message: "Backup restored successfully",
      itemsRestored: {
        workouts: backup.workoutHistory.length,
        achievements: backup.achievements.length,
        equipment: backup.equipment.length,
      },
    };
  } catch (error) {
    console.error("Error restoring backup:", error);
    return {
      success: false,
      message:
        error instanceof Error ? error.message : "Failed to parse backup file",
    };
  }
}

/**
 * Download backup as JSON file.
 */
export function generateBackupFilename(heroName: string): string {
  const date = new Date().toISOString().split("T")[0];
  const safeName = heroName.replace(/[^a-zA-Z0-9]/g, "_");
  return `ironforge_backup_${safeName}_${date}.json`;
}

/**
 * Validate backup file structure.
 */
export function validateBackupFile(content: string): {
  valid: boolean;
  error?: string;
} {
  try {
    const data = JSON.parse(content);

    if (!data.version) return { valid: false, error: "Missing version field" };
    if (!data.userId) return { valid: false, error: "Missing userId field" };
    if (!data.createdAt)
      return { valid: false, error: "Missing createdAt field" };
    if (!Array.isArray(data.workoutHistory))
      return { valid: false, error: "Invalid workoutHistory" };

    return { valid: true };
  } catch (_e) {
    return { valid: false, error: "Invalid JSON format" };
  }
}

import React from "react";
// Force Rebuild
import { createClient } from "@/utils/supabase/server";
import { cookies, headers } from "next/headers";
import { redirect } from "next/navigation";
import prisma from "@/lib/prisma";
import {
  getHevyTemplatesAction,
  getHevyRoutinesAction,
  getHevyWorkoutHistoryAction,
} from "@/actions/integrations/hevy";
import { runFullAudit } from "@/services/auditorOrchestrator";
import {
  getActivitiesAction,
  getWellnessAction,
  getEventsAction,
} from "@/actions/integrations/intervals";
import { AnalyticsService } from "@/services/analytics";
import { ProgressionService } from "@/services/progression";
import { OracleService } from "@/services/oracle";
import { RecoveryService } from "@/services/bio/RecoveryService";
import DashboardClient from "@/features/dashboard/DashboardClient";
import { getActiveChallengesAction } from "@/actions/systems/challenges";
import { ensureTitanAction, syncTitanStateWithWellness } from "@/actions/titan/core";
import { ensureUserAction } from "@/actions/user/core";
import { calculateSkillEffects } from "@/features/game/hooks/useSkillEffects";
import { getStrengthLeaderboardAction } from "@/actions/social/leaderboards";

// Types
import { AuditReport } from "@/types/auditor";
import {
  IntervalsWellness,
  TTBIndices,
  TitanLoadCalculation,
  IntervalsActivity,
  IntervalsEvent,
} from "@/types";
import { HevyExerciseTemplate, HevyRoutine, HevyWorkout } from "@/types/hevy";

// Force dynamic rendering since this page requires authentication
export const dynamic = 'force-dynamic';

export default async function Page() {
  // 0. Auth Check
  const supabase = await createClient();
  const {
    data: { user },
    error,
  } = await supabase.auth.getUser();

  if (error || !user) {
    redirect("/login");
  }

  // 0b. Fetch DB User & Config
  await ensureUserAction(user.id, user.email);
  const dbUser = await prisma.user.findUnique({
    where: { id: user.id },
    include: { achievements: true, skills: true },
  });



  // --- DEMO MODE CHECK ---
  const cookieStore = await cookies();
  const isDemoMode = cookieStore.has("ironforge_demo_mode");

  // --- INTEGRATION CREDENTIALS ---
  const hevyApiKey = dbUser?.hevyApiKey || process.env.HEVY_API_KEY;
  const intervalsApiKey = dbUser?.intervalsApiKey;
  const intervalsAthleteId = dbUser?.intervalsAthleteId;

  // --- DATA CONTAINERS ---
  let hevyRoutines: HevyRoutine[] = [];
  let hevyTemplates: HevyExerciseTemplate[] = [];
  let history: HevyWorkout[] = [];

  let wellness: IntervalsWellness | null = null;
  let activities: IntervalsActivity[] = [];
  let events: IntervalsEvent[] = [];

  // --- DATA FETCHING ---
  if (isDemoMode) {
    console.log("🧪 DEMO MODE ENABLED: Loading Mock Data");
    const { MOCK_WELLNESS, MOCK_ACTIVITIES, MOCK_EVENTS, MOCK_HEVY_WORKOUTS } =
      await import("@/lib/mock-data");

    wellness = MOCK_WELLNESS;
    activities = MOCK_ACTIVITIES;
    events = MOCK_EVENTS;
    history = MOCK_HEVY_WORKOUTS;

    // Mock Hevy Templates/Routines can be empty or added later
    hevyRoutines = [];
    hevyTemplates = [];
  } else {
    // REAL DATA
    // 1. Hevy
    if (hevyApiKey) {
      try {
        const routinesRes = await getHevyRoutinesAction(hevyApiKey);
        hevyRoutines = routinesRes.routines;

        const templatesRes = await getHevyTemplatesAction(hevyApiKey);
        hevyTemplates = templatesRes.exercise_templates;

        const historyRes = await getHevyWorkoutHistoryAction(hevyApiKey, 30);
        history = historyRes.workouts;
      } catch (e) {
        console.error("Failed to load Hevy data", e);
      }
    }

    // 2. Intervals
    if (intervalsApiKey && intervalsAthleteId) {
      try {
        const today = new Date().toISOString().split("T")[0];
        const lastMonth = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000)
          .toISOString()
          .split("T")[0];
        const nextWeek = new Date(Date.now() + 7 * 24 * 60 * 60 * 1000)
          .toISOString()
          .split("T")[0];

        // Note: The actions might not require keys if they use the session user,
        // but our previous check confirms keys exist on the user.
        // The actions `getWellnessAction` etc internally fetch keys from DB user based on session.
        // So we just pass dates.

        const [w, acts, evts] = await Promise.all([
          getWellnessAction(today),
          getActivitiesAction(lastMonth, today),
          getEventsAction(today, nextWeek),
        ]);

        wellness = w;
        activities = acts;
        events = evts;
      } catch (e) {
        console.error("Failed to load Intervals data", e);
      }
    }
  }

  // --- PROCESSING & ANALYSIS ---

  // 1. TTB & Wellness Defaults
  if (!wellness || !wellness.id) {
    wellness = {
      ctl: 0,
      ramp_rate: 0,
      bodyBattery: 0,
      sleepScore: 0,
    } as IntervalsWellness;
  }

  const fakeTTB: TTBIndices = {
    strength: 75,
    endurance: 60,
    wellness: wellness.sleepScore || 50,
    lowest: "endurance",
  };

  // 2. Audit
  let report: AuditReport | null = null;
  try {
    // Note: history from Hevy is now deprecated. Using prefetched for demo/legacy only.
    // For production, runFullAudit will use userId to fetch from IronForge DB.
    if (history.length > 0) {
      // Legacy: pass prefetched history for demo mode
      report = await runFullAudit(
        false,
        user.id,
        history,
      );
    } else {
      // Production: use userId to fetch internal logs
      report = await runFullAudit(false, user.id);
    }
  } catch (e) {
    console.error("Warning: Failed to run auditor", e);
  }

  // 3. Activity Analysis (Titan Load)
  let titanAnalysis: TitanLoadCalculation | null = null;
  if (activities.length > 0) {
    const sorted = activities.sort((a, b) =>
      (b.id || "").localeCompare(a.id || ""),
    );
    const latest = sorted[0];
    const durMins = latest.moving_time ? latest.moving_time / 60 : 60;
    const intensity = latest.icu_intensity ? latest.icu_intensity / 100 : 0.5;
    const estimatedVol = durMins * 100;

    // Calculate Skill Multipliers
    const skillIds = new Set(dbUser?.skills.map((s) => s.skillId) || []);
    const effects = calculateSkillEffects(skillIds, wellness);
    const titanLoadMultiplier = effects.titanLoadMultiplier;

    titanAnalysis = AnalyticsService.calculateTitanLoad(
      estimatedVol,
      intensity,
      durMins,
      titanLoadMultiplier,
    );
  }

  // 4. Progression & Oracle
  const progression = await ProgressionService.getProgressionState(user.id);
  if (!progression) {
    // Should ideally create one if missing
    // throw new Error("Could not load progression");
  }

  const recoveryAnalysis = await RecoveryService.analyzeRecovery(user.id);

  // Cast to any to bypass stale Prisma types in IDE
  const activePath =
    ((dbUser as any)?.activePath as import("@/types/training").TrainingPath) ||
    "WARDEN";

  // 4b. Fetch Titan State (Moved up for Oracle Dependency)
  if (wellness && user.id) {
    await syncTitanStateWithWellness(user.id, wellness);
  }
  const titanRes = await ensureTitanAction(user.id);
  const titanState = titanRes.success ? titanRes.data : null;

  // 4c. Fetch Active Duel Status
  const { getDuelStatusAction } = await import("@/actions/pvp/duel");
  const duelRes = await getDuelStatusAction();
  const activeDuel = duelRes.success ? duelRes.duel : null;

  const oracleRec = await OracleService.consult(
    wellness,
    fakeTTB,
    [],
    report,
    titanAnalysis,
    recoveryAnalysis,
    activePath,
    undefined, // weeklyMastery
    undefined, // titanState - decree handled at Titan level
  );

  // 4d. Fetch Bio-Logic Context (New Multi-Metric Payload)
  // Ensure we import the service at top of file
  const trainingContext = await import("@/services/data/TrainingContextService")
    .then(mod => mod.TrainingContextService.getTrainingContext(user.id));
  const todaysLoad = titanAnalysis ? titanAnalysis.titanLoad : 0;
  const realForecast = AnalyticsService.calculateTSBForecast(wellness, [
    todaysLoad,
  ]);

  // 5. Weekly Mastery Calculation
  const sevenDaysAgo = new Date();
  sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

  const weeklyStrengthSets = await prisma.exerciseLog.count({
    where: {
      userId: user.id,
      date: { gte: sevenDaysAgo },
    },
  });

  const weeklyMobilitySessions = await prisma.meditationLog.count({
    where: {
      userId: user.id,
      date: { gte: sevenDaysAgo },
    },
  });

  const weeklyCardioTss = activities
    .filter((a) => {
      const dateStr = (a as any).start_date_local;
      return dateStr && new Date(dateStr) >= sevenDaysAgo;
    })
    .reduce((sum, a) => sum + ((a as any).training_load || 0), 0);

  const weeklyMastery = {
    strengthSets: weeklyStrengthSets,
    cardioTss: Math.round(weeklyCardioTss),
    mobilitySessions: weeklyMobilitySessions,
    mobilityLevel: (dbUser as any)?.mobilityLevel || "I",
    recoveryLevel: (dbUser as any)?.recoveryLevel || "I",
  };

  // 6. Initial Data Construction
  const initialData = {
    wellness,
    activities,
    events,
    ttb: fakeTTB,
    recommendation: oracleRec,
    auditReport: report,
    forecast: realForecast,
    titanAnalysis,
    activePath,
    weeklyMastery,
    activeDuel,
    trainingContext,
    // We can add history here if needed directly
  };

  const hasCompletedOnboarding = !!(dbUser as any)?.hasCompletedOnboarding;

  const challenges = await getActiveChallengesAction().catch((e) => {
    console.error("Failed to fetch challenges", e);
    return [];
  });

  // 5b. Fetch Titan State (Server-Side)
  // Moved up
  // const titanState = titanRes.success ? titanRes.data : null;

  const liteMode = !!(dbUser?.preferences as any)?.liteMode;

  // New: Fetch Leaderboard Statically (ISG-ish)
  const strengthLeaderboard = await getStrengthLeaderboardAction(5).catch(() => []);

  return (
    <DashboardClient
      initialData={initialData as any}
      userData={dbUser as any} // Cast to UI User type
      dbUser={dbUser as any}
      titanState={titanState} // Pass server-side state
      isMobile={false} // Would need UA check
      hevyTemplates={hevyTemplates}
      hevyRoutines={hevyRoutines}
      intervalsConnected={
        !!(dbUser?.intervalsApiKey && dbUser?.intervalsAthleteId)
      }
      stravaConnected={!!(dbUser as any)?.stravaAccessToken}
      pocketCastsConnected={!!(dbUser as any)?.pocketCastsEnabled}
      faction={(dbUser as any)?.faction || "HORDE"}
      hasCompletedOnboarding={hasCompletedOnboarding}
      isDemoMode={isDemoMode}
      challenges={challenges}
      activeDuel={activeDuel}
      liteMode={liteMode}
      leaderboardData={strengthLeaderboard}
    />
  );
}

