import React from "react";
import { Monster, MonsterType } from "../../types";
import { MONSTERS } from "../../data/gameData";
import {
  Skull,
  Zap,
  TrendingUp,
  Shield,
  Lock,
  CheckCircle2,
  Info,
} from "lucide-react";
import { cn } from "../../lib/utils";

interface BestiaryProps {
  userLevel: number;
  onClose: () => void;
}

export const Bestiary: React.FC<BestiaryProps> = ({ userLevel, onClose }) => {
  const getTypeColor = (type: MonsterType) => {
    switch (type) {
      case "Giant":
        return "text-orange-400 border-orange-400 bg-orange-950/20";
      case "Beast":
        return "text-red-400 border-red-400 bg-red-950/20";
      case "Undead":
        return "text-purple-400 border-purple-400 bg-purple-950/20";
      case "Elemental":
        return "text-blue-400 border-blue-400 bg-blue-950/20";
      case "Construct":
        return "text-zinc-400 border-zinc-400 bg-zinc-950/20";
      case "Dragon":
        return "text-yellow-400 border-yellow-400 bg-yellow-950/20";
      default:
        return "text-zinc-400 border-zinc-400 bg-zinc-950/20";
    }
  };

  const [unlockedIds, setUnlockedIds] = React.useState<string[]>([]);

  React.useEffect(() => {
    const loadUnlocks = async () => {
      const ids = await import("../../services/storage").then((m) =>
        m.StorageService.getUnlockedMonsters(),
      );
      setUnlockedIds(ids);
    };
    loadUnlocks();
  }, []);

  const isUnlocked = (monster: Monster) =>
    userLevel >= monster.level || unlockedIds.includes(monster.id);

  return (
    <div className="h-full bg-[#050505] p-6 overflow-y-auto font-serif text-zinc-200">
      <div className="flex justify-between items-center mb-8 border-b border-forge-border pb-4">
        <div>
          <h1 className="text-3xl font-black text-warrior-light uppercase tracking-tighter flex items-center gap-3">
            <Skull className="w-8 h-8 text-rarity-legendary" />
