        animate={shake ? { x: [0, -5, 5, -2, 2, 0] } : {}}
        transition={{ duration: 0.3 }}
      >
        {/* Simple visual representation of the Boss */}
        <div className="relative w-32 h-32">
          {/* Aura */}
          <div className="absolute inset-0 bg-red-500 blur-2xl opacity-20 animate-pulse rounded-full"></div>

          {/* Boss Model (Icon for now) */}
          <div className="absolute inset-0 flex items-center justify-center">
            <Sword className="w-24 h-24 text-red-600 drop-shadow-lg" />
          </div>

          {/* Damage Number Popups (Simplified) */}
          <AnimatePresence>
            {onDamage && onDamage > 0 && (
              <motion.div
                key={shake}
                initial={{ opacity: 0, y: 0, scale: 0.5 }}
                animate={{ opacity: 1, y: -50, scale: 1.5 }}
                exit={{ opacity: 0 }}
                className="absolute top-0 left-1/2 -translate-x-1/2 text-4xl font-black text-white italic stroke-black text-shadow-lg pointer-events-none"
                style={{ textShadow: "2px 2px 0px #000" }}
              >
                -{onDamage}
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </motion.div>

      {/* HP BAR - HUGE */}
      <div className="relative w-full h-8 bg-zinc-900 rounded-full overflow-hidden border border-zinc-700 mt-2 shadow-inner">
        <motion.div
          className="h-full bg-gradient-to-r from-red-600 to-red-500 relative"
          initial={{ width: "100%" }}
          animate={{ width: `${hpPercent}%` }}
          transition={{ type: "spring", stiffness: 50 }}
        >
          {/* Shine effect */}
          <div className="absolute top-0 right-0 bottom-0 w-[1px] bg-white/50 shadow-[0_0_10px_white]"></div>
        </motion.div>

        {/* Text overlay */}
        <div className="absolute inset-0 flex items-center justify-center text-xs font-bold uppercase tracking-widest text-white drop-shadow-md z-10">
          {currentHp.toLocaleString()} / {totalHp.toLocaleString()} HP
        </div>
      </div>
    </div>
  );
};

export default DungeonInterface;

import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";
import { getPvpRank, getRankTitle, Faction, PVP_RANKS } from "@/lib/pvpRanks";
import {
    Crown, Medal, Shield, Swords, Star, Flame, Skull,
    Target, Axe, Trophy, User, Crosshair, Sword, Users
} from "lucide-react";

interface RankBadgeProps {
    rating: number;
    faction?: Faction;
    showRating?: boolean;
    size?: "sm" | "md" | "lg";
    className?: string;
}

// Icon mapping based on rank number (1-14)
const RANK_ICONS = [
    User,       // Rank 1: Private / Scout
    Swords,     // Rank 2: Corporal / Grunt
    Medal,      // Rank 3: Sergeant
    Shield,     // Rank 4: Master Sergeant / Senior Sergeant
    Sword,      // Rank 5: Sergeant Major / First Sergeant
    Shield,     // Rank 6: Knight / Stone Guard
    Flame,      // Rank 7: Knight-Lieutenant / Blood Guard
    Star,       // Rank 8: Knight-Captain / Legionnaire
    Crown,      // Rank 9: Knight-Champion / Centurion
    Trophy,     // Rank 10: Lieutenant Commander / Champion
    Target,     // Rank 11: Commander / Lieutenant General
    Crosshair,  // Rank 12: Marshal / General
    Axe,        // Rank 13: Field Marshal / Warlord
    Skull,      // Rank 14: Grand Marshal / High Warlord
];

// Color themes per rank tier
const getRankColor = (rankNumber: number, faction: Faction): string => {
    // Alliance = Blue theme, Horde = Red theme for top ranks
    const isAlliance = faction === "ALLIANCE";

    if (rankNumber >= 14) {
        // Grand Marshal / High Warlord - Legendary
        return isAlliance
            ? "text-blue-400 bg-blue-950 border-blue-500 shadow-[0_0_15px_rgba(59,130,246,0.4)]"
            : "text-red-500 bg-zinc-950 border-red-500 shadow-[0_0_15px_rgba(239,68,68,0.4)]";
    }
    if (rankNumber >= 13) {
        // Field Marshal / Warlord
        return isAlliance
            ? "text-blue-400 bg-blue-950/50 border-blue-600/50 shadow-[0_0_10px_rgba(59,130,246,0.3)]"
            : "text-red-400 bg-red-950/50 border-red-600/50 shadow-[0_0_10px_rgba(239,68,68,0.3)]";
    }
    if (rankNumber >= 12) {
        // Marshal / General
        return isAlliance
            ? "text-sky-400 bg-sky-950/40 border-sky-500/40"
            : "text-orange-400 bg-orange-950/40 border-orange-500/40";
    }
    if (rankNumber >= 11) {
        // Commander / Lieutenant General
        return isAlliance
            ? "text-cyan-400 bg-cyan-500/10 border-cyan-500/20"
            : "text-orange-500 bg-orange-500/10 border-orange-500/20";
    }
    if (rankNumber >= 10) {
        // Lieutenant Commander / Champion
        return "text-purple-400 bg-purple-500/10 border-purple-500/20";
    }
    if (rankNumber >= 9) {
        // Knight-Champion / Centurion
        return "text-violet-400 bg-violet-500/10 border-violet-500/20";
    }
    if (rankNumber >= 8) {
        // Knight-Captain / Legionnaire
        return "text-fuchsia-400 bg-fuchsia-500/10 border-fuchsia-500/20";
    }
    if (rankNumber >= 7) {
        // Knight-Lieutenant / Blood Guard
        return "text-rose-400 bg-rose-500/10 border-rose-500/20";
    }
    if (rankNumber >= 6) {
        // Knight / Stone Guard
        return "text-slate-300 bg-slate-500/10 border-slate-500/20";
    }
    if (rankNumber >= 5) {
        // Sergeant Major / First Sergeant
        return "text-yellow-400 bg-yellow-500/10 border-yellow-500/20";
    }
    if (rankNumber >= 4) {
        // Master Sergeant / Senior Sergeant
        return "text-amber-400 bg-amber-500/10 border-amber-500/20";
    }
    if (rankNumber >= 3) {
        // Sergeant
        return "text-amber-500 bg-amber-600/10 border-amber-600/20";
    }
    if (rankNumber >= 2) {
        // Corporal / Grunt
        return "text-amber-600 bg-amber-700/10 border-amber-700/20";
    }
    // Rank 1: Private / Scout
    return "text-zinc-400 bg-zinc-700/20 border-zinc-600/30";
};

export function RankBadge({ rating, faction = "HORDE", showRating = true, size = "md", className }: RankBadgeProps) {
    const pvpRank = getPvpRank(rating);
    const title = getRankTitle(rating, faction);
    const Icon = RANK_ICONS[pvpRank.rank - 1] || User;
    const colorClass = getRankColor(pvpRank.rank, faction);

    const sizeClasses = {
        sm: { badge: "h-5 text-[10px] px-1.5", icon: "w-3 h-3 mr-1" },
        md: { badge: "h-6 text-xs px-2", icon: "w-3.5 h-3.5 mr-1.5" },
        lg: { badge: "h-8 text-sm px-3", icon: "w-4 h-4 mr-2" }
    }[size];

    return (
        <Badge variant="outline" className={cn("font-bold uppercase tracking-wider relative overflow-hidden group", colorClass, sizeClasses.badge, className)}>
            <Icon className={sizeClasses.icon} />
            <span className="relative z-10">{title}</span>
            {showRating && <span className="ml-1 opacity-75 relative z-10 font-mono text-[0.7em]"> {rating}</span>}

            {/* Shine effect for high ranks */}
            {pvpRank.rank >= 13 && (
                <div className="absolute inset-0 bg-gradient-to-r from-transparent via-white/20 to-transparent -translate-x-full group-hover:animate-shimmer" />
            )}
        </Badge>
    );
}

// Export for other components
export { getPvpRank, getRankTitle, getRankProgress, getPointsToNextRank } from "@/lib/pvpRanks";

"use client";

import { useEffect, useState, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { AlertTriangle, Battery, Clock, X } from "lucide-react";
import { checkOvertrainingStatusAction } from "@/actions/training/overtraining";

interface OvertrainingBannerProps {
  userId: string;
  onDismiss?: () => void;
}

export function OvertrainingBanner({
  userId,
  onDismiss,
}: OvertrainingBannerProps) {
  const [warnings, setWarnings] = useState<string[]>([]);
  const [xpMultiplier, setXpMultiplier] = useState(1.0);
  const [dismissed, setDismissed] = useState(false);

  const checkStatus = useCallback(async () => {
    const status = await checkOvertrainingStatusAction(userId);
    setWarnings(status.warnings);
    setXpMultiplier(status.xpMultiplier);
  }, [userId]);

  useEffect(() => {
    checkStatus();
  }, [checkStatus]);

  const handleDismiss = () => {
    setDismissed(true);
    onDismiss?.();
  };

  if (dismissed || warnings.length === 0) return null;

  return (
    <AnimatePresence>
      <motion.div
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -20 }}
        className="relative bg-gradient-to-r from-amber-950/80 to-red-950/80 border border-amber-600/50 rounded-lg p-4 mb-4"
      >
        <button
          onClick={handleDismiss}
          className="absolute top-2 right-2 text-slate-400 hover:text-white"
        >
          <X className="w-4 h-4" />
        </button>

        <div className="flex items-start gap-3">
          <div className="flex-shrink-0 w-10 h-10 rounded-full bg-amber-500/20 flex items-center justify-center">
            <AlertTriangle className="w-5 h-5 text-amber-400" />
          </div>

          <div className="flex-1 min-w-0">
            <h3 className="font-bold text-amber-400 text-sm flex items-center gap-2">
              Recovery Alert
              {xpMultiplier < 1 && (
                <span className="text-xs bg-red-900/50 px-2 py-0.5 rounded-full">
                  {Math.round(xpMultiplier * 100)}% XP
                </span>
              )}
            </h3>

            <ul className="mt-2 space-y-1">
              {warnings.map((warning, i) => (
                <li
                  key={i}
                  className="text-xs text-slate-300 flex items-start gap-2"
                >
                  <span className="text-amber-500 mt-0.5">•</span>
                  {warning}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </motion.div>
    </AnimatePresence>
  );
}

"use client";

import React, { useState } from "react";
import { Challenge, UserChallenge, ChallengeType } from "@prisma/client";
import { ChallengeRewards, claimChallengeAction } from "@/actions/systems/challenges";
import { Scroll, Trophy, Zap, Coins, CheckCircle, Lock } from "lucide-react";
import { playSound } from "@/utils";
import { LoadingSpinner } from "../ui/LoadingSpinner";

export type ChallengeWithStatus = Omit<Challenge, "criteria" | "rewards"> & {
  userStatus:
    | UserChallenge
    | { progress: number; completed: boolean; claimed: boolean };
  criteria: any; // Typed in Action, but Prisma returns Json
  rewards: any;
};

interface QuestBoardProps {
  challenges: ChallengeWithStatus[];
  onClaimSuccess: (gold: number) => void;
}

const RewardBadge: React.FC<{
  type: "xp" | "gold" | "kinetic";
  value: number;
}> = ({ type, value }) => {
  const icons = {
    xp: <Trophy className="w-3 h-3 text-purple-400" />,
    gold: <Coins className="w-3 h-3 text-yellow-400" />,
    kinetic: <Zap className="w-3 h-3 text-blue-400" />,
  };
  const colors = {
    xp: "text-purple-300 bg-purple-900/40 border-purple-800",
    gold: "text-yellow-300 bg-yellow-900/40 border-yellow-800",
    kinetic: "text-blue-300 bg-blue-900/40 border-blue-800",
  };

  return (
    <div
      className={`flex items-center space-x-1 px-2 py-0.5 rounded border text-xs font-mono ${colors[type]}`}
    >
      {icons[type]}
      <span>{value}</span>
    </div>
  );
};

const ChallengeCard: React.FC<{
  challenge: ChallengeWithStatus;
  onClaim: (id: string) => void;
  isClaiming: boolean;
}> = ({ challenge, onClaim, isClaiming }) => {
  const { title, description, userStatus, rewards, criteria } = challenge;
  const progress = userStatus.progress || 0;
  const target = criteria.target || 1;
  const percent = Math.min(100, (progress / target) * 100);
  const isCompleted = userStatus.completed || percent >= 100;
  const isClaimed = userStatus.claimed;

  const r = rewards as ChallengeRewards;

  return (
    <div
      className={`relative p-4 rounded-lg border transition-all duration-300 ${isCompleted ? "bg-gradient-to-br from-slate-900 to-green-950/20 border-green-800/50" : "bg-black/40 border-slate-800"}`}
    >
      <div className="flex justify-between items-start mb-2">
        <div>
          <h4
            className={`font-bold uppercase tracking-wide ${isCompleted ? "text-green-400" : "text-slate-200"}`}
          >
            {title}
          </h4>
          <p className="text-xs text-slate-400">{description}</p>
        </div>
        {isClaimed && (
          <CheckCircle className="w-5 h-5 text-green-500 opacity-50" />
        )}
      </div>

      {/* Progress Bar */}
      <div className="mt-3 mb-2">
        <div className="flex justify-between text-xs text-slate-500 mb-1 font-mono">
          <span>PROGRESS</span>
          <span>
            {progress} / {target} {criteria.unit}
          </span>
        </div>
        <div className="h-2 bg-slate-800 rounded-full overflow-hidden">
          <div
            className={`h-full transition-all duration-1000 ${isCompleted ? "bg-green-500 shadow-[0_0_10px_rgba(34,197,94,0.5)]" : "bg-blue-600"}`}
            style={{ width: `${percent}%` }}
          />
        </div>
      </div>

      {/* Rewards & Action */}
      <div className="flex items-center justify-between mt-3">
        <div className="flex space-x-2">
          {r.xp > 0 && <RewardBadge type="xp" value={r.xp} />}
          {r.gold > 0 && <RewardBadge type="gold" value={r.gold} />}
          {r.kinetic > 0 && <RewardBadge type="kinetic" value={r.kinetic} />}
        </div>

        {isCompleted && !isClaimed && (
          <button
            onClick={() => onClaim(challenge.id)}
            disabled={isClaiming}
            className="px-3 py-1 bg-yellow-600 hover:bg-yellow-500 text-white text-xs font-bold uppercase rounded shadow-lg animate-pulse hover:animate-none transition-colors flex items-center space-x-1"
          >
            {isClaiming ? (
              <LoadingSpinner size="sm" />
            ) : (
              <span>Claim Bounty</span>
            )}
          </button>
        )}
        {isClaimed && (
          <span className="text-xs text-green-600 font-mono uppercase">
            Claimed
          </span>
        )}
        {!isCompleted && <Lock className="w-4 h-4 text-slate-700" />}
      </div>
    </div>
  );
};

export const QuestBoard: React.FC<QuestBoardProps> = ({
  challenges,
  onClaimSuccess,
}) => {
  const [claimingId, setClaimingId] = useState<string | null>(null);

  const handleClaim = async (id: string) => {
    setClaimingId(id);
    playSound("ui_click"); // Or specific claim sound
    try {
      const result = await claimChallengeAction(id);
      if (result.success) {
        playSound("achievement"); // Reward sound
        onClaimSuccess(result.newGold);
      }
    } catch (e) {
      console.error("Claim Failed", e);
      playSound("ui_error");
    } finally {
      setClaimingId(null);
    }
  };

  const daily = challenges.filter((c) => c.type === "DAILY");
  const weekly = challenges.filter((c) => c.type === "WEEKLY");

  if (challenges.length === 0) return null;

  return (
    <div className="bg-slate-950/80 border border-slate-800 rounded-xl p-4 shadow-xl backdrop-blur-sm">
      <div className="flex items-center space-x-2 mb-4 border-b border-slate-800 pb-2">
        <Scroll className="w-5 h-5 text-amber-500" />
        <h3 className="text-lg font-bold uppercase tracking-widest text-amber-100">
          Active Bounties
        </h3>
        <span className="text-xs text-amber-700 bg-amber-950/30 px-2 py-0.5 rounded border border-amber-900/50 ml-auto">
          Weekly Reset: Sunday
        </span>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div className="space-y-3">
          <h4 className="text-xs font-bold text-slate-500 uppercase tracking-wider ml-1">
            Daily Priorities
          </h4>
          {daily.length > 0 ? (
            daily.map((c) => (
              <ChallengeCard
                key={c.id}
                challenge={c}
                onClaim={handleClaim}
                isClaiming={claimingId === c.id}
              />
            ))
          ) : (
            <p className="text-xs text-slate-600 italic p-2">
              No daily bounties available.
            </p>
          )}
        </div>

        <div className="space-y-3">
          <h4 className="text-xs font-bold text-slate-500 uppercase tracking-wider ml-1">
            Weekly Objectives
          </h4>
          {weekly.length > 0 ? (
            weekly.map((c) => (
              <ChallengeCard
                key={c.id}
                challenge={c}
                onClaim={handleClaim}
                isClaiming={claimingId === c.id}
              />
            ))
          ) : (
            <p className="text-xs text-slate-600 italic p-2">
              No weekly objectives available.
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

"use client";

import { useState } from "react";
import { Exercise } from "@prisma/client";
import { logExerciseSetsAction } from "@/actions/user/logger";
import ForgeInput from "@/components/ui/ForgeInput";
import ForgeButton from "@/components/ui/ForgeButton";
import { Trash2, Plus, CheckCircle, Save } from "lucide-react";
import { toast } from "sonner";
import { cn } from "@/lib/utils";

interface SetData {
    id: string;
    weight: number;
    reps: number;
    rpe: number;
    completed: boolean;
}

interface SetLoggerProps {
    exercise: Exercise;
    onFinish: () => void;
    onCancel: () => void;
    onCombatUpdate?: (damage: number, remainingHp: number, isCritical: boolean) => void;
    onSave: (sets: any[]) => Promise<void>;
}

export default function SetLogger({ exercise, onFinish, onCancel, onCombatUpdate, onSave }: SetLoggerProps) {
    const [sets, setSets] = useState<SetData[]>([
        { id: crypto.randomUUID(), weight: 0, reps: 0, rpe: 8, completed: false },
    ]);
    const [isSaving, setIsSaving] = useState(false);

    function addSet() {
        const lastSet = sets[sets.length - 1];
        setSets([
            ...sets,
            {
                id: crypto.randomUUID(),
                weight: lastSet ? lastSet.weight : 0,
                reps: lastSet ? lastSet.reps : 0,
                rpe: lastSet ? lastSet.rpe : 8,
                completed: false,
            },
        ]);
    }

    function removeSet(id: string) {
        if (sets.length === 1) return;
        setSets(sets.filter(s => s.id !== id));
    }

    function updateSet(id: string, field: keyof SetData, value: number) {
        setSets(sets.map(s => s.id === id ? { ...s, [field]: value } : s));
    }

    function toggleComplete(id: string) {
        setSets(sets.map(s => s.id === id ? { ...s, completed: !s.completed } : s));
    }

    async function handleSave() {
        // Validate
        const validSets = sets.filter(s => s.weight > 0 && s.reps > 0);
        if (validSets.length === 0) {
            toast.error("Add at least one valid set (Weight & Reps > 0)");
            return;
        }

        // Optimistic: Delegate to parent and close immediately
        onSave(validSets);
        onFinish();
    }

    return (
        <div className="bg-zinc-900/50 border border-zinc-800 rounded-xl p-4 space-y-4 animate-in fade-in slide-in-from-bottom-4">
            <div className="flex justify-between items-center mb-2">
                <h3 className="font-bold text-lg text-magma">{exercise.name}</h3>
                <button onClick={onCancel} className="text-xs text-zinc-500 hover:text-zinc-300">
                    Cancel
                </button>
            </div>

            <div className="grid grid-cols-[auto_1fr_1fr_1fr_auto] gap-2 text-xs text-zinc-500 font-bold uppercase tracking-wider mb-2 text-center">
                <div>Set</div>
                <div>Kg</div>
                <div>Reps</div>
                <div>RPE</div>
                <div></div>
            </div>

            <div className="space-y-2">
                {sets.map((set, idx) => (
                    <div key={set.id} className={cn(
                        "grid grid-cols-[auto_1fr_1fr_1fr_auto] gap-2 items-center",
                        set.completed ? "opacity-50" : ""
                    )}>
                        <div className="w-6 h-6 flex items-center justify-center bg-zinc-800 rounded-full text-xs font-bold text-zinc-400">
                            {idx + 1}
                        </div>
                        <ForgeInput
                            type="number"
                            value={set.weight || ""}
                            onChange={e => updateSet(set.id, 'weight', parseFloat(e.target.value))}
                            className="h-9 text-center"
                            placeholder="0"
                        />
                        <ForgeInput
                            type="number"
                            value={set.reps || ""}
                            onChange={e => updateSet(set.id, 'reps', parseFloat(e.target.value))}
                            className="h-9 text-center"
                            placeholder="0"
                        />
                        <ForgeInput
                            type="number"
                            value={set.rpe || ""}
                            onChange={e => updateSet(set.id, 'rpe', parseFloat(e.target.value))}
                            className="h-9 text-center text-zinc-400"
                            placeholder="8"
                        />
                        <div className="flex items-center gap-1">
                            <button
                                onClick={() => toggleComplete(set.id)}
                                className={cn(
                                    "p-2 rounded hover:bg-zinc-800 transition-colors",
                                    set.completed ? "text-green-500" : "text-zinc-600"
                                )}
                            >
                                <CheckCircle className="h-4 w-4" />
                            </button>
                            {sets.length > 1 && (
                                <button
                                    onClick={() => removeSet(set.id)}
                                    className="p-2 text-zinc-600 hover:text-red-400 rounded hover:bg-zinc-800 transition-colors"
                                >
                                    <Trash2 className="h-4 w-4" />
                                </button>
                            )}
                        </div>
                    </div>
                ))}
            </div>

            <div className="flex gap-2 pt-2">
                <ForgeButton
                    variant="ghost"
                    size="sm"
                    onClick={addSet}
                    className="flex-1 gap-2 border border-dashed border-zinc-700 text-zinc-400 hover:text-zinc-200"
                >
                    <Plus className="h-4 w-4" /> Add Set
                </ForgeButton>
            </div>

            <div className="pt-4 border-t border-zinc-800 flex flex-col gap-4">
                {/* Context Feedback UI will be rendered via Toast for now to minimize clutter, 
                   but we could add a permanent stats block here later. 
                   For MVP, the Toast is the primary feedback mechanism. */}
                <div className="flex justify-end">
                    <ForgeButton
                        variant="magma"
                        onClick={handleSave}
                        disabled={isSaving}
                        className="w-full sm:w-auto gap-2"
                    >
                        {isSaving ? "Saving..." : <><Save className="h-4 w-4" /> Finish Exercise</>}
                    </ForgeButton>
                </div>
            </div>
        </div>
    );
}

