/**
 * @fileoverview SkillContext V2
 * Manages Neural Lattice state with V2 node structure and effect calculation.
 */

import React, {
  createContext,
  useContext,
  useState,
  useMemo,
  useEffect,
  useCallback,
} from "react";
import {
  SKILL_TREE_V2,
  getNodeById,
  countUnlockedNotables,
} from "../data/skill-tree-v2";
import {
  SkillNodeV2,
  CalculatedEffects,
  SkillStatus,
  SkillRequirement,
} from "../types/skills";
import { calculateTitanRank, playSound, getMaxTM } from "../utils";
import { IntervalsWellness, Archetype } from "../types";
import { StorageService } from "../services/storage";
import {
  useSkillEffects,
  canPurchaseKeystone,
  getPathProgress,
  SessionMetadata,
} from "@/features/game/hooks/useSkillEffects";

// ============================================================================
// Logic Helpers
// ============================================================================

/**
 * Validates a skill requirement against user data.
 */
function checkRequirement(
  req: SkillRequirement,
  unlockedAchievementIds: Set<string>,
  wellness: IntervalsWellness | null,
): boolean {
  const { type, value, comparison, exerciseId } = req;

  const compare = (val: number) => {
    if (comparison === "gte") return val >= value;
    if (comparison === "lte") return val <= value;
    if (comparison === "eq") return val === value;
    return false;
  };

  switch (type) {
    case "achievement_count":
      return unlockedAchievementIds.size >= value;

    case "vo2max_value":
      return wellness?.vo2max ? compare(wellness.vo2max) : false;

    case "1rm_weight": {
      const maxTM = getMaxTM(exerciseId);
      return compare(maxTM);
    }

    // These require more complex data sources not yet fully integrated in V2 context
    case "rep_count":
    case "session_count":
    case "rest_day_count":
    case "sleep_score_streak":
    case "brick_workout_count":
      // For now, these default to true to avoid hard-locking the tree 
      // until the relevant tracking services are wired up to the Provider.
      console.warn(`Requirement type ${type} is not yet implemented, defaulting to true.`);
      return true;

    default:
      return false;
  }
}

// ============================================================================
// Context Types
// ============================================================================

interface SkillContextType {
  // State
  purchasedSkillIds: Set<string>;
  isLoading: boolean;
  userArchetype: Archetype; // Added

  // Resources
  availableTalentPoints: number;
  availableKineticShards: number;

  // Actions
  unlockSkill: (skillId: string) => { success: boolean; message?: string };
  canAfford: (skillId: string) => boolean;
  refundSkill: (skillId: string) => { success: boolean; message?: string };

  // V2 Features
  calculatedEffects: CalculatedEffects;
  pathProgress: Record<string, number>;
  getNodeStatus: (nodeId: string) => SkillStatus;
  activeKeystoneId: string | null;
  setActiveKeystone: (keystoneId: string | null) => void;
}

const SkillContext = createContext<SkillContextType | null>(null);

export const useSkills = () => {
  const context = useContext(SkillContext);
  if (!context) {
    throw new Error("useSkills must be used within a SkillProvider");
  }
  return context;
};

// ============================================================================
// Provider
// ============================================================================

interface SkillProviderProps {
  children: React.ReactNode;
  unlockedAchievementIds: Set<string>;
  wellness: IntervalsWellness | null;
  sessionMetadata?: SessionMetadata;
  userArchetype?: Archetype; // Added
}

export const SkillProvider: React.FC<SkillProviderProps> = ({
  children,
  unlockedAchievementIds,
  wellness,
  sessionMetadata = {},
  userArchetype = Archetype.WARDEN,
}) => {
  const [purchasedSkillIds, setPurchasedSkillIds] = useState<Set<string>>(
    new Set(),
  );
  const [selectedKeystoneId, setSelectedKeystoneId] = useState<string | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  // ─────────────────────────────────────────────────────────────────────────
  // Load from Storage
  // ─────────────────────────────────────────────────────────────────────────
  useEffect(() => {
    const loadSkills = async () => {
      try {
        await StorageService.init();
        const saved = await StorageService.getState<string[]>("skills_v2");
        if (saved) {
          setPurchasedSkillIds(new Set(saved));
        } else {
          // Try loading V1 and migrating
          const v1Saved = await StorageService.getState<string[]>("skills");
          if (v1Saved) {
            // Basic migration: keep IDs that exist in V2
            const validV2Ids = v1Saved.filter((id) => getNodeById(id));
            setPurchasedSkillIds(new Set(validV2Ids));
            // Save to V2 key
            await StorageService.saveState("skills_v2", validV2Ids);
          }
        }

        // Load active keystone
        const savedKeystone = await StorageService.getState<string>("active_keystone");
        if (savedKeystone) {
          setSelectedKeystoneId(savedKeystone);
        }
      } catch (e) {
        console.error("Skill load failed", e);
      } finally {
        setIsLoading(false);
      }
    };
    loadSkills();
  }, []);

  // ─────────────────────────────────────────────────────────────────────────
  // Calculate Resources
  // ─────────────────────────────────────────────────────────────────────────
  const { talentPoints: totalTP, kineticShards: totalKS } = useMemo(
    () => calculateTitanRank(unlockedAchievementIds),
    [unlockedAchievementIds],
  );

  const { spentTP, spentKS } = useMemo(() => {
    let tp = 0;
    let ks = 0;
    purchasedSkillIds.forEach((id) => {
      const node = getNodeById(id);
      if (node) {
        if (node.currency === "talent_point") tp += node.cost;
        if (node.currency === "kinetic_shard") ks += node.cost;
      }
    });
    return { spentTP: tp, spentKS: ks };
  }, [purchasedSkillIds]);

  const availableTalentPoints = totalTP - spentTP;
  const availableKineticShards = totalKS - spentKS;

  // ─────────────────────────────────────────────────────────────────────────
  // V2 Effect Calculation
  // ─────────────────────────────────────────────────────────────────────────
  const calculatedEffects = useSkillEffects(
    purchasedSkillIds,
    wellness,
    { ...sessionMetadata, activeKeystoneId: selectedKeystoneId },
  );
  const pathProgress = useMemo(
    () => getPathProgress(purchasedSkillIds),
    [purchasedSkillIds],
  );
  const activeKeystoneId = calculatedEffects.activeKeystoneId;

  // ─────────────────────────────────────────────────────────────────────────
  // Node Status Calculation
  // ─────────────────────────────────────────────────────────────────────────
  const getNodeStatus = useCallback(
    (nodeId: string): SkillStatus => {
      if (purchasedSkillIds.has(nodeId)) {
        return SkillStatus.MASTERED;
      }

      const node = getNodeById(nodeId);
      if (!node) return SkillStatus.LOCKED;

      // Check Archetype Gating
      const allowedPaths: Record<Archetype, string[]> = {
        [Archetype.JUGGERNAUT]: ["juggernaut", "titan", "sage"],
        [Archetype.PATHFINDER]: ["pathfinder", "sage"],
        [Archetype.WARDEN]: [
          "juggernaut",
          "pathfinder",
          "warden",
          "titan",
          "sage",
        ],
      };

      // Default to Warden if undefined (safe fallback)
      const currentArchetype = userArchetype || Archetype.WARDEN;
      const accessiblePaths = allowedPaths[currentArchetype];

      if (!accessiblePaths.includes(node.path)) {
        return SkillStatus.LOCKED;
      }

      // Check parent requirements
      const parentsUnlocked =
        node.parents.length === 0 ||
        (node.unlockLogic === "AND"
          ? node.parents.every((pid) => purchasedSkillIds.has(pid))
          : node.parents.some((pid) => purchasedSkillIds.has(pid)));

      // Check requirements (achievements, stats, etc.)
      const requirementsMet = node.requirements.every((req) =>
        checkRequirement(req, unlockedAchievementIds, wellness),
      );
      if (!requirementsMet) return SkillStatus.LOCKED;

      if (!parentsUnlocked) return SkillStatus.LOCKED;

      // Check Keystone gating
      if (node.tier === "keystone") {
        const { canPurchase } = canPurchaseKeystone(nodeId, purchasedSkillIds);
        if (!canPurchase) return SkillStatus.LOCKED;
      }

      return SkillStatus.UNLOCKED;
    },
    [purchasedSkillIds, unlockedAchievementIds, wellness, userArchetype],
  );

  // ─────────────────────────────────────────────────────────────────────────
  // Actions
  // ─────────────────────────────────────────────────────────────────────────
  const canAfford = useCallback(
    (skillId: string): boolean => {
      const node = getNodeById(skillId);
      if (!node) return false;

      if (node.currency === "talent_point")
        return availableTalentPoints >= node.cost;
      if (node.currency === "kinetic_shard")
        return availableKineticShards >= node.cost;
      return false;
    },
    [availableTalentPoints, availableKineticShards],
  );

  const unlockSkill = useCallback(
    (skillId: string): { success: boolean; message?: string } => {
      if (purchasedSkillIds.has(skillId)) {
        return { success: false, message: "Already unlocked" };
      }

      const node = getNodeById(skillId);
      if (!node) {
        return { success: false, message: "Node not found" };
      }

      // Check status
      const status = getNodeStatus(skillId);
      if (status === SkillStatus.LOCKED) {
        playSound("fail");
        return { success: false, message: "Prerequisites not met" };
      }

      // Check Keystone gating
      if (node.tier === "keystone") {
        const { canPurchase, reason } = canPurchaseKeystone(
          skillId,
          purchasedSkillIds,
        );
        if (!canPurchase) {
          playSound("fail");
          return { success: false, message: reason };
        }
      }

      // Check affordability
      if (!canAfford(skillId)) {
        playSound("fail");
        return { success: false, message: "Insufficient resources" };
      }

      // Determine sound based on tier
      if (node.tier === "keystone") {
        playSound("achievement");
      } else if (node.tier === "notable") {
        playSound("loot_epic");
      } else {
        playSound("ding");
      }

      setPurchasedSkillIds((prev) => {
        const next = new Set(prev);
        next.add(skillId);
        StorageService.saveState("skills_v2", Array.from(next)).catch(
          console.error,
        );
        return next;
      });

      return { success: true };
    },
    [purchasedSkillIds, getNodeStatus, canAfford],
  );

  const refundSkill = useCallback(
    (skillId: string): { success: boolean; message?: string } => {
      if (!purchasedSkillIds.has(skillId)) {
        return { success: false, message: "Skill not owned" };
      }

      const node = getNodeById(skillId);
      if (!node) {
        return { success: false, message: "Node not found" };
      }

      // Check if any other owned skills depend on this one
      const dependents = Array.from(purchasedSkillIds).filter((id) => {
        const n = getNodeById(id);
        return n?.parents.includes(skillId);
      });

      if (dependents.length > 0) {
        return { success: false, message: "Other skills depend on this node" };
      }

      // Keystones cannot be refunded (for now)
      if (node.tier === "keystone") {
        return { success: false, message: "Keystones cannot be refunded" };
      }

      playSound("ding");

      setPurchasedSkillIds((prev) => {
        const next = new Set(prev);
        next.delete(skillId);
        StorageService.saveState("skills_v2", Array.from(next)).catch(
          console.error,
        );
        return next;
      });

      return { success: true };
    },
    [purchasedSkillIds],
  );

  const setActiveKeystone = useCallback((keystoneId: string | null) => {
    setSelectedKeystoneId(keystoneId);
    StorageService.saveState("active_keystone", keystoneId).catch(console.error);
    if (keystoneId) playSound("ui_click");
  }, []);

  // ─────────────────────────────────────────────────────────────────────────
  // Provider Value
  // ─────────────────────────────────────────────────────────────────────────
  const value = useMemo<SkillContextType>(
    () => ({
      purchasedSkillIds,
      isLoading,
      userArchetype,
      availableTalentPoints,
      availableKineticShards,
      unlockSkill,
      canAfford,
      refundSkill,
      calculatedEffects,
      pathProgress,
      getNodeStatus,
      activeKeystoneId,
      setActiveKeystone,
    }),
    [
      purchasedSkillIds,
      isLoading,
      userArchetype,
      availableTalentPoints,
      availableKineticShards,
      unlockSkill,
      canAfford,
      refundSkill,
      calculatedEffects,
      pathProgress,
      getNodeStatus,
      activeKeystoneId,
      setActiveKeystone,
    ],
  );

  return (
    <SkillContext.Provider value={value}>{children}</SkillContext.Provider>
  );
};

"use client";

import React from "react";
import { GarminWellnessData } from "@/services/bio/GarminService";
import { Battery, Activity, Zap, TrendingDown } from "lucide-react";
import { motion } from "framer-motion";

interface GarminWidgetProps {
    data: GarminWellnessData;
    variant?: "compact" | "full";
}

export const GarminWidget: React.FC<GarminWidgetProps> = ({ data, variant = "compact" }) => {
    // Body Battery Color
    const getBatteryColor = (val: number) => {
        if (val > 70) return "text-green-400";
        if (val > 30) return "text-yellow-400";
        return "text-red-500";
    };

    // Stress Color
    const getStressColor = (val: number) => {
        if (val < 25) return "text-blue-400"; // Resting
        if (val < 50) return "text-green-400"; // Low
        if (val < 75) return "text-orange-400"; // Med
        return "text-red-500"; // High
    };

    if (variant === "compact") {
        return (
            <div className="flex items-center gap-4 bg-black/40 backdrop-blur-md border border-white/10 px-4 py-2 rounded-xl">
                <div className="flex items-center gap-2">
                    <Battery className={`w-4 h-4 ${getBatteryColor(data.bodyBattery)}`} />
                    <span className="text-sm font-bold text-white">{data.bodyBattery}</span>
                </div>
                <div className="flex items-center gap-2 border-l border-white/10 pl-4">
                    <Activity className={`w-4 h-4 ${getStressColor(data.stressLevel)}`} />
                    <span className="text-sm font-bold text-white">{data.stressLevel}</span>
                </div>
            </div>
        );
    }

    return (
        <div className="grid grid-cols-2 gap-3 p-4 bg-zinc-900/50 border border-white/5 rounded-2xl shadow-xl">
            <div className="space-y-1">
                <div className="flex items-center gap-2 text-[10px] text-zinc-500 uppercase font-bold tracking-wider">
                    <Battery className="w-3 h-3" /> Body Battery
                </div>
                <div className="text-2xl font-black text-white">{data.bodyBattery}</div>
                <div className="w-full h-1 bg-zinc-800 rounded-full overflow-hidden">
                    <motion.div
                        initial={{ width: 0 }}
                        animate={{ width: `${data.bodyBattery}%` }}
                        className={`h-full ${getBatteryColor(data.bodyBattery).replace('text', 'bg')}`}
                    />
                </div>
            </div>

            <div className="space-y-1">
                <div className="flex items-center gap-2 text-[10px] text-zinc-500 uppercase font-bold tracking-wider">
                    <Activity className="w-3 h-3" /> Stress Level
                </div>
                <div className="text-2xl font-black text-white">{data.stressLevel}</div>
                <div className="w-full h-1 bg-zinc-800 rounded-full overflow-hidden">
                    <motion.div
                        initial={{ width: 0 }}
                        animate={{ width: `${data.stressLevel}%` }}
                        className={`h-full ${getStressColor(data.stressLevel).replace('text', 'bg')}`}
                    />
                </div>
            </div>

            {data.restingHeartRate && (
                <div className="col-span-2 pt-2 border-t border-white/5 flex justify-between items-center text-[10px] uppercase font-bold tracking-widest text-zinc-400">
                    <span>Resting HR</span>
                    <span className="text-white">{data.restingHeartRate} BPM</span>
                </div>
            )}
        </div>
    );
}

import React, { useState, useEffect } from "react";
import {
  Heart,
  Bluetooth,
  Activity,
  Flame,
  Bike,
  Settings,
} from "lucide-react";
import { FTMSService } from "@/services/bluetooth-ftms";
import { IoTService } from "@/services/iot"; // IMPORT

interface HeartRateMonitorProps {
  bpm: number | null;
  isConnected: boolean;
  onConnect: () => void;
  onSimulate?: () => void;
  error?: string | null;
  hasAura?: boolean; // Elite Rank passive buff
}

const HeartRateMonitor: React.FC<HeartRateMonitorProps> = ({
  bpm,
  isConnected,
  onConnect,
  onSimulate,
  error,
  hasAura = false,
}) => {
  const [ftmsConnected, setFtmsConnected] = useState(false);
  const [targetPower, setTargetPower] = useState(150);
  const [showBikeControls, setShowBikeControls] = useState(false);

  // --- IOT TRIGGER ---
  useEffect(() => {
    if (bpm) {
      IoTService.syncAtmosphere(bpm);
    }
  }, [bpm]);

  const zoneColor = bpm && bpm < 120 ? "text-green-500" : "text-orange-500";
  const zoneStatus = bpm ? (bpm < 120 ? "ZONE 2 (READY)" : "RECOVERY") : "";
  const isOverheated = bpm && bpm > 120;

  // Elite Theme Override
  const containerBorder = hasAura
    ? isOverheated
      ? "border-orange-900/50"
      : "border-[#ffd700] shadow-[0_0_15px_rgba(255,215,0,0.15)]"
    : isOverheated
      ? "border-orange-900/50"
      : bpm
        ? "border-green-900/30"
        : "border-zinc-800";

  const containerBg =
    hasAura && !isOverheated && bpm
      ? "bg-[linear-gradient(45deg,rgba(0,0,0,0.9),rgba(255,215,0,0.05))]"
      : isOverheated
        ? "bg-orange-950/10"
        : bpm
          ? "bg-green-950/10"
          : "bg-zinc-900";

  const handleFtmsConnect = async () => {
    const success = await FTMSService.connect();
    setFtmsConnected(success);
    setShowBikeControls(success);
  };

  const setErgMode = async () => {
    await FTMSService.setTargetPower(targetPower);
  };

  return (
    <div
      className={`relative flex flex-col p-3 rounded-lg mb-4 transition-all duration-500 border ${containerBorder} ${containerBg}`}
    >
      {/* Aura Visual Indicator */}
      {hasAura && isConnected && (
        <div className="absolute -top-2 left-4 bg-black border border-[#ffd700] px-2 py-0.5 rounded-full flex items-center gap-1 shadow-lg">
          <Flame className="w-3 h-3 text-[#ffd700] fill-current animate-pulse" />
          <span className="text-[9px] font-bold text-[#ffd700] uppercase tracking-wider">
            Aura: +1.5% Accuracy
          </span>
        </div>
      )}

      {/* Main HR Row */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div
            className={`p-2 rounded-full transition-colors ${isConnected ? (isOverheated ? "bg-orange-500/20" : "bg-green-500/20") : "bg-zinc-800"}`}
          >
            <Heart
              className={`w-5 h-5 transition-colors ${isConnected ? (isOverheated ? "text-orange-500 animate-pulse" : "text-green-500") : "text-zinc-500"}`}
            />
          </div>
          <div>
            <div className="text-xs text-zinc-500 font-mono uppercase tracking-wider">
              Recovery Monitor
            </div>
            <div className="flex items-baseline gap-2">
              <span
                className={`text-xl font-bold font-mono transition-colors ${isConnected ? zoneColor : "text-zinc-600"}`}
              >
                {bpm ? bpm : "--"} <span className="text-sm">BPM</span>
              </span>
              {bpm && (
                <span
                  className={`text-xs font-bold transition-colors ${isOverheated ? "text-orange-400" : "text-green-400"}`}
                >
                  [{zoneStatus}]
                </span>
              )}
            </div>
          </div>
        </div>

        <div className="flex gap-2">
          {!isConnected ? (
            <button
              onClick={onConnect}
              className="flex items-center gap-2 px-3 py-2 text-xs font-bold text-zinc-300 bg-zinc-800 hover:bg-zinc-700 rounded transition-colors"
            >
              <Bluetooth className="w-4 h-4" />
              HRM
            </button>
          ) : (
            <div
              className={`flex items-center gap-2 px-3 py-2 text-xs font-bold rounded border ${hasAura ? "text-[#ffd700] bg-[#ffd700]/10 border-[#ffd700]/30" : "text-green-500/80 bg-green-900/10 border-green-900/20"}`}
            >
              <Activity className="w-4 h-4" />
              LINKED
            </div>
          )}

          {!ftmsConnected ? (
            <button
              onClick={handleFtmsConnect}
              className="flex items-center gap-2 px-3 py-2 text-xs font-bold text-zinc-300 bg-zinc-800 hover:bg-zinc-700 rounded transition-colors"
              title="Connect Smart Bike (FTMS)"
            >
              <Bike className="w-4 h-4" />
            </button>
          ) : (
            <button
              onClick={() => setShowBikeControls(!showBikeControls)}
              className="flex items-center gap-2 px-3 py-2 text-xs font-bold text-cyan-400 bg-cyan-900/20 border border-cyan-500/30 rounded"
            >
              <Bike className="w-4 h-4" />
            </button>
          )}
        </div>
      </div>

      {/* Smart Bike Controls (FTMS) */}
      {showBikeControls && (
        <div className="mt-4 pt-4 border-t border-zinc-800 animate-slide-down">
          <div className="flex items-center justify-between mb-2">
            <span className="text-[10px] font-bold uppercase text-cyan-500 flex items-center gap-1">
              <Settings className="w-3 h-3" /> ERG Mode Control
            </span>
          </div>
          <div className="flex items-center gap-2">
            <input
              type="range"
              min="100"
              max="400"
              step="5"
              value={targetPower}
              onChange={(e) => setTargetPower(parseInt(e.target.value))}
              className="flex-1 h-2 bg-zinc-700 rounded-lg appearance-none cursor-pointer accent-cyan-500"
            />
            <span className="font-mono font-bold text-white w-12 text-right">
              {targetPower}W
            </span>
            <button
              onClick={setErgMode}
              className="px-3 py-1 bg-cyan-600 hover:bg-cyan-500 text-white text-xs font-bold uppercase rounded shadow-lg"
            >
              Set Load
            </button>
          </div>
        </div>
      )}

      {/* Hidden Simulation Trigger for Devs */}
      {isConnected && onSimulate && (
        <button
          onClick={onSimulate}
          className="opacity-0 hover:opacity-50 text-xs text-zinc-600 ml-2 absolute bottom-1 left-1"
        >
          (Sim)
        </button>
      )}
    </div>
  );
};

