interface LiveSessionHUDProps {
    onSessionJoin?: (sessionId: string) => void;
}

export const LiveSessionHUD: React.FC<LiveSessionHUDProps> = ({ onSessionJoin }) => {
    const { user } = useUser();
    const [activeSession, setActiveSession] = useState<CoOpSession | null>(null);
    const [availableSessions, setAvailableSessions] = useState<CoOpSession[]>([]);
    const [isExpanded, setIsExpanded] = useState(false);

    // Initial load & Restoration & Refresh on Open
    // Initial load & Restoration & Refresh on Open
    useEffect(() => {
        refreshSessions();

        // Debug Mock Visibility
        if (typeof window !== 'undefined') {
            // Checked for mock
        }

        if (user && (isExpanded || !activeSession)) {
            CoOpService.getActiveSession(user.id).then(session => {
                if (session) setActiveSession(session);
            });
        }
        const interval = setInterval(refreshSessions, 10000); // Polling for now for list
        return () => clearInterval(interval);
    }, [user?.id, isExpanded]);

    // Subscribe to active session updates
    useEffect(() => {
        if (!activeSession) return;

        const channel = CoOpService.subscribeToSession(activeSession.id, (payload) => {
            // Refetch full session state on changes for simplicity
            // In a pro version, we'd patch state via payload
            refreshActiveSession(activeSession.id);
        });

        return () => {
            channel.unsubscribe();
        }
    }, [activeSession?.id]);

    const refreshSessions = async () => {
        const sessions = await CoOpService.listSessions();
        setAvailableSessions(sessions);
    }

    const refreshActiveSession = async (sessionId: string) => {
        // This would require a getSession method in service, or re-finding in list
        const sessions = await CoOpService.listSessions();
        const found = sessions.find(s => s.id === sessionId);
        if (found) setActiveSession(found);
    }

    const handleCreate = async () => {
        if (!user) return;
        const sessionId = await CoOpService.createSession(user.id, "Iron Mines Run");
        if (sessionId) {
            refreshActiveSession(sessionId);
            onSessionJoin?.(sessionId);
        }
    };

    const handleJoin = async (session: CoOpSession) => {
        if (!user) return;
        await CoOpService.joinSession(session.id, user.id, user.heroName || "Hero");
        setActiveSession(session);
        onSessionJoin?.(session.id);
    };

    const handleLeave = async () => {
        if (!user || !activeSession) return;
        await CoOpService.leaveSession(activeSession.id, user.id);
        setActiveSession(null);
    };

    if (!user) {
        if (typeof window !== 'undefined') {
            // Log aborted if needed? No, silent.
        }
        // Fallback for E2E/Loading: Render disabled button to prevent timeout and show state
        return (
            <div className="fixed top-4 right-4 z-50 flex flex-col items-end pointer-events-auto">
                <button
                    disabled
                    className="flex items-center gap-2 px-3 py-2 rounded-full backdrop-blur-md border border-white/10 bg-black/40 text-zinc-600 cursor-not-allowed"
                    data-testid="coop-toggle-button"
                    data-user-status="missing"
                >
                    <Users className="w-4 h-4" />
                    <span className="text-xs">Loading...</span>
                </button>
            </div>
        );
    }



    return (
        <div className="fixed top-4 right-4 z-50 flex flex-col items-end pointer-events-auto">
            {/* Toggle Button */}
            <motion.button
                layout
                onClick={() => setIsExpanded(!isExpanded)}
                className={`flex items-center gap-2 px-3 py-2 rounded-full backdrop-blur-md border border-white/10 transition-colors ${activeSession ? "bg-green-500/20 text-green-400" : "bg-black/40 text-zinc-400 hover:text-white"
                    }`}
                data-testid="coop-toggle-button"
            >
                <Users className="w-4 h-4" />
                {activeSession ? (
                    <span className="text-xs font-mono font-bold">
                        {activeSession.participants.length}/{activeSession.maxParticipants}
                    </span>
                ) : (
                    <span className="text-xs">Multiplayer</span>
                )}
            </motion.button>

            {/* Expanded Panel */}
            <AnimatePresence>
                {isExpanded && (
                    <motion.div
                        initial={{ opacity: 0, y: -10, scale: 0.95 }}
                        animate={{ opacity: 1, y: 0, scale: 1 }}
                        exit={{ opacity: 0, y: -10, scale: 0.95 }}
                        className="mt-2 w-64 bg-zinc-900/90 backdrop-blur-xl border border-white/10 rounded-lg p-4 shadow-2xl"
                    >
                        {activeSession ? (
                            // Active Session View
                            <div className="space-y-4">
                                <div className="flex justify-between items-center border-b border-white/10 pb-2">
                                    <h3 className="text-sm font-bold text-white">Squad</h3>
                                    <div className="flex items-center gap-1 text-[10px] text-green-400">
                                        <Wifi className="w-3 h-3" />
                                        <span>LIVE</span>
                                    </div>
                                </div>

                                <div className="space-y-2 max-h-40 overflow-y-auto">
                                    {activeSession.participants.map(p => (
                                        <div key={p.id} className="flex items-center justify-between text-xs" data-testid="participant-row">
                                            <div className="flex items-center gap-2">
                                                <div className={`w-2 h-2 rounded-full ${p.status === 'active' ? 'bg-green-500' : 'bg-red-500'}`} />
                                                <span className="text-zinc-200">{p.heroName || "Unknown"}</span>
                                            </div>
                                            {/* Could show set % here later */}
                                        </div>
                                    ))}
                                </div>

                                <button
                                    onClick={handleLeave}
                                    className="w-full py-1 text-xs text-red-400 hover:bg-red-500/10 rounded border border-red-500/20"
                                    data-testid="leave-session-button"
                                >
                                    Leave Squad
                                </button>
                            </div>
                        ) : (
                            // Lobby Browser
                            <div className="space-y-4">
                                <h3 className="text-sm font-bold text-white mb-2">Find Squad</h3>

                                {availableSessions.length === 0 ? (
                                    <div className="text-center py-4 text-zinc-500 text-xs">
                                        No active squads found.
                                    </div>
                                ) : (
                                    <div className="space-y-2 max-h-40 overflow-y-auto" data-testid="session-list">
                                        {availableSessions.map(session => (
                                            <div key={session.id} className="flex items-center justify-between bg-white/5 p-2 rounded hover:bg-white/10 cursor-pointer" onClick={() => handleJoin(session)}>
                                                <div>
                                                    <div className="text-xs font-bold text-white">{session.workoutName || "Workout"}</div>
                                                    <div className="text-[10px] text-zinc-400">Host: {session.hostId.slice(0, 4)}...</div>
                                                </div>
                                                <div className="text-xs text-zinc-400">
                                                    {session.participants.length}/{session.maxParticipants}
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                )}

                                <button
                                    onClick={handleCreate}
                                    className="w-full flex items-center justify-center gap-2 py-2 bg-magma/20 hover:bg-magma/30 text-magma text-xs font-bold rounded border border-magma/50 transition-colors"
                                    data-testid="create-session-button"
                                >
                                    <UserPlus className="w-3 h-3" />
                                    Start New Squad
                                </button>
                            </div>
                        )}
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
};

"use client";

import React, { useReducer, useEffect, useState } from "react";
import { toast } from "@/components/ui/GameToast";
import { PwaInstallBanner } from "@/components/ui/PwaInstallBanner";
import Link from "next/link";
import { useRouter } from "next/navigation";

import { Exercise } from "@/types/ironforge";


import { saveWorkoutAction } from "@/actions/integrations/hevy";

import { AnimatePresence, motion } from "framer-motion";
import { mapQuestToHevyPayload } from "@/utils/hevyAdapter";


import { getProgressionAction } from "@/actions/progression/core";
import GeminiLiveCoach from "@/features/training/components/GeminiLiveCoach";
import { Settings } from "lucide-react";


import { Faction } from "@/types/training";
import { OracleChat } from "@/features/oracle/components/OracleChat";

// Dynamic Imports moved to ViewRouter
import { FirstLoginQuest } from "@/features/onboarding/FirstLoginQuest";
import { PersistentHeader } from "@/components/core/PersistentHeader";
import { CoachToggle } from "./components/CoachToggle";
import { ViewRouter } from "./components/ViewRouter";
import { CodexLoader } from "./components/SecondaryViews";
import {
  View,
  DashboardState,
  DashboardClientProps,
} from "./types";
import { dashboardReducer } from "./logic/dashboardReducer";
import { useAmbientSound } from "@/hooks/useAmbientSound";
import { usePlatformContext } from "@/hooks/usePlatformContext";
import { TvHud } from "@/components/ui/TvHud";

const getAmbientZone = (
  view: View,
): "citadel" | "forge" | "arena" | "wilds" | "void" => {
  switch (view) {
    case "citadel":
    case "war_room":
    case "training_center":
    case "social_hub":
    case "guild_hall":
    case "program_builder":
    case "trophy_room":
    case "strength_log":
    case "strava_upload":
    case "cardio_studio":
      return "citadel";
    case "forge":
    case "armory":
    case "item_shop":
    case "marketplace":
      return "forge";
    case "arena":
    case "combat_arena":
    case "world_map":
    case "bestiary":
      return "arena";
    case "grimoire":
      return "void";
    case "import_routines": // <-- Added
      return "citadel";
    default:
      return "citadel";
  }
};

// Consolidated Data Object from Server

const DashboardClient: React.FC<DashboardClientProps> = (props) => {
  const {
    initialData,
    userData,
    faction,
    hasCompletedOnboarding,
    pocketCastsConnected,
    challenges,
    titanState,
    liteMode,
  } = props;

  // Leaderboard data passed from server component (or empty for backward-compat)
  const leaderboardData = props.leaderboardData || [];

  // Use Titan State if available, fallback to User (Legacy)
  const level = titanState?.level || userData?.level || 1;
  const nameMap = new Map<string, string>();
  // Checking page.tsx again: it doesn't pass nameMap in initialData. It was passing it before.
  // We should probably derive it or accept it.
  // The previous code had `nameMap: Map<string, string>` in InitialDataProps.
  // Let's assume for now we use an empty map or fetch it.

  const initialStateFromProps: DashboardState = {
    isCodexLoading: false,
    wellnessData: initialData.wellness,
    ttb: initialData.ttb,
    level: level,
    activeQuest: null,
    questTitle: "",
    exerciseNameMap: nameMap,
    startTime: null,
    currentView: "citadel",
    oracleRecommendation: initialData.recommendation, // rename matched
    auditReport: initialData.auditReport,
    weaknessAudit: initialData.auditReport?.highestPriorityGap
      ? {
        detected: true,
        type: "NONE",
        message: `Focus: ${initialData.auditReport.highestPriorityGap.muscleGroup}`,
        confidence: 1,
      }
      : null,
    forecast: initialData.forecast,
    events: initialData.events,
    titanAnalysis: initialData.titanAnalysis,
    isCoachOpen: false,
    activeBossId: null,
    activePath: initialData.activePath || "WARDEN",
    mobilityLevel: userData?.mobilityLevel || "NONE",
    recoveryLevel: userData?.recoveryLevel || "NONE",
    totalExperience: titanState?.xp || userData?.totalExperience || 0,

    weeklyMastery: initialData.weeklyMastery,
    cardioMode: "cycling", // Default
    returnView: null,
    faction: (faction as Faction) || "HORDE",
    challenges: challenges || [],
    activeDuel: initialData.activeDuel || props.activeDuel,
    trainingContext: initialData.trainingContext,
  };

  const [state, dispatch] = useReducer(dashboardReducer, initialStateFromProps);
  // const [isConfigured, setIsConfigured] = useState(false); // Removed gate logic
  const [showOnboarding, setShowOnboarding] = useState(!hasCompletedOnboarding);
  const router = useRouter();
  const platform = usePlatformContext();
  useEffect(() => {
    dispatch({ type: "UPDATE_CHALLENGES", payload: challenges || [] });
  }, [challenges, dispatch]);

  // God Tier 2: Soundscapes
  useAmbientSound(getAmbientZone(state.currentView));

  /* Hevy API Configuration Gate Removed per user request */

  const handleSaveWorkout = async (isPrivate: boolean) => {
    if (!state.activeQuest || !state.startTime) return;

    const apiKey = userData?.hevyApiKey || localStorage.getItem("hevy_api_key");
    if (!apiKey) {
      toast.error("Access Denied", {
        description: "You need a Hevy API Key to save quests.",
      });
      router.push("/settings");
      return;
    }

    const payload = mapQuestToHevyPayload(
      state.activeQuest,
      state.questTitle,
      state.startTime,
      new Date(),
      isPrivate,
    );
    try {
      await saveWorkoutAction(apiKey, payload);
      toast.success("Quest Log Updated!", {
        description: "The Hevy Archive has explicitly recorded your victory.",
      });
    } catch (error) {
      console.error("Uplink to Hevy failed:", error);
      toast.warning("Loot Secured Locally", {
        description: "Uplink to Hevy failed. Check console.",
      });
    } finally {
      const newProgression = await getProgressionAction();
      if (newProgression) {
        dispatch({
          type: "RECALCULATE_PROGRESSION",
          payload: { level: newProgression.level },
        });
      }
      dispatch({ type: "SAVE_WORKOUT" });
    }
  };

  // View rendering delegated to ViewRouter component
  const viewRouterProps = {
    state,
    dispatch,
    userData,
    titanState,
    pocketCastsConnected,
    liteMode,
    onSaveWorkout: handleSaveWorkout,
    leaderboardEntries: leaderboardData, // Pass it down
  };

  const AnimatedViewWrapper = ({
    children,
    viewKey,
  }: {
    children: React.ReactNode;
    viewKey: string;
  }) => (
    <motion.div
      key={viewKey}
      initial={{ opacity: 0, scale: 0.98, y: 10 }}
      animate={{ opacity: 1, scale: 1, y: 0 }}
      exit={{ opacity: 0, scale: 1.02 }}
      transition={{ duration: 0.3, ease: [0.4, 0, 0.2, 1] }}
      className="w-full"
    >
      {children}
    </motion.div>
  );

  /* Configuration Screen Removed */

  if (state.isCodexLoading) return <main id="codex-loader"><CodexLoader /></main>;

  return (
    <div id="main-content" className="bg-forge-900 min-h-screen bg-noise">
      <div className="scanlines pointer-events-none fixed inset-0 z-50 opacity-5" />

      <PwaInstallBanner />

      {platform === "tv" && (
        <TvHud
          questTitle={state.questTitle}
          heartRate={state.wellnessData?.hrv} // Using HRV as a proxy for HR if HR not direct
          bossName={state.activeBossId || undefined}
        />
      )}

      <Link
        href="/settings"
        className="fixed top-6 right-6 z-50 text-forge-muted hover:text-white transition-colors p-2 hover:rotate-90 duration-300"
        aria-label="Settings"
      >
        <Settings size={24} />
      </Link>

      <PersistentHeader
        level={state.level}
        xp={state.totalExperience}
        gold={userData?.gold || 0}
        faction={state.faction}
        powerRating={titanState?.powerRating || 0}
      />

      <AnimatePresence mode="wait">
        <AnimatedViewWrapper viewKey={state.currentView}>
          <main id="view-container">
            <ViewRouter {...viewRouterProps} />
          </main>
        </AnimatedViewWrapper>
      </AnimatePresence>

      {showOnboarding && (
        <FirstLoginQuest
          onComplete={(newState) => {
            setShowOnboarding(false);
            if (newState) {
              dispatch({
                type: "RECALCULATE_PROGRESSION",
                payload: { level: newState.level },
              });
            }
          }}
        />
      )}

      <CoachToggle onClick={() => dispatch({ type: "TOGGLE_COACH" })} />
      <GeminiLiveCoach
        isOpen={state.isCoachOpen}
        onClose={() => dispatch({ type: "TOGGLE_COACH" })}
      />

      <OracleChat
        context={{
          userId: userData?.id || "unknown",
          path: state.activePath,
          wellness: state.wellnessData,
          mastery: state.weeklyMastery,
          indices: state.ttb,
        }}
      />
    </div>
  );
};

export default DashboardClient;

import React, { useEffect, useState } from "react";
import {
  IntervalsWellness,
  WeaknessAudit,
  TSBForecast,
  TitanLoadCalculation,
  TTBIndices,
  IntervalsEvent,
  ExerciseLog,
} from "@/types";
import { TrainingPath } from "@/types/training";
import {
  Brain,
  TrendingUp,
  AlertTriangle,
  Calendar,
  Battery,
  Activity,
  Flame,
  Wind,
  Trophy,
  Gauge,
  ShieldAlert,
  ZapOff,
} from "lucide-react";
import { AnalyticsService } from "@/services/analytics";
import { AnalyticsWorkerService } from "@/services/analyticsWorker";
import { StorageService } from "@/services/storage";
import TTBCompass from "@/components/TTBCompass";
import { Alert, AlertTitle, AlertDescription } from "@/components/ui/Alert";
import { TrainingMemoryManager } from "@/services/trainingMemoryManager";
import { Skeleton } from "@/components/ui/Skeleton";
import { JargonTooltip } from "@/components/ui/JargonTooltip";

interface UltrathinkDashboardProps {
  wellness: IntervalsWellness | null;
  audit: WeaknessAudit;
  forecast: TSBForecast[];
  ttb?: TTBIndices;
  events?: IntervalsEvent[];
  titanAnalysis?: TitanLoadCalculation;
  activePath?: TrainingPath;
}

const UltrathinkDashboard: React.FC<UltrathinkDashboardProps> = ({
  wellness,
  audit,
  forecast,
  ttb,
  events = [],
  titanAnalysis,
  activePath = "WARDEN",
}) => {
  const [acwrData, setAcwrData] = useState<{
    acwr: number;
    acute: number;
    chronic: number;
  } | null>(null);

  // --- WORKER COMPUTATION ---
  useEffect(() => {
    const runHeavyMath = async () => {
      const history = await StorageService.getHistory();
      if (history && wellness) {
        const res = await AnalyticsWorkerService.computeAdvancedStats(
          history,
          wellness,
        );
        setAcwrData({
          acwr: res.acwr,
          acute: res.acuteLoad,
          chronic: res.chronicLoad,
        });
      }
    };
    runHeavyMath();
  }, [wellness]);

  // Use passed prop or default to a "No Activity" state (or keep static for demo? Let's use 0s if missing to encourage real data)
  const displayTitan: TitanLoadCalculation = titanAnalysis || {
    standardTss: 0,
    titanLoad: 0,
    discrepancy: 0,
    advice: "No recent weight training detected.",
  };
  const upcomingRace = events.find(
    (e) => new Date(e.start_date_local) >= new Date(),
  );

  // ACWR Color Logic
  let acwrColor = "text-green-500";
  let acwrStatus = "Optimal Zone";
  if (acwrData) {
    if (acwrData.acwr > 1.5) {
      acwrColor = "text-red-500 animate-pulse";
      acwrStatus = "DANGER: High Injury Risk";
    } else if (acwrData.acwr > 1.3) {
      acwrColor = "text-orange-500";
      acwrStatus = "Caution: Overreaching";
    } else if (acwrData.acwr < 0.8) {
      acwrColor = "text-zinc-500";
      acwrStatus = "Detraining";
    }
  }

  // --- PARENTING SECURITY LAYER ---
  const debuffs = wellness
    ? TrainingMemoryManager.calculateDebuffs(
      wellness.sleepScore || 100,
      wellness.hrv || 50,
    )
    : [];
  const isSurvivalMode = wellness
    ? TrainingMemoryManager.shouldEnterSurvivalMode(
      {
        ctl: wellness.ctl || 0,
        atl: wellness.atl || 0,
        tsb: wellness.tsb || 0,
        hrv: wellness.hrv || 50,
        sleepScore: wellness.sleepScore || 100,
        bodyBattery: wellness.bodyBattery || 100,
        strengthDelta: 0,
        consecutiveStalls: 0,
        weeksInPhase: 0,
        nutritionMode: "MAINTENANCE",
        sleepDebt: 0,
        acwr: 1.0,
        junkMilePercent: 0,
        neuralLoad: 0,
        impactLoad: 0,
        interferenceEvents: 0,
      },
      activePath,
    )
    : false;

  // ... useEffect ...

  // Removed early return to allow Titan Load (Strength) to be visible even without Cardio Wellness
  // if (!wellness) return null;

  return (
    <div className="space-y-6 animate-fade-in">
      {/* Header */}
      <div className="flex items-center gap-3 border-b border-zinc-800 pb-4">
        <div className="p-2 bg-purple-900/20 border border-purple-500/50 rounded-lg">
          <Brain className="w-6 h-6 text-purple-400" />
        </div>
        <div>
          <h2 className="text-lg font-black uppercase tracking-tight text-white">
            Ultrathink Engine
          </h2>
          <p className="text-xs text-purple-400 font-mono">
            Predictive Biometrics & Logistics
          </p>
        </div>
      </div>

      {/* SURVIVAL MODE ALERT */}
      {isSurvivalMode && (
        <div className="bg-red-950/30 border border-red-500/50 rounded-lg p-4 animate-pulse flex items-start gap-3">
          <ShieldAlert className="w-6 h-6 text-red-500 shrink-0 mt-1" />
          <div>
            <h3 className="text-sm font-black text-red-500 uppercase tracking-widest">
              ⚠️ SURVIVAL MODE ACTIVE
            </h3>
            <p className="text-xs text-red-400 mt-1">
              System integrity critical. All anabolic protocols suspended.
              Active Debuffs:{" "}
              {debuffs.map((d) => d.reason).join(", ") || "Low TSB"}.
            </p>
          </div>
        </div>
      )}

      {/* CAPACITY WARN (If not survival but debuffs exist) */}
      {!isSurvivalMode && debuffs.length > 0 && (
        <div className="bg-orange-950/30 border border-orange-500/50 rounded-lg p-3 flex items-center gap-3">
          <ZapOff className="w-5 h-5 text-orange-500" />
          <div className="flex-1">
            <h3 className="text-xs font-bold text-orange-500 uppercase">
              Capacity Reduced
            </h3>
            <p className="text-[10px] text-orange-400">
              {debuffs.map((d) => d.reason).join(" + ")} detected. High
              intensity load restricted.
            </p>
          </div>
        </div>
      )}

      {/* TTB & Weakness Row */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        {/* TTB Compass (Visual) */}
        {ttb && (
          <div className="bg-zinc-900/50 border border-zinc-800 rounded-lg p-4 flex justify-center items-center">
            <TTBCompass indices={ttb} />
          </div>
        )}

        {/* Weakness Auditor Card */}
        <div className="md:col-span-2 bg-zinc-900/50 border border-zinc-800 rounded-lg p-5 relative overflow-hidden group">
          <div className="absolute top-0 right-0 p-4 opacity-5 group-hover:opacity-10 transition-opacity">
            <Activity className="w-24 h-24 text-zinc-500" />
          </div>

          <h3 className="text-xs font-bold text-zinc-500 uppercase tracking-widest mb-3 flex items-center gap-2">
            <TrendingUp className="w-4 h-4" />
            Performance Auditor
          </h3>

          {audit.detected ? (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertTitle>Bottleneck Identified: {audit.type}</AlertTitle>
              <AlertDescription>{audit.message}</AlertDescription>
            </Alert>
          ) : (
            <div className="text-green-500 text-sm flex items-center gap-2">
              <Activity className="w-4 h-4" />
              <span>System Nominal. No obvious bottlenecks detected.</span>
            </div>
          )}
        </div>
      </div>

      {/* ACWR (NEW OPTIMIZATION) */}
      <div className="bg-[#1a1a1a] border border-zinc-800 rounded-lg p-5 flex items-center justify-between min-h-[100px]">
        {acwrData ? (
          <>
            <div className="flex items-center gap-4">
              <div
                className={`p-3 rounded-full border bg-zinc-900 ${acwrData.acwr > 1.3 ? "border-red-900 text-red-500" : "border-green-900 text-green-500"}`}
              >
                <Gauge className="w-6 h-6" />
              </div>
              <div>
                <h3 className="text-sm font-bold text-white uppercase tracking-wider">
                  <JargonTooltip term="ACWR">ACWR Score</JargonTooltip>
                </h3>
                <div className="flex items-baseline gap-2 mt-1">
                  <span className={`text-2xl font-black ${acwrColor}`}>
                    {acwrData.acwr}
                  </span>
                  <span className="text-xs text-zinc-500 font-mono uppercase">
                    {acwrStatus}
                  </span>
                </div>
