                    {day.tsb > 0 ? `+ ${day.tsb} ` : day.tsb} <JargonTooltip term="TSB">TSB</JargonTooltip>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UltrathinkDashboard;

"use client";

import React from "react";
import dynamic from "next/dynamic";
import { ArrowLeft } from "lucide-react";
import { toast } from "@/components/ui/GameToast";
import { DashboardState, DashboardAction, View } from "../types";
import { TitanState } from "@/actions/titan/core";
import { User } from "@prisma/client";
import { mapQuestToSession } from "@/utils/typeMappers";
import { mapDefinitionToSession } from "@/utils/workoutMapper";

// Static imports for lightweight components
import { CitadelHub } from "@/features/dashboard/CitadelHub";
import StravaUpload from "@/features/training/components/strava/StravaUpload";
import { StrengthContainer } from "@/features/strength/StrengthContainer";
import { ProgramBuilder } from "@/features/training/ProgramBuilder";
import { TrophyRoom } from "@/features/gamification/TrophyRoom";
import { GuildHall } from "@/features/guild/GuildHall";
import TrainingCenter from "@/features/training/TrainingCenter";
import { Citadel } from "./Citadel";
import { QuestCompletion } from "./QuestCompletion";
import {
    EquipmentArmory,
    Bestiary,
    WorldMap,
    Grimoire,
    Arena,
} from "./SecondaryViews";

// Dynamic imports for heavy/client-only components
const RoutineSelector = dynamic(
    () => import("@/features/training/RoutineSelector"),
    { ssr: false },
);
const IronMines = dynamic(() => import("@/features/strength/IronMines"), {
    ssr: false,
});
const CombatArena = dynamic(() => import("@/features/game/CombatArena"), {
    ssr: false,
});
const SocialHub = dynamic(
    () => import("@/features/social/SocialHub").then((mod) => mod.SocialHub),
    { ssr: false },
);
const Marketplace = dynamic(() => import("@/components/game/Marketplace"), {
    ssr: false,
});
const TheForge = dynamic(() => import("@/features/game/TheForge"), {
    ssr: false,
});
const CardioStudio = dynamic(() => import("@/features/training/CardioStudio"), {
    ssr: false,
});

import { LeaderboardEntry } from "@/actions/social/leaderboards";

export interface ViewRouterProps {
    state: DashboardState;
    dispatch: React.Dispatch<DashboardAction>;
    userData?: Partial<User> | null;
    titanState?: TitanState | null;
    pocketCastsConnected?: boolean;
    liteMode?: boolean;
    onSaveWorkout: (isPrivate: boolean) => void;
    leaderboardEntries?: LeaderboardEntry[];
}

/**
 * Routes to the appropriate view based on currentView state.
 * Extracted from DashboardClient to reduce component size.
 */
export const ViewRouter: React.FC<ViewRouterProps> = ({
    state,
    dispatch,
    userData,
    titanState,
    pocketCastsConnected,
    liteMode,
    onSaveWorkout,
    leaderboardEntries,
}) => {
    console.log("[ViewRouter] Rendering view:", state.currentView, "ActiveQuest:", !!state.activeQuest);
    switch (state.currentView) {
        case "citadel":
            return (
                <Citadel
                    state={state}
                    dispatch={dispatch}
                    titanState={titanState}
                    pocketCastsConnected={pocketCastsConnected}
                    liteMode={liteMode}
                    leaderboardEntries={leaderboardEntries}
                />
            );

        case "training_center":
            return (
                <TrainingCenter
                    activePath={state.activePath}
                    mobilityLevel={state.mobilityLevel}
                    recoveryLevel={state.recoveryLevel}
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                    onSelectWorkout={(workout) => {
                        if (workout.type === "STRENGTH" || workout.type === "MOBILITY") {
                            const session = mapDefinitionToSession(workout);
                            dispatch({ type: "START_GENERATED_QUEST", payload: session });
                        } else {
                            dispatch({ type: "START_CODEX_WORKOUT", payload: { workout } });
                        }
                    }}
                    onImportRoutines={() =>
                        dispatch({ type: "SET_VIEW", payload: "import_routines" })
                    }
                />
            );

        case "import_routines":
            return (
                <div className="p-4 md:p-8 animate-fade-in relative min-h-screen">
                    <button
                        onClick={() =>
                            dispatch({ type: "SET_VIEW", payload: "training_center" })
                        }
                        className="absolute top-4 right-4 z-50 px-4 py-2 bg-white/10 hover:bg-white/20 rounded text-white text-sm font-bold flex items-center gap-2"
                    >
                        <ArrowLeft className="w-4 h-4" /> Back to Training Center
                    </button>
                    <RoutineSelector
                        exerciseNameMap={state.exerciseNameMap}
                        onSelectRoutine={async (routine) => {
                            try {
                                const { importHevyRoutineToTemplateAction } =
                                    await import("@/actions/integrations/hevy");
                                await importHevyRoutineToTemplateAction(routine);
                                toast.success("Routine Imported", {
                                    description: `${routine.title} is now a Workout Template.`,
                                });
                                dispatch({ type: "SET_VIEW", payload: "training_center" });
                            } catch (err: unknown) {
                                console.error(err);
                                const message = err instanceof Error ? err.message : "Unknown error";
                                toast.error("Import Failed", { description: message });
                            }
                        }}
                        mode="import"
                    />
                </div>
            );

        case "war_room":
            return (
                <RoutineSelector
                    exerciseNameMap={state.exerciseNameMap}
                    onSelectRoutine={(routine) =>
                        dispatch({
                            type: "SELECT_ROUTINE",
                            payload: { routine, nameMap: state.exerciseNameMap },
                        })
                    }
                />
            );

        case "iron_mines":
            console.log("[ViewRouter] Mounting IronMines. ActiveQuest Exercises:", state.activeQuest?.length);
            return (
                <IronMines
                    session={mapQuestToSession(state.activeQuest!, state.questTitle)}
                    onComplete={() => dispatch({ type: "COMPLETE_QUEST" })}
                    onExit={() => dispatch({ type: "ABORT_QUEST" })}
                    hrvBaseline={titanState?.hrvBaseline || userData?.hrv || 60}
                    userId={userData?.id}
                />
            );

        case "item_shop":
        case "marketplace":
            return (
                <Marketplace
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                />
            );

        case "social_hub":
            return (
                <SocialHub
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                    currentUserId={userData?.id}
                />
            );

        case "quest_completion":
            return (
                <QuestCompletion
                    onSave={onSaveWorkout}
                    onCancel={() => dispatch({ type: "ABORT_QUEST" })}
                />
            );

        case "forge":
            return (
                <TheForge
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                />
            );

        case "armory":
            return <EquipmentArmory />;

        case "bestiary":
            return (
                <Bestiary
                    userLevel={state.level}
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                />
            );

        case "world_map":
            return (
                <WorldMap
                    userLevel={state.level}
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                    onEnterCombat={(bossId) =>
                        dispatch({ type: "START_COMBAT", payload: bossId })
                    }
                />
            );

        case "grimoire":
            return (
                <Grimoire
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                />
            );

        case "guild_hall":
            return (
                <ViewWithCloseButton
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                >
                    <GuildHall userId={userData?.id ?? ""} />
                </ViewWithCloseButton>
            );

        case "strength_log":
            return (
                <ViewWithCloseButton
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                >
                    <StrengthContainer userId={userData?.id ?? ""} />
                </ViewWithCloseButton>
            );

        case "program_builder":
            return (
                <ViewWithCloseButton
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                >
                    <ProgramBuilder userId={userData?.id ?? ""} />
                </ViewWithCloseButton>
            );

        case "trophy_room":
            return (
                <ViewWithCloseButton
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                >
                    <TrophyRoom userId={userData?.id ?? ""} />
                </ViewWithCloseButton>
            );

        case "arena":
            return (
                <Arena
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                />
            );

        case "combat_arena":
            return state.activeBossId ? (
                <CombatArena
                    bossId={state.activeBossId}
                    onClose={() => dispatch({ type: "SET_VIEW", payload: "world_map" })}
                />
            ) : (
                <Citadel
                    state={state}
                    dispatch={dispatch}
                    pocketCastsConnected={pocketCastsConnected}
                    titanState={titanState}
                    liteMode={liteMode}
                />
            );

        case "cardio_studio":
            return (
                <CardioStudio
                    mode={state.cardioMode || "cycling"}
                    activeWorkout={state.activeWorkout}
                    userProfile={{
                        ftpCycle: userData?.ftpCycle || 200,
                        ftpRun: userData?.ftpRun || 250,
                    }}
                    onClose={() => dispatch({ type: "RETURN_TO_PREVIOUS" })}
                    userId={userData?.id}
                    activeDuel={state.activeDuel}
                    pocketCastsConnected={pocketCastsConnected}
                    streak={titanState?.streak || userData?.loginStreak || 0}
                    maxHr={userData?.maxHr || 190}
                />
            );

        case "strava_upload":
            return (
                <div className="flex flex-col items-center justify-center min-h-screen p-4">
                    <StravaUpload />
                    <button
                        onClick={() => dispatch({ type: "SET_VIEW", payload: "citadel" })}
                        className="mt-8 text-slate-400 hover:text-white transition-colors"
                    >
                        Return to Citadel
                    </button>
                </div>
            );

        default:
            return (
                <Citadel state={state} dispatch={dispatch} titanState={titanState} />
            );
    }
};

/**
 * Reusable wrapper for views that just need a close button.
 */
const ViewWithCloseButton: React.FC<{
    children: React.ReactNode;
    onClose: () => void;
}> = ({ children, onClose }) => (
    <div className="p-4 relative min-h-screen">
        <button
            onClick={onClose}
            className="absolute top-4 right-4 z-50 px-4 py-2 bg-white/10 hover:bg-white/20 rounded text-white text-sm font-bold"
        >
            Close
        </button>
        {children}
    </div>
);

export default ViewRouter;

import { DashboardState, DashboardAction } from "../types";
import { mapHevyToQuest } from "@/utils/hevyAdapter";
import { mapSessionToQuest } from "@/utils/typeMappers";
import { mapDefinitionToSession } from "@/utils/workoutMapper";

export const dashboardReducer = (
  state: DashboardState,
  action: DashboardAction,
): DashboardState => {
  switch (action.type) {
    case "INITIAL_DATA_LOAD_START":
      return { ...state, isCodexLoading: true };
    case "INITIAL_DATA_LOAD_SUCCESS":
      return {
        ...state,
        isCodexLoading: false,
        exerciseNameMap: action.payload.nameMap,
        ttb: action.payload.ttb,
        wellnessData: action.payload.wellness,
        level: action.payload.level,
        auditReport: action.payload.auditReport,
        oracleRecommendation: action.payload.oracleRec,
        weaknessAudit: action.payload.weaknessAudit,
        forecast: action.payload.forecast,
        events: action.payload.events,
        titanAnalysis: action.payload.titanAnalysis,
      };
    case "INITIAL_DATA_LOAD_FAILURE":
      return { ...state, isCodexLoading: false };
    case "SELECT_ROUTINE":
      return {
        ...state,
        questTitle: action.payload.routine.title,
        activeQuest: mapHevyToQuest(
          action.payload.routine,
          action.payload.nameMap,
        ),
        startTime: new Date(),
        currentView: "iron_mines",
      };
    case "COMPLETE_QUEST":
      return { ...state, currentView: "quest_completion" };
    case "SAVE_WORKOUT":
    case "ABORT_QUEST":
      return {
        ...state,
        activeQuest: null,
        questTitle: "",
        startTime: null,
        currentView: "citadel",
      };
    case "SET_VIEW":
      return { ...state, currentView: action.payload };
    case "START_COMBAT":
      return {
        ...state,
        currentView: "combat_arena",
        activeBossId: action.payload,
      };

    case "START_GENERATED_QUEST":
      return {
        ...state,
        questTitle: action.payload.name,
        activeQuest: mapSessionToQuest(
          action.payload.blocks.flatMap((b) => b.exercises || []),
        ),
        startTime: new Date(),
        currentView: "iron_mines",
      };
    case "RECALCULATE_PROGRESSION":
      return { ...state, level: action.payload.level };
    case "TOGGLE_COACH":
      return { ...state, isCoachOpen: !state.isCoachOpen };
    case "UPDATE_PATH":
      return { ...state, activePath: action.payload };
    case "SET_CARDIO_MODE":
      return {
        ...state,
        cardioMode: action.payload,
        currentView: "cardio_studio",
        returnView: state.currentView,
      };
    case "START_CODEX_WORKOUT":
      const { workout } = action.payload;
      if (workout.type === "RUN" || workout.type === "BIKE") {
        return {
          ...state,
          activeWorkout: workout,
          cardioMode: workout.type === "RUN" ? "running" : "cycling",
          currentView: "cardio_studio",
          returnView: "training_center",
        };
      } else {
        return {
          ...state,
          activeWorkout: workout,
          questTitle: workout.name,
          activeQuest: null,
          startTime: new Date(),
          currentView: "iron_mines",
        };
      }
    case "RETURN_TO_PREVIOUS":
      return {
        ...state,
        currentView: state.returnView || "citadel",
        returnView: null,
        activeWorkout: undefined,
      };
    case "UPDATE_CHALLENGES":
      return { ...state, challenges: action.payload };
    default:
      return state;
  }
};

"use client";

import { useEffect, useState } from "react";
import { motion } from "framer-motion";
import Image from 'next/image';

import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { getDuelArenaStateAction } from "@/actions/pvp/duel";
import { executeTitanCombatTurnAction } from "@/actions/pvp/titan";
import { Swords, Timer, Zap, MessageSquare } from "lucide-react";
import { toast } from "sonner";
import { EmotePicker } from "@/features/pvp/components/EmotePicker";
import { EmoteOverlay, useBroadcastEmote } from "@/features/pvp/components/EmoteOverlay";

interface DuelArenaProps {
    duelId: string;
    currentUserId: string;
    onClose: () => void;
}

export function DuelArena({ duelId, currentUserId, onClose }: DuelArenaProps) {
    const [duelState, setDuelState] = useState<any>(null);
    const [loading, setLoading] = useState(true);
    const [combatLog, setCombatLog] = useState<string[]>([]);
    const [isAttacking, setIsAttacking] = useState(false);
    const [isMuted, setIsMuted] = useState(false);

    const { broadcastEmote } = useBroadcastEmote(duelId, duelState?.challenger?.id === currentUserId ? duelState?.challenger?.heroName : duelState?.defender?.heroName);

    // Polling logic
    useEffect(() => {
        let intervalId: NodeJS.Timeout;

        const fetchState = async () => {
            const result = await getDuelArenaStateAction(duelId);
            if (result.success) {
                setDuelState(result.duel);
            } else {
                toast.error("Lost connection to arena...");
            }
            setLoading(false);
        };

        // Initial fetch
        fetchState();

        // Poll every 3 seconds
        intervalId = setInterval(fetchState, 3000);

        return () => clearInterval(intervalId);
    }, [duelId]);

    const handleAttack = async () => {
        setIsAttacking(true);
        try {
            const result = await executeTitanCombatTurnAction(duelId);
            if (result.success && result.combatLog) {
                // Add combat messages to log
                setCombatLog(prev => [...result.combatLog, ...prev].slice(0, 10)); // Keep last 10
                toast.success(`You dealt ${result.damageDealt?.challenger || result.damageDealt?.defender} damage!`);

                // Refresh state
                const stateResult = await getDuelArenaStateAction(duelId);
                if (stateResult.success) {
                    setDuelState(stateResult.duel);
                }
            } else {
                toast.error(result.error || "Attack failed");
            }
        } catch (_error) {
            toast.error("Combat error");
        } finally {
            setIsAttacking(false);
        }
    };

    if (loading) {
        return (
            <div className="flex flex-col items-center justify-center p-12 text-slate-400">
                <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-amber-500 mb-4" />
                <p>Connecting to Iron Arena...</p>
            </div>
        );
    }

    if (!duelState) return null;

    const isChallenger = currentUserId === duelState.challengerId;
    const user = isChallenger ? duelState.challenger : duelState.defender;
    const opponent = isChallenger ? duelState.defender : duelState.challenger;

    const userScore = isChallenger ? duelState.challengerScore : duelState.defenderScore;
    const opponentScore = isChallenger ? duelState.defenderScore : duelState.challengerScore;

    // For Titan vs Titan, scores represent damage dealt
    const maxScore = Math.max(userScore, opponentScore, 1000);
    const userProgress = Math.min((userScore / maxScore) * 100, 100);
    const opponentProgress = Math.min((opponentScore / maxScore) * 100, 100);

    return (
        <div className="fixed inset-0 z-50 bg-black/90 flex items-center justify-center p-4">
            <div className="w-full max-w-4xl bg-slate-900 border border-slate-700 rounded-2xl overflow-hidden shadow-2xl relative">
                <div className="absolute top-4 right-4 z-10 flex gap-2">
                    <Button
                        variant="ghost"
                        size="icon"
                        className={isMuted ? "text-red-500" : "text-slate-400"}
                        onClick={() => setIsMuted(!isMuted)}
                        title={isMuted ? "Unmute Emotes" : "Mute Emotes"}
                    >
                        <MessageSquare className="w-5 h-5" />
                    </Button>
                    <Button
                        variant="ghost"
                        className="text-slate-400 hover:text-white"
                        onClick={onClose}
                    >
                        Exit Arena
                    </Button>
                </div>

                {/* Dynamic Background */}
                <div className="absolute inset-0 bg-gradient-to-b from-slate-900 to-slate-950 opacity-90" />
                <div className={`absolute inset-0 bg-[url('/assets/arena_bg.jpg')] bg-cover bg-center opacity-20`} />

                <div className="relative z-0 p-8 space-y-8">
                    {/* Emote Display */}
                    <EmoteOverlay
                        matchId={duelId}
                        currentUserId={currentUserId}
                        isMuted={isMuted}
                    />

                    {/* Battle Scene */}
                    <div className="relative h-80 bg-slate-950/80 rounded-2xl border border-amber-500/20 shadow-[inset_0_0_50px_rgba(0,0,0,1)] overflow-hidden">
                        {/* Background Scene */}
                        <div className="absolute inset-0 bg-[url('/assets/arena_bg.jpg')] bg-cover bg-center opacity-30 mix-blend-overlay" />

                        <div className="flex justify-between items-center h-full px-12 relative">
                            {/* Left Titan (User) */}
                            <motion.div
                                initial={{ x: -200, opacity: 0 }}
                                animate={{ x: 0, opacity: 1 }}
                                transition={{ type: "spring", stiffness: 50, damping: 15 }}
                                className="relative w-64 h-64"
                            >
                                <div className="absolute inset-0 bg-blue-500/10 blur-3xl rounded-full" />
                                <Image
                                    src={`/assets/game/titans/${(user?.activePath || 'WARDEN').toLowerCase()}/base.png`}
                                    alt="User Titan"
                                    fill
                                    className="object-contain drop-shadow-[0_0_20px_rgba(59,130,246,0.3)]"
                                    priority
                                    unoptimized
                                />
                                <div className="absolute -bottom-4 left-1/2 -translate-x-1/2 bg-blue-500/20 border border-blue-500/50 px-3 py-1 rounded text-[10px] font-black uppercase tracking-widest text-blue-400 backdrop-blur-sm">
                                    {user.heroName || 'YOU'}
                                </div>
                            </motion.div>

                            {/* VS Effect */}
                            <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 flex flex-col items-center">
                                <motion.div
                                    animate={{
                                        scale: [1, 1.1, 1],
                                        opacity: [0.1, 0.2, 0.1]
                                    }}
                                    transition={{ duration: 2, repeat: Infinity }}
                                    className="text-8xl font-black italic text-amber-500/20"
                                >
                                    VS
                                </motion.div>
                            </div>

                            {/* Right Titan (Opponent) */}
                            <motion.div
                                initial={{ x: 200, opacity: 0 }}
                                animate={{ x: 0, opacity: 1 }}
                                transition={{ type: "spring", stiffness: 50, damping: 15 }}
                                className="relative w-64 h-64 scale-x-[-1]"
                            >
                                <div className="absolute inset-0 bg-red-500/10 blur-3xl rounded-full" />
                                <Image
                                    src={`/assets/game/titans/${(opponent?.activePath || 'WARDEN').toLowerCase()}/base.png`}
                                    alt="Opponent Titan"
                                    fill
                                    className="object-contain drop-shadow-[0_0_20px_rgba(239,68,68,0.3)]"
                                    priority
                                    unoptimized
                                />
                                <div className="absolute -bottom-4 left-1/2 -translate-x-1/2 bg-red-500/20 border border-red-500/50 px-3 py-1 rounded text-[10px] font-black uppercase tracking-widest text-red-400 backdrop-blur-sm scale-x-[-1]">
                                    {opponent.heroName || 'OPPONENT'}
                                </div>
                            </motion.div>
                        </div>
                    </div>
                    {/* Header */}
                    <div className="text-center space-y-2">
                        <Badge variant="outline" className="bg-red-500/10 text-red-500 border-red-500/50 px-4 py-1 text-sm tracking-widest uppercase">
                            <Swords className="w-4 h-4 inline mr-2" />
                            Titan vs Titan
                        </Badge>
                        <h2 className="text-3xl font-black italic">COMBAT ARENA</h2>
                        <div className="flex items-center justify-center gap-2 text-slate-400">
                            <Timer className="w-4 h-4" />
                            <span>Status: {duelState.status}</span>
                        </div>
                    </div>

                    {/* Score Bars */}
                    <div className="space-y-8 py-8 bg-black/40 rounded-xl p-6 border border-white/5">
                        {/* User Score */}
                        <div className="relative">
                            <div className="flex justify-between items-end mb-2 text-blue-400 font-bold uppercase tracking-wider text-sm">
                                <span>{user.heroName || "You"}</span>
                                <span>{userScore} DMG</span>
                            </div>
                            <div className="h-12 bg-slate-800 rounded-full relative overflow-hidden ring-2 ring-blue-500/20">
                                <motion.div
                                    className="absolute top-0 bottom-0 left-0 bg-gradient-to-r from-blue-800 to-blue-500"
                                    initial={{ width: 0 }}
                                    animate={{ width: `${userProgress}%` }}
                                    transition={{ type: "spring", stiffness: 50 }}
                                />
                            </div>
                        </div>

                        {/* Opponent Score */}
                        <div className="relative">
                            <div className="flex justify-between items-end mb-2 text-red-400 font-bold uppercase tracking-wider text-sm">
                                <span>{opponent.heroName || "Opponent"}</span>
                                <span>{opponentScore} DMG</span>
                            </div>
                            <div className="h-12 bg-slate-800 rounded-full relative overflow-hidden ring-2 ring-red-500/20">
                                <motion.div
                                    className="absolute top-0 bottom-0 left-0 bg-gradient-to-r from-red-800 to-red-500"
                                    initial={{ width: 0 }}
                                    animate={{ width: `${opponentProgress}%` }}
                                    transition={{ type: "spring", stiffness: 50 }}
                                />
                            </div>
                        </div>
                    </div>

                    {/* Combat Log */}
                    <div className="bg-slate-950/50 rounded-lg p-4 h-32 overflow-y-auto border border-slate-800">
                        <div className="text-xs font-mono text-slate-400 space-y-1">
                            {combatLog.length === 0 ? (
                                <p className="text-center italic">No combat yet. Strike first!</p>
                            ) : (
                                combatLog.map((msg, i) => (
                                    <p key={i} className="animate-in fade-in slide-in-from-bottom-2">
                                        {msg}
                                    </p>
                                ))
                            )}
                        </div>
                    </div>

                    {/* Attack & Emotes */}
                    <div className="flex gap-4">
                        <Button
                            onClick={handleAttack}
                            disabled={isAttacking || duelState.status !== "ACTIVE"}
                            className="flex-1 bg-gradient-to-r from-red-600 to-amber-600 hover:from-red-500 hover:to-amber-500 text-white font-black text-lg py-6 rounded-lg shadow-lg shadow-red-900/50 disabled:opacity-50"
                        >
                            {isAttacking ? (
                                <>
                                    <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white mr-2" />
                                    Attacking...
                                </>
                            ) : (
                                <>
                                    <Zap className="w-5 h-5 mr-2" />
                                    ATTACK
                                </>
                            )}
                        </Button>

                        <EmotePicker
                            matchId={duelId}
                            disabled={duelState.status !== "ACTIVE"}
                            onSend={(emote) => broadcastEmote(emote, currentUserId)}
                        />
                    </div>
                </div>
            </div>
        </div>
    );
}

"use client";

import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import { Book, Skull, Scroll, Search, Sword, LogOut } from "lucide-react";
import { getBestiaryAction } from "@/actions/systems/world";

type Tab = "bestiary" | "legends";

interface BestiaryEntry {
  id: string;
  name: string;
  description: string;
  image: string | null;
  levelReq: number;
  isDiscovered: boolean;
  kills: number;
}

interface GrimoireProps {
  onClose?: () => void;
}

export default function Grimoire({ onClose }: GrimoireProps) {
  const [activeTab, setActiveTab] = useState<Tab>("bestiary");
  const [entries, setEntries] = useState<BestiaryEntry[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchGrimoire = async () => {
      try {
        const { monsters } = await getBestiaryAction();
        setEntries(monsters as BestiaryEntry[]);
      } catch (error) {
        console.error("Failed to open Grimoire", error);
      } finally {
        setLoading(false);
      }
    };
    fetchGrimoire();
  }, []);

  return (
    <div className="h-full bg-zinc-950 flex flex-col font-serif">
      {/* Header */}
      <div className="p-6 border-b border-zinc-900 bg-[#0a0a0a] relative">
        {onClose && (
          <button
            onClick={onClose}
            className="absolute top-6 right-6 p-2 bg-zinc-900 border border-zinc-700 rounded hover:bg-zinc-800 transition-colors"
          >
            <LogOut className="w-5 h-5 text-zinc-400" />
          </button>
        )}
        <div className="flex items-center gap-3 mb-2">
          <Book className="w-6 h-6 text-purple-500" />
          <h2 className="text-3xl font-black uppercase tracking-tighter text-zinc-200">
            The Grimoire
          </h2>
        </div>
        <p className="text-zinc-500 italic text-sm">
          Keeper of knowledge, slain foes, and ancient legends.
        </p>

        {/* Tabs */}
        <div className="flex gap-4 mt-6">
          <button
            onClick={() => setActiveTab("bestiary")}
            className={`px-4 py-2 rounded uppercase text-xs font-bold tracking-widest transition-colors ${activeTab === "bestiary" ? "bg-purple-900/50 text-purple-300 border border-purple-500" : "bg-zinc-900 text-zinc-600 hover:text-zinc-400"}`}
          >
            Bestiary
          </button>
          <button
            onClick={() => setActiveTab("legends")}
            className={`px-4 py-2 rounded uppercase text-xs font-bold tracking-widest transition-colors ${activeTab === "legends" ? "bg-amber-900/50 text-amber-300 border border-amber-500" : "bg-zinc-900 text-zinc-600 hover:text-zinc-400"}`}
          >
            Legends
          </button>
        </div>
      </div>

      {/* Content Content */}
      <div className="flex-1 overflow-y-auto p-6">
        {loading ? (
          <div className="text-center text-zinc-600 animate-pulse mt-10">
            Deciphering Runes...
          </div>
        ) : (
          <>
            {activeTab === "bestiary" && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                {entries.map((monster) => (
                  <motion.div
                    key={monster.id}
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    className={`
                                            relative h-64 border rounded-xl overflow-hidden group
                                            ${monster.isDiscovered
                        ? "bg-zinc-900 border-zinc-800 hover:border-purple-500/50 transition-colors"
                        : "bg-black border-zinc-900"
                      }
                                        `}
                  >
                    {/* Image / Silhouette */}
                    <div className="absolute inset-0 bg-zinc-900">
                      {/* Placeholder for real image or generic monster icon */}
                      <div className="w-full h-full flex items-center justify-center opacity-20">
                        <Skull className="w-32 h-32" />
                      </div>
                    </div>

                    {/* Overlay Content */}
                    <div className="absolute inset-x-0 bottom-0 p-4 bg-gradient-to-t from-black via-black/90 to-transparent">
                      {monster.isDiscovered ? (
                        <>
                          <h3 className="text-xl font-bold text-zinc-200">
                            {monster.name}
                          </h3>
                          <p className="text-zinc-500 text-xs line-clamp-2 mt-1">
                            {monster.description}
                          </p>
                          <div className="flex items-center gap-2 mt-3 text-xs font-mono text-purple-400">
                            <Sword className="w-3 h-3" />
                            <span>Slain: {monster.kills}</span>
                          </div>
                        </>
                      ) : (
                        <div className="flex flex-col items-center text-center">
                          <div className="text-zinc-700 text-4xl font-black">
                            ? ? ?
                          </div>
                          <div className="text-zinc-600 text-[10px] uppercase tracking-widest mt-2">
                            Undiscovered Entity
                          </div>
                          <div className="text-zinc-800 text-[10px] mt-1">
                            Requires Level {monster.levelReq}
                          </div>
                        </div>
                      )}
                    </div>
                  </motion.div>
                ))}
              </div>
            )}

            {activeTab === "legends" && (
              <div className="flex flex-col items-center justify-center h-full text-zinc-600">
                <Scroll className="w-12 h-12 mb-4 opacity-20" />
                <p>The scrolls are empty. Only time will tell your story.</p>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

import React, { useState, useEffect, useRef } from "react";
import { RaidBoss, ChatMessage } from "../../types";
import {
  // Skull removed
  Swords,
  Shield,
  Users,
  Send,
  Info,
  WifiOff,
  Zap,
  DoorOpen,
} from "lucide-react";
import { cn } from "../../lib/utils";
import { createClient } from "@/utils/supabase/client";
import { RealtimeChannel } from "@supabase/supabase-js";
import {
  sendChatAction,
  attackBossAction,
  getUserStatsAction,
} from "@/actions/guild/core";
import { motion } from "framer-motion";

interface GuildHallProps {
  onClose: () => void;
}

const DEFAULT_BOSS: RaidBoss = {
  id: "boss_iron_golem",
  name: "The Iron Golem",
  totalHp: 1000000,
  currentHp: 1000000,
  image: "🦾",
  description:
    "A massive automaton forged in the heart of the Iron Mines. It grows stronger with every failed attempt.",
  rewards: ["Kinetic Shard x50", "Golem Core", "Title: Golem Smasher"],
};

const ATTACK_COST = 5;

interface FloatingText {
  id: number;
  text: string;
  x: number;
  y: number;
  color: string;
}

export const GuildHall: React.FC<GuildHallProps> = ({ onClose }) => {
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [input, setInput] = useState("");
  const [boss, setBoss] = useState<RaidBoss>(DEFAULT_BOSS);
  const [isConnected, setIsConnected] = useState(false);
  const [onlineCount, setOnlineCount] = useState(1);

  // Performance Coach State
  const [kineticEnergy, setKineticEnergy] = useState<number>(0);
  const [_heroName, setHeroName] = useState<string>("Titan");

  // Visual Juice State
  const [floatingTexts, setFloatingTexts] = useState<FloatingText[]>([]);
  const [shake, setShake] = useState(false);

  // Animation state
  const [hpAnim, setHpAnim] = useState(boss.currentHp);

  const channelRef = useRef<RealtimeChannel | null>(null);

  // Initial Load & Subscription
  useEffect(() => {
    let mounted = true;

    const init = async () => {
      const supabase = createClient();
      setIsConnected(true);

      // Fetch User Stats (Ammo)
      const userStats = await getUserStatsAction();
      if (userStats && mounted) {
        setKineticEnergy(userStats.kineticEnergy);
        setHeroName(userStats.heroName || "Titan");
      }

      // 1. Fetch Initial Boss State
      const { data: bossData } = await supabase
        .from("raid_bosses")
        .select("*")
        .eq("is_active", true)
        .single();

      if (bossData) {
        const mappedBoss: RaidBoss = {
          id: bossData.id,
          name: bossData.name,
          totalHp: bossData.total_hp,
          currentHp: bossData.current_hp,
          image: bossData.image,
          description: bossData.description,
          rewards: bossData.rewards,
        };
        if (mounted) {
          setBoss(mappedBoss);
          setHpAnim(mappedBoss.currentHp);
        }
      }

      // 2. Fetch Recent Chat
      const { data: chatData } = await supabase
        .from("chat_messages")
        .select("*")
        .order("timestamp", { ascending: false })
        .limit(50);

      if (chatData && mounted) {
        const mappedMessages = chatData.map((msg: any) => ({
          id: msg.id,
          userName: msg.user_name,
          message: msg.message,
          timestamp: msg.timestamp,
          type: msg.type,
        }));
        setMessages(mappedMessages.reverse());
      }

      // 3. Subscribe to Changes
      const channel = supabase
        .channel("guild_hall_global")
        .on(
          "postgres_changes",
          {
            event: "UPDATE",
            schema: "public",
            table: "raid_bosses",
            filter: `id=eq.${bossData?.id || DEFAULT_BOSS.id}`,
          },
          (payload) => {
            const newHp = payload.new.current_hp;
            if (mounted) {
              setBoss((prev) => ({ ...prev, currentHp: newHp }));
            }
          },
        )
        .on(
          "postgres_changes",
          { event: "INSERT", schema: "public", table: "chat_messages" },
          (payload) => {
            const newMsgRaw = payload.new;
            const newMsg: ChatMessage = {
              id: newMsgRaw.id,
              userName: newMsgRaw.user_name,
              message: newMsgRaw.message,
              timestamp: newMsgRaw.timestamp,
              type: newMsgRaw.type,
            };

            if (mounted) {
              setMessages((prev) => [...prev, newMsg]);
            }
          },
        )
        .on("presence", { event: "sync" }, () => {
          const state = channel.presenceState();
          if (mounted) setOnlineCount(Object.keys(state).length + 1);
        })
        .subscribe();

      await channel.track({ online_at: new Date().toISOString() });
      channelRef.current = channel;
    };

    init();

    return () => {
      mounted = false;
      if (channelRef.current) {
        channelRef.current.unsubscribe();
      }
    };
  }, []);

  // Lerp HP for smooth animation
  useEffect(() => {
    const interval = setInterval(() => {
      setHpAnim((prev) => {
        const diff = boss.currentHp - prev;
        if (Math.abs(diff) < 1) return boss.currentHp;
        return prev + diff * 0.1;
      });
    }, 30);
    return () => clearInterval(interval);
  }, [boss.currentHp]);

  // Floating Text Animation Loop
  useEffect(() => {
    const interval = setInterval(() => {
      setFloatingTexts((prev) =>
        prev.map((ft) => ({ ...ft, y: ft.y - 2 })).filter((ft) => ft.y > -50),
      );
    }, 30);
    return () => clearInterval(interval);
  }, []);

  const hpPct = (hpAnim / boss.totalHp) * 100;

  const spawnFloatingText = (text: string, color: string = "white") => {
    // Random offset
    const xOffset = Math.random() * 100 - 50;
    const yOffset = Math.random() * 50 - 25;

    const newText: FloatingText = {
      id: Date.now() + Math.random(),
      text,
      x: xOffset,
      y: yOffset,
      color,
    };
    setFloatingTexts((prev) => [...prev, newText]);
  };

  const handleSendMessage = async () => {
    if (!input.trim()) return;
    const msgToSend = input;
    setInput("");

    try {
      await sendChatAction(msgToSend);
    } catch (_e) {
      console.error("Failed to send message", _e);
    }
  };

  const handleAttack = async (e: React.MouseEvent) => {
    if (!boss.id) return;

    // 1. Check Energy (Optimistic Check)
    if (kineticEnergy < ATTACK_COST) {
      spawnFloatingText("LOW ENERGY!", "#ef4444"); // Red
      return;
    }

    // 2. Consume Energy Optimistically
    setKineticEnergy((prev) => Math.max(0, prev - ATTACK_COST));

    // 3. Visual Juice
    setShake(true);
    setTimeout(() => setShake(false), 200);

    // 4. Server Action
    try {
      const result = await attackBossAction(boss.id);
      if (result.damage) {
        spawnFloatingText(`-${result.damage}`, "#facc15"); // Yellow
      }
    } catch (err: any) {
      console.error("Attack failed", err);
      // Revert Energy on fail? Or just let it sync next time.
      spawnFloatingText("MISS!", "#a1a1aa");
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      exit={{ opacity: 0, y: -20 }}
      className={cn(
        "h-full bg-forge-900 overflow-hidden flex flex-col font-serif transition-transform duration-75",
        shake ? "translate-x-1 translate-y-1" : "",
      )}
    >
      {/* Header */}
      <div className="bg-black/50 border-b border-forge-border p-6 flex justify-between items-center shrink-0">
        <div className="flex items-center gap-4">
          <div className="p-3 bg-red-950/30 border border-red-500/50 rounded-lg shadow-[0_0_15px_rgba(239,68,68,0.2)]">
            <Swords className="w-8 h-8 text-red-500" />
          </div>
          <div>
            <h1 className="text-3xl font-black text-white uppercase tracking-tighter">
              The Guild Hall
            </h1>
            <div className="flex items-center gap-4 text-xs font-bold uppercase tracking-widest text-zinc-500">
              <span className="flex items-center gap-1">
                {isConnected ? (
                  <Users className="w-3 h-3 text-cyan-500" />
                ) : (
                  <WifiOff className="w-3 h-3 text-red-500" />
                )}
                {isConnected ? `${onlineCount} Online` : "Offline Mode"}
              </span>
              <span className="flex items-center gap-1 text-yellow-500">
                <Zap className="w-3 h-3 text-yellow-500" /> {kineticEnergy}{" "}
                Energy
                <span className="opacity-50 text-[10px] ml-1">
                  (Cost: {ATTACK_COST})
                </span>
              </span>
            </div>
          </div>
        </div>
        <button
          onClick={onClose}
          className="bg-red-900/20 border-2 border-red-500/50 hover:bg-red-900/50 text-red-500 px-6 py-2 rounded-lg font-bold uppercase text-xs transition-all hover:scale-105 active:scale-95 flex items-center gap-2 shadow-[0_0_15px_rgba(239,68,68,0.2)]"
        >
          <DoorOpen className="w-4 h-4" />
          Retreat
        </button>
      </div>

      <div className="flex-1 flex overflow-hidden">
        {/* Main Battle Area */}
        <div className="flex-1 p-8 flex flex-col items-center justify-center relative overflow-hidden bg-[radial-gradient(circle_at_center,_var(--tw-gradient-stops))] from-red-900/10 via-forge-900 to-forge-900">
          {/* Floating Texts Layer */}
          <div className="absolute inset-0 pointer-events-none flex items-center justify-center overflow-visible">
            {floatingTexts.map((ft) => (
              <div
                key={ft.id}
                className="absolute text-4xl font-black animate-float-up"
                style={{
                  transform: `translate(${ft.x}px, ${ft.y}px)`,
                  color: ft.color,
                  textShadow: "0 2px 4px rgba(0,0,0,0.8)",
                }}
              >
                {ft.text}
              </div>
            ))}
          </div>

          {/* Boss Visualization */}
          <div
            className={cn(
              "relative group cursor-pointer transition-transform active:scale-95 select-none",
              kineticEnergy < ATTACK_COST
                ? "opacity-50 cursor-not-allowed filter grayscale"
                : "",
            )}
            onClick={handleAttack}
          >
            <motion.div
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.9, filter: "brightness(1.5)" }}
              className="text-[12rem] drop-shadow-[0_0_50px_rgba(239,68,68,0.3)] filter grayscale-[0.2] relative z-10"
            >
