"use client";

import React, { useState, useEffect } from "react";
import ForgeCard from "@/components/ui/ForgeCard";
import ForgeButton from "@/components/ui/ForgeButton";
import { LoadingSpinner } from "@/components/ui/LoadingSpinner";
import { ITEMS, RECIPES } from "@/data/gameData";
import { UserInventory, CraftingRecipe } from "@/types/game";
import { craftItem } from "@/actions/economy/forge";
import { toast } from "@/components/ui/GameToast";
import { Hammer, Anvil, Coins } from "lucide-react";

interface TheForgeProps {
  onClose: () => void;
}

const TheForge: React.FC<TheForgeProps> = ({ onClose }) => {
  // TODO: In a real app, inventory would be passed in or fetched via hook
  // For MVP, we mock the initial state or fetch it on mount
  const [inventory, setInventory] = useState<UserInventory | null>(null);
  const [_loading, _setLoading] = useState(false);
  const [craftingId, setCraftingId] = useState<string | null>(null);

  // Initial Mock Load (Replace with server action fetch later if needed)
  useEffect(() => {
    // Simulating data fetch
    const mockInventory: UserInventory = {
      userId: "current-user",
      gold: 500,
      items: [
        { itemId: "item_iron_ore", count: 10 },
        { itemId: "item_flux", count: 5 },
      ],
    };
    setInventory(mockInventory);
  }, []);

  const handleCraft = async (recipe: CraftingRecipe) => {
    if (!inventory) return;
    setCraftingId(recipe.id);

    try {
      const result = await craftItem(recipe.id);
      if (result.success && result.inventory) {
        // Update local state with new inventory from server
        // Note: The mock server action returns the inventory, so we use that.
        // In a real app we might revalidatePath or use a router refresh.
        // But for client interactivity, updating state is faster.
        // Since the mock action is stateless (doesn't persist to DB truly yet),
        // we might need to manually update local state if the server result isn't fully persistent.
        // BUT, our server action returns 'inventory'. Let's trust it.
        // Wait, the Mock Action re-computes inventory freshly every time from static mock,
        // so it won't persist across calls in this specific implementation step.
        // TO FIX: The server action calculates result based on Input Inventory it generates.
        // For this UI to feel "real", we should manually update the local state
        // to reflect the craft immediately, assuming success.

        // Manual Optimistic Update for MVP fluidity:
        const newItems = [...inventory.items];

        // Deduct
        recipe.materials.forEach((mat) => {
          const idx = newItems.findIndex((i) => i.itemId === mat.itemId);
          if (idx > -1) {
            newItems[idx] = {
              ...newItems[idx],
              count: newItems[idx].count - mat.count,
            };
            if (newItems[idx].count <= 0) newItems.splice(idx, 1);
          }
        });

        // Add
        const resIdx = newItems.findIndex(
          (i) => i.itemId === recipe.resultItemId,
        );
        if (resIdx > -1) {
          newItems[resIdx] = {
            ...newItems[resIdx],
            count: newItems[resIdx].count + recipe.resultCount,
          };
        } else {
          newItems.push({
            itemId: recipe.resultItemId,
            count: recipe.resultCount,
          });
        }

        setInventory({
          ...inventory,
          gold: inventory.gold - recipe.goldCost,
          items: newItems,
        });

        toast.success(result.message, {
          description: "Item added to inventory.",
        });
      } else {
        toast.error(result.message);
      }
    } catch (_error) {
      toast.error("The Hammer failed to strike.");
    } finally {
      setCraftingId(null);
    }
  };

  if (!inventory) return <LoadingSpinner />;

  return (
    <div className="p-4 md:p-8 animate-fade-in max-w-6xl mx-auto">
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="font-heading text-3xl text-magma tracking-widest uppercase flex items-center gap-3">
            <Anvil className="w-8 h-8" /> The Forge
          </h1>
          <p className="text-forge-muted font-mono text-sm mt-1">
            Refine materials. Forge destiny.
          </p>
        </div>
        <ForgeButton variant="rune" onClick={onClose}>
          Exit
        </ForgeButton>
      </div>

      {/* Inventory Bar */}
      <ForgeCard className="mb-8 p-4 flex items-center gap-6 overflow-x-auto bg-black/40 border-forge-border">
        <div className="flex items-center gap-2 text-gold font-mono border-r border-forge-border pr-6">
          <Coins className="w-5 h-5" />
          <span className="text-xl">{inventory.gold}</span>
        </div>
        {inventory.items.map((slot) => {
          const item = ITEMS.find((i) => i.id === slot.itemId);
          if (!item) return null;
          return (
            <div
              key={slot.itemId}
              className="flex items-center gap-2 min-w-fit px-3 py-1 bg-forge-800 rounded border border-forge-border/50"
            >
              <span className="text-xl">{item.image}</span>
              <div className="flex flex-col leading-none">
                <span
                  className={`text-xs uppercase font-bold text-${item.rarity === "common" ? "white" : "magma"}`}
                >
                  {item.name}
                </span>
                <span className="text-xs text-forge-muted font-mono">
                  x{slot.count}
                </span>
              </div>
            </div>
          );
        })}
      </ForgeCard>

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {RECIPES.map((recipe) => {
          const resultItem = ITEMS.find((i) => i.id === recipe.resultItemId);
          if (!resultItem) return null;

          const canCraft =
            inventory.gold >= recipe.goldCost &&
            recipe.materials.every((mat) => {
              const slot = inventory.items.find((i) => i.itemId === mat.itemId);
              return slot && slot.count >= mat.count;
            });

          return (
            <ForgeCard
              key={recipe.id}
              className="relative group overflow-visible"
            >
              <div className="flex justify-between items-start mb-4">
                <div className="flex items-center gap-3">
                  <div className="w-12 h-12 bg-forge-900 rounded-lg flex items-center justify-center text-3xl border border-forge-border shadow-inner">
                    {resultItem.image}
                  </div>
                  <div>
                    <h3 className="font-heading text-lg text-white group-hover:text-magma transition-colors">
                      {recipe.name}
                    </h3>
                    <div className="flex items-center gap-1 text-gold text-xs font-mono">
                      <Coins className="w-3 h-3" /> {recipe.goldCost}
                    </div>
                  </div>
                </div>
              </div>

              <div className="space-y-2 mb-6">
                <div className="text-xs text-forge-muted uppercase tracking-wider">
                  Requires:
                </div>
                <div className="space-y-1">
                  {recipe.materials.map((mat) => {
                    const matItem = ITEMS.find((i) => i.id === mat.itemId);
                    const inventoryCount =
                      inventory.items.find((i) => i.itemId === mat.itemId)
                        ?.count || 0;
                    const hasEnough = inventoryCount >= mat.count;

                    return (
                      <div
                        key={mat.itemId}
                        className="flex justify-between text-sm font-mono"
                      >
                        <span className="text-white/80">
                          {matItem?.name || mat.itemId}
                        </span>
                        <span
                          className={
                            hasEnough ? "text-green-400" : "text-blood"
                          }
                        >
                          {inventoryCount}/{mat.count}
                        </span>
                      </div>
                    );
                  })}
                </div>
              </div>

              <ForgeButton
                variant={canCraft ? "magma" : "rune"}
                className="w-full"
                disabled={!canCraft || craftingId === recipe.id}
                onClick={() => handleCraft(recipe)}
              >
                {craftingId === recipe.id ? (
                  <>
                    <LoadingSpinner className="w-4 h-4 mr-2" /> Forging...
                  </>
                ) : (
                  <>
                    <Hammer className="w-4 h-4 mr-2" /> Forge Item
                  </>
                )}
              </ForgeButton>
            </ForgeCard>
          );
        })}
      </div>
    </div>
  );
};

export default TheForge;

import React, { useState } from "react";
import {
  Session,
  Block,
  BlockType,
  Exercise,
  ExerciseLogic,
  Set,
} from "@/types";
import {
  Plus,
  Trash2,
  Save,
  Dumbbell,
  ArrowDown,
  ChevronUp,
  ChevronDown,
} from "lucide-react";
import { StorageService } from "@/services/storage";

interface DungeonBuilderProps {
  onSave: (session: Session) => void;
  onCancel: () => void;
}

const DungeonBuilder: React.FC<DungeonBuilderProps> = ({
  onSave,
  onCancel,
}) => {
  const [name, setName] = useState("");
  const [zoneName, setZoneName] = useState("");
  const [blocks, setBlocks] = useState<Block[]>([]);

  const addBlock = (type: BlockType) => {
    const newBlock: Block = {
      id: `blk_${Date.now()}`,
      name: type === BlockType.TRANSITION ? "Transition Area" : "New Station",
      type,
      exercises: type === BlockType.STATION ? [] : undefined,
      setupInstructions:
        type === BlockType.TRANSITION ? ["Setup instruction 1"] : undefined,
    };
    setBlocks([...blocks, newBlock]);
  };

  const removeBlock = (index: number) => {
    setBlocks(blocks.filter((_, i) => i !== index));
  };

  const moveBlock = (index: number, direction: "UP" | "DOWN") => {
    if (direction === "UP" && index === 0) return;
    if (direction === "DOWN" && index === blocks.length - 1) return;

    const newBlocks = [...blocks];
    const targetIndex = direction === "UP" ? index - 1 : index + 1;
    const temp = newBlocks[targetIndex];
    newBlocks[targetIndex] = newBlocks[index];
    newBlocks[index] = temp;
    setBlocks(newBlocks);
  };

  const addExercise = (blockIndex: number) => {
    const newEx: Exercise = {
      id: `ex_${Date.now()}`,
      name: "New Exercise",
      logic: ExerciseLogic.FIXED_REPS,
      sets: [
        { id: `s_${Date.now()}_1`, reps: 10, completed: false },
        { id: `s_${Date.now()}_2`, reps: 10, completed: false },
        { id: `s_${Date.now()}_3`, reps: 10, completed: false },
      ],
      instructions: ["Perform with perfect form."],
    };

    const updatedBlocks = [...blocks];
    if (!updatedBlocks[blockIndex].exercises)
      updatedBlocks[blockIndex].exercises = [];
    updatedBlocks[blockIndex].exercises!.push(newEx);
    setBlocks(updatedBlocks);
  };

  const updateExercise = (
    bIdx: number,
    eIdx: number,
    field: keyof Exercise,
    value: any,
  ) => {
    const updatedBlocks = [...blocks];
    updatedBlocks[bIdx].exercises![eIdx] = {
      ...updatedBlocks[bIdx].exercises![eIdx],
      [field]: value,
    };
    setBlocks(updatedBlocks);
  };

  const handleSave = () => {
    if (!name) return alert("Dungeon must have a name!");
    const session: Session = {
      id: `custom_${Date.now()}`,
      name,
      zoneName: zoneName || "The Player Realm",
      difficulty: "Normal",
      blocks,
      isCustom: true,
    };
    onSave(session);
  };

  return (
    <div className="h-full bg-[#050505] p-6 overflow-y-auto font-serif text-zinc-200">
      <h1 className="text-3xl font-black text-[#c79c6e] mb-6 border-b border-[#46321d] pb-4">
        Dungeon Architect
      </h1>

      <div className="space-y-4 mb-8">
        <div className="space-y-1">
          <label className="text-xs font-bold uppercase text-zinc-500">
            Dungeon Name
          </label>
          <input
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="w-full bg-[#111] border border-zinc-700 p-3 rounded focus:border-[#c79c6e] focus:outline-none"
            placeholder="e.g. The Iron Fortress"
          />
        </div>
        <div className="space-y-1">
          <label className="text-xs font-bold uppercase text-zinc-500">
            Zone / Flavor Text
          </label>
          <input
            value={zoneName}
            onChange={(e) => setZoneName(e.target.value)}
            className="w-full bg-[#111] border border-zinc-700 p-3 rounded focus:border-[#c79c6e] focus:outline-none"
            placeholder="e.g. A dark cavern filled with heavy weights..."
          />
        </div>
      </div>

      <div className="space-y-6">
        {blocks.map((block, bIdx) => (
          <div
            key={block.id}
            className="bg-[#1a1a1a] border border-zinc-800 rounded-lg p-4 relative group transition-all"
          >
            <div className="absolute top-2 right-2 flex gap-1">
              <button
                onClick={() => moveBlock(bIdx, "UP")}
                disabled={bIdx === 0}
                className="p-1 text-zinc-600 hover:text-white disabled:opacity-30 disabled:hover:text-zinc-600"
              >
                <ChevronUp className="w-4 h-4" />
              </button>
              <button
                onClick={() => moveBlock(bIdx, "DOWN")}
                disabled={bIdx === blocks.length - 1}
                className="p-1 text-zinc-600 hover:text-white disabled:opacity-30 disabled:hover:text-zinc-600"
              >
                <ChevronDown className="w-4 h-4" />
              </button>
              <div className="w-px h-4 bg-zinc-700 mx-1 self-center"></div>
              <button
                onClick={() => removeBlock(bIdx)}
                className="p-1 text-zinc-600 hover:text-red-500"
              >
                <Trash2 className="w-4 h-4" />
              </button>
            </div>

            <div className="mb-4 pr-24">
              <input
                value={block.name}
                onChange={(e) => {
                  const newBlocks = [...blocks];
                  newBlocks[bIdx].name = e.target.value;
                  setBlocks(newBlocks);
                }}
                className="bg-transparent text-xl font-bold text-white focus:outline-none w-full"
              />
              <span className="text-[10px] uppercase font-bold text-[#c79c6e] bg-[#c79c6e]/10 px-2 py-0.5 rounded">
                {block.type}
              </span>
            </div>

            {block.type === BlockType.STATION && (
              <div className="space-y-4 pl-4 border-l-2 border-zinc-800">
                {block.exercises?.map((ex, eIdx) => (
                  <div key={ex.id} className="space-y-2">
                    <div className="flex items-center gap-2">
                      <Dumbbell className="w-4 h-4 text-zinc-500" />
                      <input
                        value={ex.name}
                        onChange={(e) =>
                          updateExercise(bIdx, eIdx, "name", e.target.value)
                        }
                        className="bg-[#111] border border-zinc-700 p-1 rounded text-sm w-full"
                      />
                    </div>
                    <div className="flex gap-2">
                      {ex.sets.map((set, sIdx) => (
                        <div
                          key={set.id}
                          className="bg-zinc-900 px-2 py-1 rounded text-xs border border-zinc-800"
                        >
                          Set {sIdx + 1}: {set.reps} reps
                        </div>
                      ))}
                    </div>
                  </div>
                ))}
                <button
                  onClick={() => addExercise(bIdx)}
                  className="text-xs text-zinc-500 hover:text-[#c79c6e] flex items-center gap-1"
                >
                  <Plus className="w-3 h-3" /> Add Monster (Exercise)
                </button>
              </div>
            )}

            {block.type === BlockType.TRANSITION && (
              <textarea
                className="w-full bg-[#111] text-xs text-zinc-400 p-2 rounded"
                placeholder="Setup instructions..."
                value={block.setupInstructions?.join("\n")}
                onChange={(e) => {
                  const newBlocks = [...blocks];
                  newBlocks[bIdx].setupInstructions =
                    e.target.value.split("\n");
                  setBlocks(newBlocks);
                }}
              />
            )}
          </div>
        ))}
      </div>

      <div className="mt-8 grid grid-cols-2 gap-4">
        <button
          onClick={() => addBlock(BlockType.STATION)}
          className="py-4 border-2 border-dashed border-zinc-700 text-zinc-500 hover:border-[#c79c6e] hover:text-[#c79c6e] rounded flex justify-center items-center gap-2 uppercase font-bold text-xs"
        >
          <Plus className="w-4 h-4" /> Add Station
        </button>
        <button
          onClick={() => addBlock(BlockType.TRANSITION)}
          className="py-4 border-2 border-dashed border-zinc-700 text-zinc-500 hover:border-blue-500 hover:text-blue-500 rounded flex justify-center items-center gap-2 uppercase font-bold text-xs"
        >
          <ArrowDown className="w-4 h-4" /> Add Transition
        </button>
      </div>

      <div className="mt-8 flex gap-4 sticky bottom-0 bg-[#050505] py-4 border-t border-zinc-900">
        <button
          onClick={onCancel}
          className="flex-1 py-3 text-zinc-500 hover:text-white"
        >
          Cancel
        </button>
        <button
          onClick={handleSave}
          className="flex-1 py-3 bg-[#c79c6e] hover:bg-[#d4a87a] text-[#46321d] font-bold uppercase tracking-widest rounded flex items-center justify-center gap-2"
        >
          <Save className="w-4 h-4" /> Save Dungeon
        </button>
      </div>
    </div>
  );
};

export default DungeonBuilder;

import React, { useState, useMemo } from "react";
import { Session, ExerciseLog } from "@/types";
import {
  Scroll,
  Sword,
  Map,
  Lock,
  Star,
  Sparkles,
  Hammer,
  CheckCircle2,
  ChevronRight,
} from "lucide-react";

interface QuestLogProps {
  sessions: Session[];
  history: ExerciseLog[];
  onSelectSession: (sessionId: string) => void;
  level: number;
}

type ActFilter = "ACT_I" | "ACT_II" | "ACT_III";

const QuestLog: React.FC<QuestLogProps> = ({
  sessions,
  history,
  onSelectSession,
  level,
}) => {
  const [activeAct, setActiveAct] = useState<ActFilter>("ACT_I");

  // --- FILTER LOGIC ---
  const filteredSessions = useMemo(() => {
    return sessions.filter((session) => {
      if (activeAct === "ACT_I") {
        // Core static sessions: Not Generated AND Not Custom
        return !session.isGenerated && !session.isCustom;
      }
      if (activeAct === "ACT_II") {
        // Oracle / Generated: Must be generated
        return session.isGenerated === true;
      }
      if (activeAct === "ACT_III") {
        // User Custom Dungeons: Must be custom
        return session.isCustom === true;
      }
      return false;
    });
  }, [sessions, activeAct]);

  const acts = [
    {
      id: "ACT_I",
      label: "Act I: Initiation",
      icon: <Map className="w-4 h-4" />,
      desc: "Standard Protocol",
    },
    {
      id: "ACT_II",
      label: "Act II: The Oracle",
      icon: <Sparkles className="w-4 h-4" />,
      desc: "AI Generated",
    },
    {
      id: "ACT_III",
      label: "Act III: Architect",
      icon: <Hammer className="w-4 h-4" />,
      desc: "Custom Dungeons",
    },
  ];

  return (
    <div className="bg-[#111] border-2 border-[#46321d] rounded-lg overflow-hidden flex flex-col shadow-2xl">
      {/* Header / Tabs */}
      <div className="flex border-b border-[#46321d]">
        {acts.map((act) => (
          <button
            key={act.id}
            onClick={() => setActiveAct(act.id as ActFilter)}
            className={`flex-1 py-4 px-2 flex flex-col items-center gap-1 transition-all relative overflow-hidden group
              ${activeAct === act.id
                ? "bg-[#1a1a1a] text-[#c79c6e]"
                : "bg-[#0a0a0a] text-zinc-500 hover:text-zinc-400 hover:bg-[#111]"
              }
            `}
          >
            {activeAct === act.id && (
              <div className="absolute top-0 left-0 w-full h-0.5 bg-[#c79c6e] shadow-[0_0_10px_#c79c6e]" />
            )}
            <div
              className={`p-1 rounded ${activeAct === act.id ? "bg-[#c79c6e]/10" : ""}`}
            >
              {act.icon}
            </div>
            <span className="text-[10px] font-bold uppercase tracking-widest">
              {act.label}
            </span>
          </button>
        ))}
      </div>

      {/* Content Area */}
      <div className="p-6 min-h-[300px] bg-[url('https://www.transparenttextures.com/patterns/dark-leather.png')] bg-zinc-900/50">
        {/* Act Description */}
        <div className="mb-6 flex items-center justify-between">
          <div>
            <h3 className="text-xl font-serif font-bold text-white uppercase tracking-wider">
              {acts.find((a) => a.id === activeAct)?.label}
            </h3>
            <p className="text-xs text-zinc-500 font-sans">
              {activeAct === "ACT_I" &&
                "The foundational training grounds. Master the basics."}
              {activeAct === "ACT_II" &&
                "Dynamic challenges forged by the Spirit Guide based on your biometrics."}
              {activeAct === "ACT_III" &&
                "Custom dungeons constructed by your own hand."}
            </p>
          </div>
          <div className="text-right">
            <span className="text-xs font-bold text-zinc-500 uppercase tracking-widest">
              Available Quests: {filteredSessions.length}
            </span>
          </div>
        </div>

        {/* Quest List */}
        <div className="space-y-4">
          {filteredSessions.length === 0 ? (
            <div className="flex flex-col items-center justify-center py-12 border-2 border-dashed border-zinc-800 rounded-lg opacity-50">
              <Lock className="w-12 h-12 text-zinc-700 mb-2" />
              <p className="text-zinc-500 font-serif text-sm">
                No Quests Available in this Region.
              </p>
              {activeAct === "ACT_II" && (
                <p className="text-[10px] text-zinc-600 mt-1">
                  Consult the Oracle to generate new paths.
                </p>
              )}
              {activeAct === "ACT_III" && (
                <p className="text-[10px] text-zinc-600 mt-1">
                  Use the Dungeon Builder to construct this area.
                </p>
              )}
            </div>
          ) : (
            filteredSessions.map((session) => {
              const isGenerated = session.isGenerated;
              const isCustom = session.isCustom;

              let borderColor = "border-[#c0b3a0] group-hover:border-[#a335ee]";
              let iconColor = "bg-[#46321d] border-[#6b4e31] text-[#c79c6e]";
              let typeLabel = "Daily Quest";

              if (isGenerated) {
                borderColor =
                  "border-purple-500 shadow-[0_0_10px_rgba(168,85,247,0.15)] group-hover:border-purple-400";
                iconColor = "bg-purple-900 border-purple-500 text-purple-200";
                typeLabel = "Prophecy";
              } else if (isCustom) {
                borderColor =
                  "border-green-500 shadow-[0_0_10px_rgba(34,197,94,0.15)] group-hover:border-green-400";
                iconColor = "bg-green-900 border-green-500 text-green-200";
                typeLabel = "Construct";
              }

              return (
                <button
                  key={session.id}
                  onClick={() => onSelectSession(session.id)}
                  className={`w-full group relative flex items-start gap-4 p-4 border-2 rounded bg-zinc-950 text-left transition-all hover:-translate-y-1 hover:shadow-xl ${borderColor}`}
                >
                  {/* Icon Box */}
                  <div
                    className={`mt-1 p-3 rounded border shadow-inner transition-colors ${iconColor}`}
                  >
                    {isGenerated ? (
                      <Sparkles className="w-6 h-6 animate-pulse" />
                    ) : isCustom ? (
                      <Hammer className="w-6 h-6" />
                    ) : (
                      <Sword className="w-6 h-6" />
                    )}
                  </div>

                  <div className="flex-1">
                    <div className="flex justify-between items-start">
                      <h4 className="text-lg font-bold text-zinc-200 group-hover:text-white font-serif tracking-wide">
                        {session.name}
                      </h4>
                      <span className="text-[10px] font-bold uppercase tracking-widest text-zinc-500 bg-zinc-900 px-2 py-1 rounded border border-zinc-800">
                        {typeLabel}
                      </span>
                    </div>

                    <p className="text-zinc-500 text-xs italic font-serif mt-1 border-l-2 border-zinc-800 pl-2">
                      {session.zoneName || "Unknown Territory"}
                    </p>

                    {/* Rewards / Difficulty */}
                    <div className="flex items-center gap-4 mt-3 text-[10px] text-zinc-400 font-mono uppercase">
                      <span className="flex items-center gap-1 text-[#ffd700]">
                        <Star className="w-3 h-3 fill-current" />
                        XP: High
                      </span>
                      <span className="flex items-center gap-1">
                        <Scroll className="w-3 h-3" />
                        Blocks: {session.blocks.length}
                      </span>
                      {session.levelReq && session.levelReq > level && (
                        <span className="text-red-500 flex items-center gap-1">
                          <Lock className="w-3 h-3" /> Req Lvl{" "}
                          {session.levelReq}
                        </span>
                      )}
                    </div>
                  </div>

                  <div className="self-center opacity-0 group-hover:opacity-100 transition-opacity -ml-4 group-hover:ml-0 text-[#c79c6e]">
                    <ChevronRight className="w-6 h-6" />
                  </div>
                </button>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
};

export default QuestLog;

import React, { useState, useEffect } from "react";
import Image from "next/image";
import { Block } from "@/types";
import {
  Wrench,
  CheckCircle2,
  ArrowRight,
  Clock,
  SkipForward,
  Keyboard,
  X,
} from "lucide-react";
import { playSound } from "@/utils";

interface TransitionViewProps {
  block: Block;
  onComplete: () => void;
  onAbort: () => void;
}

// Mapping of setup names to image URLs (simulated for demo with matching aesthetic)
const SETUP_IMAGES: Record<string, string> = {
  "Landmine Station":
    "https://placehold.co/800x450/09090b/ea580c?text=Landmine+Configuration",
  "Hyper Pro GHD Mode":
    "https://placehold.co/800x450/09090b/ea580c?text=GHD+Configuration",
  "Belt Squat Station":
    "https://placehold.co/800x450/09090b/ea580c?text=Belt+Squat+Configuration",
};

const TransitionView: React.FC<TransitionViewProps> = ({
  block,
  onComplete,
  onAbort,
}) => {
  const [seconds, setSeconds] = useState(0);

  useEffect(() => {
    // Play sound on mount to signal phase change
    playSound("quest_accept");

    const timer = setInterval(() => {
      setSeconds((s) => s + 1);
    }, 1000);
    return () => clearInterval(timer);
  }, []);

  // Keyboard shortcut listener
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (e.code === "Space" || e.code === "Enter") {
        e.preventDefault();
        playSound("ding");
        onComplete();
      }
    };
    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [onComplete]);

  const formatTime = (totalSeconds: number) => {
    const m = Math.floor(totalSeconds / 60);
    const s = totalSeconds % 60;
    return `${m.toString().padStart(2, "0")}:${s.toString().padStart(2, "0")}`;
  };

  const setupImage = block.targetSetupName
    ? SETUP_IMAGES[block.targetSetupName]
    : null;

  return (
    <div className="flex flex-col h-full animate-fade-in bg-[#050505]">
      {/* Header */}
      <div className="bg-orange-600 p-6 shadow-2xl shadow-orange-900/20 sticky top-0 z-10 relative">
        <button
          onClick={onAbort}
          className="absolute top-6 right-6 text-orange-200 hover:text-white transition-colors"
          title="Abandon Setup"
        >
          <X className="w-6 h-6" />
        </button>
        <div className="flex items-center justify-between mb-2 pr-8">
          <h2 className="text-orange-100 font-bold uppercase tracking-widest text-sm flex items-center gap-2">
            <Wrench className="w-4 h-4" />
            Setup Phase
          </h2>
          <div className="flex items-center gap-2 bg-orange-700/50 px-3 py-1 rounded font-mono text-orange-100 font-bold">
            <Clock className="w-4 h-4" />
            {formatTime(seconds)}
          </div>
        </div>
        <h1 className="text-3xl font-black text-white leading-tight">
          {block.targetSetupName || "Prepare Station"}
        </h1>
      </div>

      {/* Content */}
      <div className="flex-1 p-6 overflow-y-auto space-y-6">
        {/* Instructions */}
        <div className="space-y-4">
          <h3 className="text-zinc-500 uppercase tracking-widest text-xs font-bold border-b border-zinc-800 pb-2">
            Configuration Protocol
          </h3>
          <ul className="space-y-4">
            {block.setupInstructions?.map((inst, idx) => (
              <li
                key={idx}
                className="flex items-start gap-4 p-4 bg-zinc-900 border border-zinc-800 rounded-lg group hover:border-orange-500/30 transition-colors"
              >
                <div className="mt-0.5 min-w-[24px] h-6 rounded-full border-2 border-zinc-700 text-zinc-500 flex items-center justify-center text-xs font-mono font-bold group-hover:border-orange-500 group-hover:text-orange-500 transition-colors">
                  {idx + 1}
                </div>
                <p className="text-zinc-300 leading-relaxed font-sans">
                  {inst}
                </p>
              </li>
            ))}
          </ul>
        </div>

        {/* Visual Aid */}
        {setupImage ? (
          <div className="aspect-video bg-zinc-900 border border-zinc-800 rounded-lg overflow-hidden shadow-lg relative group">
            <Image
              src={setupImage}
              alt={block.targetSetupName || "Setup"}
              fill
              className="object-cover opacity-90 group-hover:opacity-100 transition-opacity"
            />
            <div className="absolute bottom-0 left-0 right-0 bg-gradient-to-t from-zinc-950/90 to-transparent p-4">
              <span className="text-xs font-mono text-zinc-300 uppercase tracking-widest">
                Visual Reference: {block.targetSetupName}
              </span>
            </div>
          </div>
        ) : (
          <div className="aspect-video bg-zinc-900 border border-zinc-800 rounded-lg flex flex-col items-center justify-center text-zinc-700">
            <Wrench className="w-12 h-12 mb-2 opacity-20" />
            <span className="font-mono text-xs uppercase">
              Visual Reference: {block.targetSetupName || "N/A"}
            </span>
          </div>
        )}

        <div className="flex items-center justify-center text-zinc-600 text-xs font-serif uppercase tracking-widest gap-2">
          <Keyboard className="w-4 h-4" />
          <span>Press [SPACE] to Confirm Setup</span>
        </div>
      </div>

      {/* Footer Action */}
      <div className="p-6 bg-zinc-950 border-t border-zinc-900 space-y-3">
        <button
          onClick={() => {
            playSound("ding");
            onComplete();
          }}
          className="w-full h-16 bg-zinc-100 hover:bg-white text-zinc-950 font-black text-xl uppercase tracking-widest rounded flex items-center justify-center gap-3 shadow-[0_0_20px_rgba(255,255,255,0.1)] transition-all active:scale-[0.98]"
        >
          <span>Station Ready</span>
          <ArrowRight className="w-6 h-6" />
        </button>

        <button
          onClick={onComplete}
          className="w-full py-4 border border-zinc-800 text-zinc-500 hover:text-zinc-300 hover:border-zinc-700 hover:bg-zinc-900 rounded font-mono text-xs font-bold uppercase tracking-widest transition-all flex items-center justify-center gap-2"
        >
          <SkipForward className="w-4 h-4" />
          <span>Manual Override</span>
        </button>
      </div>
    </div>
  );
};

export default TransitionView;

import React, { useState, useEffect } from "react";
import {
  Cloud,
  X,
  Shield,
  RefreshCw,
  CheckCircle2,
  Server,
  Wifi,
} from "lucide-react";
import { ValhallaPayload, ValhallaSyncResult } from "@/types";
import { ValhallaService } from "@/services/valhalla";
import { playSound } from "@/utils";
import { useSkills } from "@/context/SkillContext";

interface ValhallaGateProps {
  isOpen: boolean;
  onClose: () => void;
  heroName: string | undefined;
  onBind: (name: string, id: string) => void;
  syncPayload: ValhallaPayload | null; // Partial payload from App
}

const ValhallaGate: React.FC<ValhallaGateProps> = ({
  isOpen,
  onClose,
  heroName,
  onBind,
  syncPayload,
}) => {
  const [step, setStep] = useState<"BIND" | "IDLE" | "SYNCING" | "SUCCESS">(
    "BIND",
  );
  const [inputName, setInputName] = useState(heroName || "");
  const [logs, setLogs] = useState<string[]>([]);

  // Integrate Skills Context
  const { purchasedSkillIds } = useSkills();

  // Construct final payload with skills
  const finalPayload: ValhallaPayload | null = syncPayload
    ? {
      ...syncPayload,
      skills: Array.from(purchasedSkillIds),
    }
    : null;

  useEffect(() => {
    if (isOpen) {
      if (heroName) {
        setStep("IDLE");
      } else {
        setStep("BIND");
      }
      setLogs([]);
    }
  }, [isOpen, heroName]);

  const addLog = (msg: string) => setLogs((prev) => [...prev, msg]);

  const handleBind = async () => {
    if (!inputName) return;
    setStep("SYNCING");
    addLog("Initiating Soul Bind Protocol...");

    const res = await ValhallaService.bindSoul(inputName);

    if (res.success) {
      playSound("quest_accept");
      addLog(`Identity Confirmed. Welcome, ${inputName}.`);
      addLog(`Valhalla ID Assigned: ${res.id}`);
      onBind(inputName, res.id);
      // Step transition handled by useEffect on heroName change
    }
  };

  const handleSync = async () => {
    if (!finalPayload) return;
    setStep("SYNCING");
    playSound("quest_accept");
    addLog("Opening Bifrost Gate...");
    addLog("Compressing Soul Data...");

    const res = await ValhallaService.engraveRecords(finalPayload);

    if (res.success) {
      playSound("achievement");
      addLog("Success: " + res.message);
      setStep("SUCCESS");
      setTimeout(() => {
        onClose();
      }, 2000);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 z-[100] flex items-center justify-center bg-[#050505]/90 backdrop-blur-md animate-fade-in p-4">
      <div className="w-full max-w-md bg-[#0a0f14] border-2 border-cyan-900 rounded-lg shadow-[0_0_50px_rgba(8,145,178,0.2)] overflow-hidden font-serif relative">
        {/* Valhalla Texture */}
        <div className="absolute inset-0 bg-[url('https://www.transparenttextures.com/patterns/cubes.png')] opacity-10 pointer-events-none"></div>

        {/* Header */}
        <div className="relative bg-gradient-to-r from-cyan-950 to-[#0a0f14] p-6 border-b border-cyan-900/50 flex justify-between items-start">
          <div>
            <div className="flex items-center gap-2 text-cyan-400 mb-1">
              <Cloud className="w-6 h-6" />
              <h2 className="font-bold uppercase tracking-[0.2em] text-sm">
                Project Valhalla
              </h2>
            </div>
            <p className="text-cyan-700 text-[10px] font-sans">
              IronForge Cloud Services (v1.0)
            </p>
          </div>
          <button
            onClick={onClose}
            className="text-cyan-800 hover:text-cyan-400 transition-colors"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        {/* Content */}
        <div className="p-8 space-y-6 relative z-10">
          {/* STEP 1: BIND SOUL (Login) */}
          {step === "BIND" && (
            <div className="space-y-4 animate-slide-up">
              <div className="text-center">
                <div className="w-20 h-20 bg-cyan-900/20 border border-cyan-500 rounded-full flex items-center justify-center mx-auto mb-4 shadow-[0_0_20px_rgba(6,182,212,0.3)] animate-pulse">
                  <Shield className="w-10 h-10 text-cyan-400" />
                </div>
                <h3 className="text-cyan-100 font-bold uppercase tracking-widest mb-2">
                  Bind Your Soul
                </h3>
                <p className="text-cyan-500/60 text-xs font-sans max-w-xs mx-auto">
                  Create a permanent link to the Hall of Records to enable
                  cross-device progression.
                </p>
              </div>
              <div className="space-y-2">
                <label className="text-[10px] font-bold uppercase text-cyan-700 tracking-widest">
                  Hero Name
                </label>
                <input
                  value={inputName}
                  onChange={(e) => setInputName(e.target.value)}
                  className="w-full bg-[#050505] border border-cyan-900 p-3 text-cyan-100 focus:border-cyan-500 focus:outline-none rounded font-mono text-center uppercase tracking-widest"
                  placeholder="ENTER NAME"
                />
              </div>
              <button
                onClick={handleBind}
                disabled={!inputName}
                className="w-full py-4 bg-cyan-900/30 border border-cyan-600 text-cyan-400 hover:bg-cyan-800/40 font-bold uppercase tracking-widest rounded transition-all disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Engrave Name
              </button>
            </div>
          )}

          {/* STEP 2: DASHBOARD (Sync) */}
          {step === "IDLE" && (
            <div className="space-y-6 animate-slide-up">
              <div className="flex items-center justify-between bg-cyan-950/20 p-4 rounded border border-cyan-900/30">
                <div className="flex items-center gap-3">
                  <div className="w-10 h-10 bg-cyan-900 rounded-full flex items-center justify-center text-cyan-200 font-bold text-lg">
                    {inputName.charAt(0)}
                  </div>
                  <div>
                    <div className="text-cyan-100 font-bold uppercase text-sm">
                      {inputName}
                    </div>
                    <div className="text-cyan-700 text-[10px] flex items-center gap-1">
                      <Wifi className="w-3 h-3" /> Connected
                    </div>
                  </div>
                </div>
                <div className="text-right">
                  <div className="text-[10px] text-cyan-600 uppercase">
                    Cloud Status
                  </div>
                  <div className="text-cyan-400 font-bold text-xs">Ready</div>
                </div>
              </div>

              <div className="space-y-2">
                <h4 className="text-[10px] font-bold uppercase text-cyan-800 border-b border-cyan-900/50 pb-1">
                  Pending Upload
                </h4>
                <div className="grid grid-cols-2 gap-2 text-xs text-cyan-600 font-mono">
                  <div>
                    XP Level:{" "}
                    <span className="text-cyan-300">
                      {finalPayload?.level || 0}
                    </span>
                  </div>
                  <div>
                    Achievements:{" "}
                    <span className="text-cyan-300">
                      {finalPayload?.achievements.length || 0}
                    </span>
                  </div>
                  <div>
                    Talents:{" "}
                    <span className="text-cyan-300">
                      {finalPayload?.skills.length || 0}
                    </span>
                  </div>
                  <div>
                    Logs:{" "}
                    <span className="text-cyan-300">
                      {finalPayload?.historyCount || 0}
                    </span>
                  </div>
                </div>
              </div>

              <button
                onClick={handleSync}
                className="w-full py-6 bg-gradient-to-r from-cyan-900/50 to-blue-900/50 border border-cyan-500/50 hover:border-cyan-400 text-cyan-100 font-black uppercase tracking-[0.15em] rounded transition-all shadow-[0_0_30px_rgba(6,182,212,0.1)] hover:shadow-[0_0_30px_rgba(6,182,212,0.3)] flex flex-col items-center justify-center gap-1 group"
              >
                <div className="flex items-center gap-2">
                  <RefreshCw className="w-5 h-5 group-hover:rotate-180 transition-transform duration-700" />
                  <span>Sync to Valhalla</span>
                </div>
                <span className="text-[9px] text-cyan-600 font-sans group-hover:text-cyan-400">
                  Overwrites Remote Data
                </span>
              </button>
            </div>
          )}

          {/* STEP 3: TERMINAL LOGS (Syncing/Success) */}
          {(step === "SYNCING" || step === "SUCCESS") && (
            <div className="h-64 bg-black border border-cyan-900/50 p-4 rounded font-mono text-xs overflow-y-auto space-y-2 shadow-inner">
              {logs.map((log, i) => (
                <div key={i} className="flex gap-2">
                  <span className="text-cyan-800">
                    [{new Date().toLocaleTimeString()}]
                  </span>
                  <span className="text-cyan-400">{log}</span>
                </div>
              ))}
              {step === "SYNCING" && (
                <div className="animate-pulse text-cyan-600">
                  _ Process Active...
                </div>
              )}
              {step === "SUCCESS" && (
                <div className="text-green-500 font-bold mt-4 flex items-center gap-2">
                  <CheckCircle2 className="w-4 h-4" />
                  SYNC COMPLETE
                </div>
              )}
            </div>
          )}
        </div>

        {/* Footer Status */}
        <div className="bg-[#050505] p-3 text-center border-t border-cyan-900/30">
          <div className="flex items-center justify-center gap-2 text-[10px] text-cyan-800 uppercase font-bold tracking-widest">
            <Server className="w-3 h-3" />
            <span>Server Realm: EU-North (Simulated)</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ValhallaGate;

