import React from "react";
import { IntervalsWellness, TTBIndices } from "@/types";
import { TrainingPath, WeeklyMastery } from "@/types/training";
import { PATH_INFO, BUILD_VOLUME_TARGETS } from "@/data/builds";
import { Lock, CheckCircle2, Scroll } from "lucide-react";
import { Alert, AlertDescription, AlertTitle } from "@/components/ui/Alert";
import { cn } from "@/lib/utils";
import { TitanXPBar } from "@/features/titan/components/TitanXPBar";

interface CampaignTrackerProps {
  wellness: IntervalsWellness | null;
  ttb: TTBIndices | null;
  level: number;
  activePath?: TrainingPath;
  strengthProgress?: number; // e1RM increase over time
  totalExperience?: number;
  weeklyMastery?: WeeklyMastery;
}

/**
 * Generate dynamic campaign gates based on active training path
 */
function getPhase1Gates(
  ctl: number,
  wellnessScore: number,
  level: number,
  activePath: TrainingPath,
  strengthProgress: number,
) {
  // Dynamic thresholds based on path
  const ctlThreshold =
    {
      JUGGERNAUT: 10, // Lower cardio requirement
      PATHFINDER: 40, // Higher for cardio focus
      WARDEN: 20,
    }[activePath] || 20;

  const strengthGate = activePath === "JUGGERNAUT";

  const gates = [
    {
      label: `Establish Base Resilience (CTL > ${ctlThreshold})`,
      completed: ctl >= ctlThreshold,
      current: `${Math.round(ctl)} / ${ctlThreshold}`,
      pathRelevant: activePath === "PATHFINDER" || activePath === "WARDEN",
    },
    {
      label: "Stabilize Recovery (Wellness > 80)",
      completed: wellnessScore >= 80,
      current: `${wellnessScore} / 80`,
      pathRelevant: true,
    },
    {
      label: "Prove Consistency (Reach Level 5)",
      completed: level >= 5,
      current: `Lvl ${level} / 5`,
      pathRelevant: true,
    },
  ];

  if (strengthGate) {
    gates.unshift({
      label: "Increase 1RM by 5%",
      completed: strengthProgress >= 5,
      current: `+${strengthProgress.toFixed(1)}% / 5%`,
      pathRelevant: true,
    });
  }

  return gates;
}

export const CampaignTracker: React.FC<CampaignTrackerProps> = ({
  wellness,
  ttb,
  level,
  activePath = "WARDEN",
  strengthProgress = 0,
  totalExperience = 0,
  weeklyMastery,
}) => {
  const wellnessScore = ttb?.wellness || 0;
  const ctl = wellness?.ctl || 0;
  const isPhase1Complete = getPhase1Gates(
    ctl,
    wellnessScore,
    level,
    activePath,
    strengthProgress,
  ).every((p) => p.completed);
  const gates = getPhase1Gates(
    ctl,
    wellnessScore,
    level,
    activePath,
    strengthProgress,
  );

  const cardStyle = cn(
    "bg-forge-900 border-2 rounded-lg p-4 h-full transition-all duration-300",
    {
      "border-rarity-legendary shadow-legendary-glow animate-pulse-glow":
        !isPhase1Complete,
      "border-forge-border": isPhase1Complete,
    },
  );

  return (
    <div className={cardStyle}>
      {/* Header */}
      <div className="flex justify-between items-center border-b-2 border-forge-border pb-2 mb-3">
        <div className="flex items-center gap-2">
          <Scroll className="w-5 h-5 text-rarity-legendary" />
          <h2 className="font-bold uppercase tracking-widest text-warrior-light text-sm">
            The Grand Campaign
          </h2>
        </div>
        <span
          className={`border ${isPhase1Complete ? "border-rarity-legendary text-rarity-legendary" : "border-warrior text-warrior"} font-bold text-xs uppercase rounded px-2 py-0.5`}
        >
          {isPhase1Complete ? "Complete" : "Active"}
        </span>
      </div>

      {/* Current Act Details */}
      <div className="mb-4">
        <h3 className="text-lg font-bold text-white mb-1">
          Act I: The Rites of Initiation
        </h3>
        <p className="text-rarity-common text-xs italic max-w-xl">
          &quot;Before a Titan can carry the weight of the world, they must
          first master the weight of their own spirit. Build the
          foundation.&quot;
        </p>
      </div>

      {/* Level & XP Progress */}
      <div className="mb-6 bg-forge-800/50 p-2 rounded-lg border border-forge-border/50">
        <TitanXPBar
          currentXP={(totalExperience || 0) % 100}
          maxXP={100}
          level={level}
        />
      </div>

      {/* Weekly Mastery (System Matrix Metrics) */}
      <div className="mb-6 space-y-3">
        <div className="flex items-center gap-2 mb-2">
          <CheckCircle2 className="w-4 h-4 text-rarity-legendary" />
          <h3 className="font-bold uppercase tracking-wider text-xs text-warrior-light">
            Weekly Mastery
          </h3>
        </div>

        <div className="grid grid-cols-1 gap-3">
          <MasteryProgressBar
            label="Strength Sets"
            current={weeklyMastery?.strengthSets || 0}
            target={(BUILD_VOLUME_TARGETS[activePath] || BUILD_VOLUME_TARGETS.WARDEN).strengthSets}
            color="bg-red-500"
          />
          <MasteryProgressBar
            label="Cardio TSS"
            current={weeklyMastery?.cardioTss || 0}
            target={(BUILD_VOLUME_TARGETS[activePath] || BUILD_VOLUME_TARGETS.WARDEN).cardioTss}
            color="bg-cyan-500"
          />
          <MasteryProgressBar
            label="Mobility Sessions"
            current={weeklyMastery?.mobilitySets || 0}
            target={(BUILD_VOLUME_TARGETS[activePath] || BUILD_VOLUME_TARGETS.WARDEN).mobilitySets}
            color="bg-amber-500"
          />
        </div>
      </div>

      {/* Attunement Gate */}
      <Alert className="bg-forge-800 border-forge-border">
        <Lock className="w-4 h-4 text-warrior" />
        <AlertTitle className="font-bold uppercase text-xs text-warrior">
          Attunement Gate (Requirements to Advance)
        </AlertTitle>
        <AlertDescription>
          <div className="space-y-1.5 mt-2">
            {gates.map((req, i) => (
              <div
                key={i}
                className="flex items-center justify-between text-sm"
              >
                <div className="flex items-center gap-2">
                  {req.completed ? (
                    <CheckCircle2 className="w-4 h-4 text-rarity-legendary" />
                  ) : (
                    <div className="w-4 h-4 rounded-full border-2 border-rarity-common" />
                  )}
                  <span className="text-rarity-common text-xs">
                    {req.label}
                  </span>
                </div>
                <span className="font-mono text-xs text-rarity-common">
                  {req.current}
                </span>
              </div>
            ))}
          </div>
        </AlertDescription>
      </Alert>

      {/* Future Acts */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-2 mt-4">
        <div className="bg-forge-800 border border-forge-border rounded p-3 flex items-center gap-3">
          <Lock className="w-5 h-5 text-rarity-common" />
          <div>
            <h4 className="text-rarity-common font-bold uppercase text-xs">
              Act II
            </h4>
            <p className="font-bold text-sm text-white">The Basalt Bastion</p>
          </div>
        </div>
        <div className="bg-forge-800 border border-forge-border rounded p-3 flex items-center gap-3">
          <Lock className="w-5 h-5 text-rarity-common" />
          <div>
            <h4 className="text-rarity-common font-bold uppercase text-xs">
              Act III
            </h4>
            <p className="font-bold text-sm text-white">The Elite Crucible</p>
          </div>
        </div>
      </div>
    </div>
  );
};

interface MasteryProgressBarProps {
  label: string;
  current: number;
  target: number;
  color: string;
}

const MasteryProgressBar: React.FC<MasteryProgressBarProps> = ({
  label,
  current,
  target,
  color,
}) => {
  const percentage = Math.min(100, (current / target) * 100);

  return (
    <div className="space-y-1">
      <div className="flex justify-between items-center text-[10px] uppercase font-bold tracking-tighter">
        <span className="text-rarity-common">{label}</span>
        <span className="text-white font-mono">
          {current} / {target}
        </span>
      </div>
      <div className="h-1.5 w-full bg-forge-700 rounded-full overflow-hidden border border-forge-border/30">
        <div
          className={cn(
            "h-full transition-all duration-1000 ease-out shadow-[0_0_10px_rgba(0,0,0,0.5)]",
            color,
          )}
          style={{ width: `${percentage}%` }}
        />
      </div>
    </div>
  );
};

/**
 * @fileoverview useSkillEffects Hook
 * The "brain" of the Neural Lattice system.
 * Calculates aggregated effects from all unlocked skills.
 */
import { useMemo } from "react";
import {
  SkillNodeV2,
  CalculatedEffects,
  SkillEffect,
  SkillDrawback,
  EffectCondition,
  DrawbackCondition,
} from "@/types/skills";
import {
  SKILL_TREE_V2,
  getNodeById,
  countUnlockedNotables,
} from "@/data/skill-tree-v2";
import { IntervalsWellness } from "@/types";

// ============================================================================
// Session Metadata (for conditional effect checking)
// ============================================================================

export interface SessionMetadata {
  /** Average reps per set in the session */
  avgReps?: number;
  /** Session duration in minutes */
  durationMins?: number;
  /** Whether the session includes strength training */
  hasStrengthComponent?: boolean;
  /** Whether the session includes cardio */
  hasCardioComponent?: boolean;
  /** Primary training zone (1-5) */
  primaryZone?: 1 | 2 | 3 | 4 | 5;
  /** Number of consecutive training days */
  consecutiveTrainingDays?: number;
  /** Whether any sets were failed/incomplete */
  hasFailedSets?: boolean;
  /** Is this a rest day? */
  isRestDay?: boolean;
  /** Explicitly selected active keystone ID */
  activeKeystoneId?: string | null;
}

// ============================================================================
// Default Calculated Effects
// ============================================================================

const DEFAULT_EFFECTS: CalculatedEffects = {
  tpMultiplier: 1.0,
  ksMultiplier: 1.0,
  titanLoadMultiplier: 1.0,
  recoveryRateMultiplier: 1.0,
  passiveTpPerDay: 0,
  passiveKsPerDay: 0,
  flatTpBonus: 0,
  flatKsBonus: 0,
  flatTitanLoad: 0,
  features: {
    brickWorkouts: false,
    vikingPress: false,
    pumpIndicator: false,
    autoDeload: false,
  },
  activeKeystoneId: null,
};

// ============================================================================
// Condition Checking Helpers
// ============================================================================

/**
 * Check if effect conditions are met for a node.
 */
function checkEffectConditions(
  conditions: EffectCondition | undefined,
  session: SessionMetadata,
  wellness: IntervalsWellness | null,
): boolean {
  if (!conditions) return true; // No conditions = always active

  // Rep range check
  if (
    conditions.minReps !== undefined &&
    (session.avgReps ?? 0) < conditions.minReps
  ) {
    return false;
  }
  if (
    conditions.maxReps !== undefined &&
    (session.avgReps ?? Infinity) > conditions.maxReps
  ) {
    return false;
  }

  // Session duration check
  if (conditions.minSessionDurationMins !== undefined) {
    if ((session.durationMins ?? 0) < conditions.minSessionDurationMins) {
      return false;
    }
  }

  // Hybrid session check
  if (conditions.requiresHybridSession) {
    if (!session.hasStrengthComponent || !session.hasCardioComponent) {
      return false;
    }
  }

  // Zone check
  if (conditions.requiredZone !== undefined) {
    if (session.primaryZone !== conditions.requiredZone) {
      return false;
    }
  }

  // Wellness checks
  if (conditions.minBodyBattery !== undefined) {
    if ((wellness?.bodyBattery ?? 0) < conditions.minBodyBattery) {
      return false;
    }
  }
  if (conditions.minSleepScore !== undefined) {
    if ((wellness?.sleepScore ?? 0) < conditions.minSleepScore) {
      return false;
    }
  }
  if (conditions.minVO2max !== undefined) {
    if ((wellness?.vo2max ?? 0) < conditions.minVO2max) {
      return false;
    }
  }

  return true;
}

/**
 * Check if drawback conditions are triggered for a node.
 */
function checkDrawbackConditions(
  conditions: DrawbackCondition | undefined,
  session: SessionMetadata,
  wellness: IntervalsWellness | null,
): boolean {
  if (!conditions) return false; // No conditions = drawback never applies

  // Low rep drawback (e.g., Hypertrophy Ascendant)
  if (conditions.maxReps !== undefined) {
    if ((session.avgReps ?? Infinity) <= conditions.maxReps) {
      return true;
    }
  }

  // Low sleep drawback (e.g., Sage's Rest)
  if (conditions.maxSleepScore !== undefined) {
    if ((wellness?.sleepScore ?? 100) < conditions.maxSleepScore) {
      return true;
    }
  }

  // Session too short (e.g., Void Runner)
  if (conditions.maxSessionDurationMins !== undefined) {
    if (
      (session.durationMins ?? Infinity) <= conditions.maxSessionDurationMins
    ) {
      return true;
    }
  }

  // Overtraining check
  if (conditions.consecutiveTrainingDays !== undefined) {
    if (
      (session.consecutiveTrainingDays ?? 0) >=
      conditions.consecutiveTrainingDays
    ) {
      return true;
    }
  }

  // Failed set check (Iron Discipline)
  if (conditions.onFailedSet && session.hasFailedSets) {
    return true;
  }

  return false;
}

// ============================================================================
// Main Hook
// ============================================================================

/**
 * Pure function to calculate effects.
 * Separated from hook for easier testing.
 */
export function calculateSkillEffects(
  purchasedSkillIds: Set<string>,
  wellness: IntervalsWellness | null,
  session: SessionMetadata = {},
): CalculatedEffects {
  // Start with defaults
  const result: CalculatedEffects = { ...DEFAULT_EFFECTS };

  // Find active keystone (if any)
  // Find active keystone (if any)
  const keystones = SKILL_TREE_V2.filter(
    (node) => node.tier === "keystone" && purchasedSkillIds.has(node.id),
  );

  if (keystones.length > 0) {
    // If a specific keystone is requested and owned, use it
    if (
      session.activeKeystoneId &&
      purchasedSkillIds.has(session.activeKeystoneId)
    ) {
      result.activeKeystoneId = session.activeKeystoneId;
    } else {
      // Default to first unlocked keystone (legacy behavior)
      result.activeKeystoneId = keystones[0].id;
    }
  }

  // Iterate through all unlocked nodes
  purchasedSkillIds.forEach((nodeId) => {
    const node = getNodeById(nodeId);
    if (!node) return;

    // Check if effect conditions are met
    const effectsApply = checkEffectConditions(
      node.effectConditions,
      session,
      wellness,
    );

    // Apply effects if conditions are met
    if (effectsApply && node.effects) {
      const effects = node.effects;

      // Multiplicative effects (compound)
      if (effects.tpMultiplier !== undefined) {
        result.tpMultiplier *= effects.tpMultiplier;
      }
      if (effects.ksMultiplier !== undefined) {
        result.ksMultiplier *= effects.ksMultiplier;
      }
      if (effects.titanLoadMultiplier !== undefined) {
        result.titanLoadMultiplier *= effects.titanLoadMultiplier;
      }
      if (effects.recoveryRateMultiplier !== undefined) {
        result.recoveryRateMultiplier *= effects.recoveryRateMultiplier;
      }

      // Additive effects
      if (effects.passiveTpPerDay !== undefined) {
        result.passiveTpPerDay += effects.passiveTpPerDay;
      }
      if (effects.passiveKsPerDay !== undefined) {
        result.passiveKsPerDay += effects.passiveKsPerDay;
      }
      if (effects.flatTpBonus !== undefined) {
        result.flatTpBonus += effects.flatTpBonus;
      }
      if (effects.flatKsBonus !== undefined) {
        result.flatKsBonus += effects.flatKsBonus;
      }
      if (effects.flatTitanLoad !== undefined) {
        result.flatTitanLoad += effects.flatTitanLoad;
      }

      // Feature unlocks (OR logic)
      if (effects.unlocksBrickWorkouts) {
        result.features.brickWorkouts = true;
      }
      if (effects.unlocksVikingPress) {
        result.features.vikingPress = true;
      }
      if (effects.unlocksPumpIndicator) {
        result.features.pumpIndicator = true;
      }
      if (effects.unlocksAutoDeload) {
        result.features.autoDeload = true;
      }
    }

    // Check if drawback conditions are met (for keystones)
    const drawbacksApply = checkDrawbackConditions(
      node.drawbackConditions,
      session,
      wellness,
    );

    // Apply drawbacks if conditions are met
    if (drawbacksApply && node.drawbacks) {
      const drawbacks = node.drawbacks;

      // Drawback multipliers override (take the worst)
      if (drawbacks.tpMultiplier !== undefined) {
        result.tpMultiplier = Math.min(
          result.tpMultiplier,
          drawbacks.tpMultiplier,
        );
      }
      if (drawbacks.ksMultiplier !== undefined) {
        result.ksMultiplier = Math.min(
          result.ksMultiplier,
          drawbacks.ksMultiplier,
        );
      }
      if (drawbacks.titanLoadMultiplier !== undefined) {
        result.titanLoadMultiplier = Math.min(
          result.titanLoadMultiplier,
          drawbacks.titanLoadMultiplier,
        );
      }
      if (drawbacks.recoveryRateMultiplier !== undefined) {
        result.recoveryRateMultiplier = Math.min(
          result.recoveryRateMultiplier,
          drawbacks.recoveryRateMultiplier,
        );
      }
    }
  });

  return result;
}

/**
 * Calculate aggregated effects from all unlocked skills.
 *
 * @param purchasedSkillIds - Set of unlocked skill node IDs
 * @param wellness - Current wellness data from Intervals.icu
 * @param session - Current session metadata (optional, for conditional effects)
 * @returns Aggregated effects object
 */
export function useSkillEffects(
  purchasedSkillIds: Set<string>,
  wellness: IntervalsWellness | null,
  session: SessionMetadata = {},
): CalculatedEffects {
  return useMemo(() => {
    return calculateSkillEffects(purchasedSkillIds, wellness, session);
  }, [purchasedSkillIds, wellness, session]);
}

// ============================================================================
// Utility Functions
// ============================================================================

/**
 * Check if a player can purchase a Keystone.
 * Requires 10 notables to be unlocked first.
 */
export function canPurchaseKeystone(
  nodeId: string,
  purchasedSkillIds: Set<string>,
): { canPurchase: boolean; reason?: string } {
  const node = getNodeById(nodeId);
  if (!node) {
    return { canPurchase: false, reason: "Node not found" };
  }

  if (node.tier !== "keystone") {
    return { canPurchase: true }; // Not a keystone, normal rules apply
  }

  const gateRequirement = node.gateRequirement ?? 10;
  const unlockedNotables = countUnlockedNotables(purchasedSkillIds);

  if (unlockedNotables < gateRequirement) {
    return {
      canPurchase: false,
      reason: `Requires ${gateRequirement} Notables (you have ${unlockedNotables})`,
    };
  }

  return { canPurchase: true };
}

/**
 * Get the status of each path (% completion toward Keystone).
 */
export function getPathProgress(
  purchasedSkillIds: Set<string>,
): Record<string, number> {
  const paths = ["juggernaut", "pathfinder", "warden", "titan", "sage"] as const;
  const progress: Record<string, number> = {};

  paths.forEach((path) => {
    const pathNodes = SKILL_TREE_V2.filter(
      (n) => n.path === path && n.tier !== "minor",
    );
    const unlockedCount = pathNodes.filter((n) =>
      purchasedSkillIds.has(n.id),
    ).length;
    progress[path] =
      pathNodes.length > 0 ? (unlockedCount / pathNodes.length) * 100 : 0;
  });

  return progress;
}

"use client";

import React, { useState } from "react";
import { ScrollArea, ScrollBar } from "@/components/ui/scroll-area";
import { Progress } from "@/components/ui/progress";
import { RewardNode } from "./RewardNode";
import { claimBattlePassRewardAction, upgradeToPremiumAction } from "@/actions/systems/battle-pass";
import { Button } from "@/components/ui/button";
import { toast } from "sonner";

interface BattlePassData {
  seasonId: string;
  seasonName: string;
  level: number;
  xp: number;
  hasPremium: boolean;
  tiers: {
    level: number;
    requiredXp: number;
    freeReward: { id: string | null; data: any };
    premiumReward: { id: string | null; data: any };
    isClaimedFree: boolean;
    isClaimedPremium: boolean;
    isUnlocked: boolean;
  }[];
}

interface BattlePassViewProps {
  initialData: BattlePassData | null;
  userId: string;
}

export function BattlePassView({ initialData, userId }: BattlePassViewProps) {
  const [data, setData] = useState<BattlePassData | null>(initialData);
  const [loading, setLoading] = useState(false);

  if (!data) {
    return (
      <div className="p-8 text-center border border-dashed rounded-xl">
        <h3 className="text-lg font-medium text-slate-400">No Active Season</h3>
        <p className="text-sm text-slate-500">
          Check back later for the next Battle Pass season.
        </p>
      </div>
    );
  }

  // Calculate overall progress logic
  // Unlocked tiers / total tiers
  const totalTiers = data.tiers.length;
  // Current tier is 0-indexed concept effectively in currentTier from DB (it returns the last COMPLETED tier usually or current level)
  // Here data.level is usually the current level achieved.

  // Find next tier XP
  const nextTier = data.tiers.find((t) => t.requiredXp > data.xp);
  const currentTier = data.tiers
    .slice()
    .reverse()
    .find((t) => t.requiredXp <= data.xp);

  const prevXp = currentTier?.requiredXp || 0;
  const nextXp = nextTier?.requiredXp || (currentTier?.requiredXp || 0) + 1000; // Fallback

  // Progress within current level
  const tierProgress = Math.min(
    100,
    Math.max(0, ((data.xp - prevXp) / (nextXp - prevXp)) * 100),
  );

  const handleClaim = async (tierLevel: number, isPremium: boolean) => {
    if (loading) return;
    setLoading(true);
    // Optimistic update could happen here, but for now simple await
    const result = await claimBattlePassRewardAction(
      userId,
      tierLevel,
      isPremium,
    );

    if (result.success) {
      toast.success(result.message);
      // In a real app we'd re-fetch or optimistically update local state.
      // For simplicity, we are forcing a refresh via router or just manual state update:
      setData((prev) => {
        if (!prev) return null;
        return {
          ...prev,
          tiers: prev.tiers.map((t) => {
            if (t.level === tierLevel) {
              return {
                ...t,
                isClaimedFree: !isPremium ? true : t.isClaimedFree,
                isClaimedPremium: isPremium ? true : t.isClaimedPremium,
              };
            }
            return t;
          }),
        };
      });
    } else {
      toast.error(result.message);
    }
    setLoading(false);
  };

  const handleUpgrade = async () => {
    if (loading) return;
    setLoading(true);
    const result = await upgradeToPremiumAction(userId);
    if (result.success) {
      toast.success("Premium Unlocked!");
      // Optimistic update
      setData(prev => prev ? ({ ...prev, hasPremium: true }) : null);
    } else {
      toast.error(result.message);
    }
    setLoading(false);
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex flex-col gap-4 bg-slate-900/50 p-6 rounded-xl border border-slate-800 relative overflow-hidden">
        {/* Background Glow for Premium */}
        {data.hasPremium && (
          <div className="absolute top-0 right-0 w-64 h-64 bg-amber-500/10 blur-[100px] rounded-full pointer-events-none" />
        )}

        <div className="flex justify-between items-start">
          <div>
            <h2 className="text-2xl font-bold bg-gradient-to-r from-amber-200 to-yellow-500 bg-clip-text text-transparent flex items-center gap-2">
              {data.seasonName}
              {data.hasPremium && <span className="text-xs border border-amber-500/50 text-amber-500 px-2 py-0.5 rounded-full uppercase tracking-widest">Premium</span>}
            </h2>
            <p className="text-slate-400 text-sm">Season ends in 30 days</p>
          </div>
          <div className="flex items-center gap-4">
            {!data.hasPremium && (
              <Button
                onClick={handleUpgrade}
                disabled={loading}
                className="bg-gradient-to-r from-amber-600 to-yellow-600 hover:from-amber-500 hover:to-yellow-500 text-white font-bold shadow-lg shadow-amber-900/20"
              >
                Unlock Premium
              </Button>
            )}
            <div className="text-right">
              <div className="text-3xl font-bold font-mono">{data.level}</div>
              <div className="text-xs text-slate-500 uppercase tracking-wider">
                Current Tier
              </div>
            </div>
          </div>
        </div>

        {/* Main Progress Bar */}
        <div className="space-y-2">
          <div className="flex justify-between text-xs text-slate-400">
            <span>{data.xp} XP</span>
            <span>Next: {nextXp} XP</span>
          </div>
          <Progress value={tierProgress} className="h-3" />
        </div>
      </div>

      {/* Tiers Track */}
      <div className="space-y-2">
        <div className="flex items-center justify-between px-2">
          <h3 className="font-semibold text-slate-300">Reward Track</h3>
          {!data.hasPremium && (
            <span className="text-xs text-amber-500 font-medium">
              Upgrade to Premium to unlock more rewards!
            </span>
          )}
        </div>

        <ScrollArea className="w-full whitespace-nowrap rounded-xl border border-slate-800 bg-slate-950/30 p-4">
          <div className="flex space-x-4 pb-4">
            {data.tiers.map((tier) => (
              <div key={tier.level} className="flex flex-col gap-2">
                {/* Free Reward */}
                <RewardNode
                  tier={tier.level}
                  pointsRequired={tier.requiredXp}
                  isUnlocked={tier.isUnlocked}
                  isClaimed={tier.isClaimedFree}
                  isPremium={false}
                  reward={tier.freeReward}
                  onClaim={() => handleClaim(tier.level, false)}
                  userHasPremium={data.hasPremium}
                />

                {/* Connector / Spacer */}
                <div className="h-8 flex items-center justify-center">
                  <div
                    className={`w-0.5 h-full ${tier.isUnlocked ? "bg-amber-500" : "bg-slate-800"}`}
                  ></div>
                </div>

                {/* Premium Reward */}
                <RewardNode
                  tier={tier.level}
                  pointsRequired={tier.requiredXp}
                  isUnlocked={tier.isUnlocked}
                  isClaimed={tier.isClaimedPremium}
                  isPremium={true}
                  reward={tier.premiumReward}
                  onClaim={() => handleClaim(tier.level, true)}
                  userHasPremium={data.hasPremium}
                />
              </div>
            ))}
          </div>
          <ScrollBar orientation="horizontal" />
        </ScrollArea>
      </div>
    </div>
  );
}

