import { motion, AnimatePresence } from "framer-motion";

export const HevyImportWizard = () => {
  const [file, setFile] = useState<File | null>(null);
  const [stats, setStats] = useState<{
    count: number;
    dateRange: string;
  } | null>(null);
  const [parsedData, setParsedData] = useState<any[] | null>(null);
  const [isUploading, setIsUploading] = useState(false);
  const [complete, setComplete] = useState(false);

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const selected = acceptedFiles[0];
    if (selected?.type !== "application/json") {
      toast.error("Please upload a valid JSON file");
      return;
    }

    const reader = new FileReader();
    reader.onload = () => {
      try {
        const json = JSON.parse(reader.result as string);
        const workouts = json.workouts || [];

        if (!Array.isArray(workouts)) {
          throw new Error('Invalid format: missing "workouts" array');
        }

        setFile(selected);
        setParsedData(workouts);

        // Calculate stats
        const count = workouts.length;
        const dates = workouts.map((w: any) =>
          new Date(w.start_time).getTime(),
        );
        const minDate = new Date(Math.min(...dates)).toLocaleDateString();
        const maxDate = new Date(Math.max(...dates)).toLocaleDateString();
        setStats({ count, dateRange: `${minDate} - ${maxDate}` });
      } catch (e) {
        toast.error("Failed to parse JSON file");
        console.error(e);
      }
    };
    reader.readAsText(selected);
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    maxFiles: 1,
    accept: { "application/json": [".json"] },
  });

  const handleImport = async () => {
    if (!parsedData) return;
    setIsUploading(true);

    try {
      // Chunking if necessary? For now send all
      // If huge, we might need to chunk. Let's send in batches of 50.
      const BATCH_SIZE = 50;
      const batches = [];
      for (let i = 0; i < parsedData.length; i += BATCH_SIZE) {
        batches.push(parsedData.slice(i, i + BATCH_SIZE));
      }

      let totalImported = 0;

      for (const batch of batches) {
        await importHevyHistoryAction(batch);
        totalImported += batch.length;
      }

      setComplete(true);
      toast.success(`Successfully imported ${totalImported} workouts!`);
    } catch (e: any) {
      toast.error("Import failed: " + e.message);
    } finally {
      setIsUploading(false);
    }
  };

  if (complete) {
    return (
      <div className="text-center p-8 bg-zinc-900 border border-emerald-500/20 rounded-xl">
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          className="w-16 h-16 bg-emerald-500/10 rounded-full flex items-center justify-center mx-auto mb-4"
        >
          <CheckCircle className="w-8 h-8 text-emerald-500" />
        </motion.div>
        <h3 className="text-xl font-bold text-white mb-2">Import Complete!</h3>
        <p className="text-zinc-400 mb-6">
          Your legendary history has been inscribed in the archives.
        </p>
        <button
          onClick={() => {
            setFile(null);
            setStats(null);
            setComplete(false);
          }}
          className="text-sm text-emerald-400 hover:text-emerald-300 underline"
        >
          Import another file
        </button>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {!file ? (
        <div
          {...getRootProps()}
          className={`border-2 border-dashed rounded-xl p-8 text-center cursor-pointer transition-colors ${isDragActive
              ? "border-magma bg-magma/5"
              : "border-zinc-700 hover:border-zinc-500 bg-zinc-900/50"
            }`}
        >
          <input {...getInputProps()} />
          <Upload className="w-10 h-10 text-zinc-500 mx-auto mb-4" />
          <p className="text-lg font-medium text-white mb-1">
            {isDragActive
              ? "Drop your legacy here..."
              : "Drag & drop Hevy export (JSON)"}
          </p>
          <p className="text-sm text-zinc-500">or click to browse files</p>
        </div>
      ) : (
        <div className="bg-zinc-900 border border-zinc-800 rounded-xl p-6">
          <div className="flex items-center gap-4 mb-6">
            <div className="p-3 bg-zinc-800 rounded-lg">
              <FileJson className="w-6 h-6 text-magma" />
            </div>
            <div>
              <h4 className="text-white font-medium">{file.name}</h4>
              <p className="text-xs text-zinc-500">
                {(file.size / 1024).toFixed(1)} KB
              </p>
            </div>
            <button
              onClick={() => {
                setFile(null);
                setStats(null);
              }}
              className="ml-auto text-xs text-red-400 hover:text-red-300"
            >
              Remove
            </button>
          </div>

          {stats && (
            <div className="grid grid-cols-2 gap-4 mb-6">
              <div className="p-4 bg-black/20 rounded-lg">
                <div className="text-sm text-zinc-500 mb-1">Workouts Found</div>
                <div className="text-2xl font-bold text-white">
                  {stats.count}
                </div>
              </div>
              <div className="p-4 bg-black/20 rounded-lg">
                <div className="text-sm text-zinc-500 mb-1">Timeline</div>
                <div className="text-lg font-medium text-white">
                  {stats.dateRange}
                </div>
              </div>
            </div>
          )}

          <div className="bg-yellow-500/10 border border-yellow-500/20 p-4 rounded-lg flex items-start gap-3 mb-6">
            <AlertCircle className="w-5 h-5 text-yellow-500 shrink-0 mt-0.5" />
            <div className="text-sm text-yellow-200/80">
              Missing exercises will be automatically created. Duplicates will
              be skipped based on date and exercise.
            </div>
          </div>

          <button
            onClick={handleImport}
            disabled={isUploading}
            className="w-full py-3 bg-white text-black font-bold rounded-lg hover:bg-zinc-200 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
          >
            {isUploading ? (
              <>
                <Loader2 className="w-4 h-4 animate-spin" />
                Analyzing Scrolls...
              </>
            ) : (
              "Confirm Import"
            )}
          </button>
        </div>
      )}
    </div>
  );
};

import React from "react";
import { OracleRecommendation } from "@/types";
import {
  AlertTriangle,
  TrendingUp,
  Sword,
  HeartPulse,
  ArrowRight,
  Skull,
  Sparkles,
} from "lucide-react";

interface OracleCardProps {
  recommendation: OracleRecommendation;
  onAccept: (rec: OracleRecommendation) => void;
}

const OracleCard: React.FC<OracleCardProps> = ({
  recommendation,
  onAccept,
}) => {
  // Theme logic based on type
  let theme = {
    border: "border-zinc-700",
    bg: "bg-zinc-900",
    icon: <Sword className="w-6 h-6" />,
    accent: "text-zinc-400",
    button: "bg-zinc-800 text-zinc-300",
    glow: "",
  };

  switch (recommendation.type) {
    case "RECOVERY":
      theme = {
        border: "border-blue-500",
        bg: "bg-blue-950/30",
        icon: <HeartPulse className="w-6 h-6 text-blue-400" />,
        accent: "text-blue-400",
        button:
          "bg-blue-900/50 text-blue-200 border border-blue-500/50 hover:bg-blue-800",
        glow: "shadow-[0_0_20px_rgba(59,130,246,0.2)]",
      };
      break;
    case "PR_ATTEMPT":
      theme = {
        border: "border-[#ffd700]",
        bg: "bg-gradient-to-br from-yellow-950/40 to-black",
        icon: <Skull className="w-6 h-6 text-[#ffd700]" />,
        accent: "text-[#ffd700]",
        button: "bg-[#ffd700] text-black font-bold hover:bg-yellow-400",
        glow: "shadow-[0_0_20px_rgba(255,215,0,0.25)] animate-pulse-slow",
      };
      break;
    case "CARDIO_VALIDATION":
      theme = {
        border: "border-cyan-500",
        bg: "bg-cyan-950/30",
        icon: <Sparkles className="w-6 h-6 text-cyan-400" />,
        accent: "text-cyan-400",
        button:
          "bg-cyan-900/50 text-cyan-200 border border-cyan-500/50 hover:bg-cyan-800",
        glow: "shadow-[0_0_20px_rgba(34,211,238,0.2)]",
      };
      break;
    case "GRIND":
      theme = {
        border: "border-[#c79c6e]",
        bg: "bg-zinc-900",
        icon: <TrendingUp className="w-6 h-6 text-[#c79c6e]" />,
        accent: "text-[#c79c6e]",
        button: "bg-[#c79c6e] text-[#46321d] font-bold hover:bg-[#d4a87a]",
        glow: "",
      };
      break;
  }

  return (
    <div
      className={`relative p-6 rounded-lg border-2 ${theme.border} ${theme.bg} ${theme.glow} transition-all duration-500 flex flex-col md:flex-row items-start md:items-center gap-6 overflow-hidden`}
    >
      {/* Background Texture */}
      <div className="absolute inset-0 opacity-10 bg-[url('https://www.transparenttextures.com/patterns/carbon-fibre.png')] pointer-events-none"></div>

      {/* Icon Area */}
      <div
        className={`p-4 rounded-full border-2 ${theme.border} bg-black/50 z-10 shrink-0`}
      >
        {theme.icon}
      </div>

      {/* Text Content */}
      <div className="flex-1 z-10 space-y-2">
        <div className="flex items-center gap-2">
          <span
            className={`text-[10px] uppercase font-bold tracking-[0.2em] ${theme.accent}`}
          >
            The Oracle Commands
          </span>
          {recommendation.generatedSession && (
            <span className="px-2 py-0.5 bg-purple-500 text-white border border-purple-400 text-[9px] font-black uppercase rounded animate-pulse shadow-[0_0_10px_#a855f7]">
              New Quest Generated
            </span>
          )}
        </div>
        <h2 className="text-2xl font-black italic uppercase text-white tracking-tighter leading-none">
          {recommendation.title}
        </h2>
        <p className="text-zinc-400 text-xs font-serif italic border-l-2 border-zinc-700 pl-3">
          &quot;{recommendation.rationale}&quot;
        </p>
      </div>

      {/* Action Button */}
      {(recommendation.sessionId || recommendation.generatedSession) && (
        <button
          onClick={() => onAccept(recommendation)}
          className={`z-10 px-6 py-4 rounded uppercase tracking-widest text-xs flex items-center gap-3 transition-transform active:scale-95 shadow-lg whitespace-nowrap ${theme.button}`}
        >
          <span>
            {recommendation.generatedSession
              ? "Accept Special Quest"
              : "Initiate Protocol"}
          </span>
          <ArrowRight className="w-4 h-4" />
        </button>
      )}
    </div>
  );
};

export default OracleCard;

"use client";

import React, { useState, useEffect, useRef } from "react";
import { useChat } from "@ai-sdk/react";
import { motion, AnimatePresence } from "framer-motion";
import {
  Send,
  Sparkles,
  User,
  Brain,
  X,
  MessageSquare,
  Terminal,
} from "lucide-react";
import { ProgramGenerator } from "@/features/training/components/ProgramGenerator";

interface OracleChatProps {
  context?: Record<string, unknown> & { userId?: string };
}

export const OracleChat: React.FC<OracleChatProps> = ({ context }) => {
  const [isOpen, setIsOpen] = useState(false);
  const [showProgramGenerator, setShowProgramGenerator] = useState(false);
  const scrollRef = useRef<HTMLDivElement>(null);

  const { messages, sendMessage, status } =
    useChat({
      // @ts-ignore - api is valid at runtime but not in UseChatOptions type definition
      api: "/api/chat",
      body: {
        context,
        userId: context?.userId,
      },
      initialMessages: [
        {
          id: "welcome",
          role: "assistant",
          content:
            "Identity confirmed. I am the Oracle. Speak, Titan, and I shall guide your evolution. What metrics weigh upon your spirit today?",
        },
      ],
    });

  const [input, setInput] = useState("");
  const isLoading = status === "submitted" || status === "streaming";

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setInput(e.target.value);
  };

  const handleSubmit = async (e?: React.FormEvent) => {
    e?.preventDefault();
    if (!input.trim()) return;
    await sendMessage({ role: "user", content: input } as any);
    setInput("");
  };

  // Auto-scroll to bottom
  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages]);

  return (
    <>
      {/* Floating Action Button */}
      <motion.button
        data-testid="oracle-chat-trigger"
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        onClick={() => setIsOpen(true)}
        className="fixed bottom-6 right-6 p-4 rounded-full bg-gradient-to-br from-indigo-600 to-purple-700 text-white shadow-2xl z-50 flex items-center gap-2 group border border-white/20"
      >
        <MessageSquare className="w-6 h-6" />
        <span className="max-w-0 overflow-hidden group-hover:max-w-xs transition-all duration-300 ease-in-out whitespace-nowrap font-medium">
          Whisper Mode
        </span>
      </motion.button>

      {/* Chat Window */}
      <AnimatePresence>
        {isOpen && (
          <motion.div
            data-testid="oracle-chat-panel"
            initial={{ opacity: 0, y: 100, scale: 0.9 }}
            animate={{ opacity: 1, y: 0, scale: 1 }}
            exit={{ opacity: 0, y: 100, scale: 0.9 }}
            className="fixed bottom-24 right-6 w-96 h-[600px] bg-slate-900/90 backdrop-blur-xl border border-white/10 rounded-2xl shadow-2xl z-50 flex flex-col overflow-hidden"
          >
            {/* Header */}
            <div className="p-4 border-b border-white/10 bg-white/5 flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="p-2 rounded-lg bg-indigo-500/20 text-indigo-400">
                  <Brain className="w-5 h-5" />
                </div>
                <div>
                  <h3 className="font-bold text-white text-sm">
                    THE IRON ORACLE
                  </h3>
                  <div className="flex items-center gap-1.5">
                    <div className="w-2 h-2 rounded-full bg-green-500 animate-pulse" />
                    <span className="text-[10px] text-zinc-400 uppercase tracking-widest font-bold">
                      Whisper Mode Active
                    </span>
                  </div>
                </div>
              </div>
              <button
                onClick={() => setIsOpen(false)}
                className="p-1 hover:bg-white/10 rounded-full text-zinc-400 transition-colors"
              >
                <X className="w-5 h-5" />
              </button>
            </div>

            {/* Messages Area */}
            <div
              ref={scrollRef}
              className="flex-1 overflow-y-auto p-4 space-y-4 scrollbar-hide"
            >
              {messages.map((m: any) => (
                <div
                  key={m.id}
                  className={`flex ${m.role === "user" ? "justify-end" : "justify-start"}`}
                >
                  <div
                    className={`flex gap-3 max-w-[85%] ${m.role === "user" ? "flex-row-reverse" : ""}`}
                  >
                    <div
                      className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${m.role === "user"
                        ? "bg-indigo-600"
                        : "bg-zinc-800 border border-white/10"
                        }`}
                    >
                      {m.role === "user" ? (
                        <User className="w-4 h-4 text-white" />
                      ) : (
                        <Sparkles className="w-4 h-4 text-indigo-400" />
                      )}
                    </div>
                    <div
                      data-testid="oracle-message"
                      className={`p-3 rounded-2xl text-sm leading-relaxed ${m.role === "user"
                        ? "bg-indigo-600 text-white rounded-tr-none"
                        : "bg-white/5 text-zinc-100 border border-white/5 rounded-tl-none font-medium"
                        }`}
                    >
                      {m.content}
                    </div>
                  </div>
                </div>
              ))}
              {isLoading && (
                <div className="flex justify-start">
                  <div className="flex gap-3 max-w-[85%]">
                    <div className="w-8 h-8 rounded-full bg-zinc-800 border border-white/10 flex items-center justify-center">
                      <Sparkles className="w-4 h-4 text-indigo-400 animate-spin" />
                    </div>
                    <div className="p-3 rounded-2xl bg-white/5 text-zinc-400 italic text-xs animate-pulse">
                      Consulting the Metric Planes...
                    </div>
                  </div>
                </div>
              )}
            </div>

            {/* Input Area */}
            <form
              onSubmit={handleSubmit}
              className="p-4 border-t border-white/10 bg-black/20"
            >
              <div className="relative group">
                <input
                  value={input}
                  onChange={(e) => {
                    const val = e.target.value;
                    if (val === "/program") {
                      setShowProgramGenerator(true);
                      handleInputChange({
                        ...e,
                        target: { ...e.target, value: "" },
                      });
                      setIsOpen(false);
                      return;
                    }
                    handleInputChange(e);
                  }}
                  placeholder="Ask the Oracle... (/program for generator)"
                  className="w-full bg-white/5 border border-white/10 rounded-xl py-3 pl-4 pr-12 text-sm text-white focus:outline-none focus:border-indigo-500 transition-all placeholder:text-zinc-600"
                />
                <button
                  type="submit"
                  disabled={!input || isLoading}
                  className="absolute right-2 top-2 p-1.5 rounded-lg bg-indigo-600 text-white disabled:opacity-50 disabled:bg-zinc-700 transition-all hover:bg-indigo-500"
                >
                  <Send className="w-4 h-4" />
                </button>
              </div>
            </form>
          </motion.div>
        )}
      </AnimatePresence>

      <ProgramGenerator
        isOpen={showProgramGenerator}
        onClose={() => setShowProgramGenerator(false)}
      />
    </>
  );
};

"use client";

import React from "react";
import { OracleDecree, OracleDecreeType } from "@/types/oracle";
import { Flame, Moon, Zap, AlertTriangle, Sparkles } from "lucide-react";
import { cn } from "@/lib/utils";
import { motion, AnimatePresence } from "framer-motion";

interface OracleVerdictProps {
  decree: OracleDecree | null;
  isLoading?: boolean;
  className?: string;
}

/**
 * Clear daily verdict UI for the Oracle recommendation.
 * Shows TRAIN / REST / LIGHT recommendation prominently.
 */
export const OracleVerdict: React.FC<OracleVerdictProps> = ({
  decree,
  isLoading = false,
  className,
}) => {
  // Derive simple verdict from decree type & label
  const getVerdict = (
    decree: OracleDecree,
  ): { label: string; color: string; icon: React.ReactNode; bg: string } => {
    // 1. DEBUFF -> REST
    if (decree.type === "DEBUFF") {
      return {
        label: "REST",
        color: "text-red-400",
        bg: "from-red-500/20 to-rose-500/10 border-red-500/30",
        icon: <Moon className="w-8 h-8 text-red-400" />,
      };
    }

    // 2. BUFF -> TRAIN (PEAK)
    if (decree.type === "BUFF") {
      return {
        label: "TRAIN HARD",
        color: "text-green-400",
        bg: "from-green-500/20 to-emerald-500/10 border-green-500/30",
        icon: <Flame className="w-8 h-8 text-green-400" />,
      };
    }

    // 3. NEUTRAL -> LIGHT vs TRAIN
    if (decree.label === "Decree of Patience") {
      return {
        label: "LIGHT",
        color: "text-cyan-400",
        bg: "from-cyan-500/20 to-blue-500/10 border-cyan-500/30",
        icon: <Sparkles className="w-8 h-8 text-cyan-400" />,
      };
    }

    // Default NEUTRAL (Discipline) -> TRAIN (STEADY)
    return {
      label: "TRAIN",
      color: "text-amber-400",
      bg: "from-amber-500/20 to-yellow-500/10 border-amber-500/30",
      icon: <Zap className="w-8 h-8 text-amber-400" />,
    };
  };

  if (isLoading) {
    return (
      <div
        className={cn(
          "relative rounded-xl border border-white/10 p-6 bg-gradient-to-br from-zinc-800/50 to-zinc-900/50",
          className,
        )}
      >
        <div className="flex items-center gap-4">
          <div className="w-12 h-12 rounded-full bg-zinc-700 animate-pulse" />
          <div className="flex-1 space-y-2">
            <div className="h-6 w-24 bg-zinc-700 rounded animate-pulse" />
            <div className="h-4 w-48 bg-zinc-700 rounded animate-pulse" />
          </div>
        </div>
      </div>
    );
  }

  if (!decree) {
    return (
      <div
        className={cn(
          "relative rounded-xl border border-zinc-700/50 p-6 bg-zinc-800/30",
          className,
        )}
      >
        <div className="flex items-center gap-4 text-zinc-500">
          <AlertTriangle className="w-8 h-8" />
          <div>
            <p className="font-bold">Oracle Unavailable</p>
            <p className="text-sm">
              Connect wellness data to receive guidance.
            </p>
          </div>
        </div>
      </div>
    );
  }

  const verdict = getVerdict(decree);

  return (
    <AnimatePresence mode="wait">
      <motion.div
        key={decree.label}
        initial={{ opacity: 0, y: 10 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -10 }}
        className={cn(
          "relative overflow-hidden rounded-xl border p-6 bg-gradient-to-br",
          verdict.bg,
          className,
        )}
      >
        {/* Glow effect */}
        <div className="absolute -right-10 -top-10 w-32 h-32 bg-white/5 blur-3xl rounded-full" />

        <div className="relative z-10 flex items-center gap-4">
          {/* Icon */}
          <div className="flex-shrink-0 w-14 h-14 rounded-full bg-black/20 flex items-center justify-center">
            {verdict.icon}
          </div>

          {/* Content */}
          <div className="flex-1 min-w-0">
            <div className="flex items-center gap-2 mb-1">
              <Sparkles className="w-4 h-4 text-zinc-400" />
              <span className="text-xs font-mono uppercase tracking-wider text-zinc-400">
                Oracle Decree
              </span>
            </div>

            <h3
              className={cn(
                "text-2xl font-black uppercase tracking-wide",
                verdict.color,
              )}
            >
              {verdict.label}
            </h3>

            <p
              className="text-sm text-zinc-300 mt-1 truncate"
              title={decree.description}
            >
              {decree.description}
            </p>
          </div>

          {/* XP Multiplier Badge */}
          {decree.effect?.xpMultiplier &&
            decree.effect.xpMultiplier !== 1.0 && (
              <div className="flex-shrink-0 text-center">
                <div
                  className={cn(
                    "text-2xl font-black",
                    decree.effect.xpMultiplier > 1
                      ? "text-green-400"
                      : "text-red-400",
                  )}
                >
                  {decree.effect.xpMultiplier > 1 ? "+" : ""}
                  {Math.round((decree.effect.xpMultiplier - 1) * 100)}%
                </div>
                <div className="text-xs text-zinc-500 uppercase">XP</div>
              </div>
            )}
        </div>

        {/* Decree Label */}
        <div className="mt-4 pt-3 border-t border-white/5">
          <p className="text-xs font-mono text-zinc-500 italic">
            {`"${decree.label}"`}
          </p>
        </div>
      </motion.div>
    </AnimatePresence>
  );
};

export default OracleVerdict;

"use client";

import { useState, useEffect } from "react";
import { PocketCastsPodcast, PocketCastsEpisode } from "@/services/pocketcasts";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Loader2, Play, ArrowLeft, Podcast } from "lucide-react";
import Image from "next/image";

interface PodcastBrowserProps {
    onPlayEpisode: (episode: PocketCastsEpisode) => void;
}

export function PodcastBrowser({ onPlayEpisode }: PodcastBrowserProps) {
    const [view, setView] = useState<"subscriptions" | "episodes">("subscriptions");
    const [subscriptions, setSubscriptions] = useState<PocketCastsPodcast[]>([]);
    const [selectedPodcast, setSelectedPodcast] = useState<PocketCastsPodcast | null>(null);
    const [episodes, setEpisodes] = useState<PocketCastsEpisode[]>([]);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        fetchSubscriptions();
    }, []);

    const fetchSubscriptions = async () => {
        setIsLoading(true);
        try {
            const res = await fetch("/api/podcast?type=subscriptions");
            const data = await res.json();
            if (data.error) throw new Error(data.error);
            setSubscriptions(data);
        } catch (error) {
            console.error("Failed to fetch subscriptions", error);
        } finally {
            setIsLoading(false);
        }
    };

    const fetchEpisodes = async (podcast: PocketCastsPodcast) => {
        setIsLoading(true);
        setSelectedPodcast(podcast);
        setView("episodes");
        try {
            const res = await fetch(`/api/podcast?type=episodes&uuid=${podcast.uuid}`);
            const data = await res.json();
            if (data.error) throw new Error(data.error);
            setEpisodes(data);
        } catch (error) {
            console.error("Failed to fetch episodes", error);
        } finally {
            setIsLoading(false);
        }
    };

    const handleBack = () => {
        setView("subscriptions");
        setSelectedPodcast(null);
        setEpisodes([]);
    };

    if (isLoading && subscriptions.length === 0) {
        return <div className="flex justify-center p-8"><Loader2 className="animate-spin" /></div>;
    }

    return (
        <div className="h-[400px] flex flex-col">
            {view === "subscriptions" ? (
                <ScrollArea className="flex-1">
                    <div className="grid grid-cols-3 gap-4 p-4">
                        {subscriptions.map(sub => (
                            <button
                                key={sub.uuid}
                                onClick={() => fetchEpisodes(sub)}
                                className="group relative aspect-square rounded-lg overflow-hidden hover:ring-2 hover:ring-amber-500 transition-all"
                            >
                                <Image
                                    src={sub.thumbnail_url || "/images/podcast-placeholder.png"}
                                    alt={sub.title}
                                    fill
                                    className="object-cover transition-transform group-hover:scale-105"
                                    unoptimized
                                />
                                <div className="absolute inset-x-0 bottom-0 bg-black/60 p-2 text-xs text-white truncate">
                                    {sub.title}
                                </div>
                            </button>
                        ))}
                    </div>
                </ScrollArea>
            ) : (
                <div className="flex flex-col h-full">
                    <div className="flex items-center gap-2 p-4 border-b border-zinc-800">
                        <Button variant="ghost" size="sm" onClick={handleBack}>
                            <ArrowLeft className="w-4 h-4 mr-1" /> Back
                        </Button>
                        <h3 className="font-bold truncate text-sm">{selectedPodcast?.title}</h3>
                    </div>
                    <ScrollArea className="flex-1 p-2">
                        <div className="space-y-2">
                            {episodes.map(ep => (
                                <div key={ep.uuid} className="flex items-center gap-3 p-2 rounded hover:bg-zinc-800/50 group">
                                    <Button
                                        size="icon"
                                        variant="secondary"
                                        className="h-8 w-8 shrink-0 opacity-0 group-hover:opacity-100 transition-opacity"
                                        onClick={() => onPlayEpisode({ ...ep, podcast_title: selectedPodcast?.title })}
                                    >
                                        <Play className="w-4 h-4" />
                                    </Button>
                                    <div className="min-w-0">
                                        <h4 className="text-sm font-medium leading-tight">{ep.title}</h4>
                                        <div className="flex gap-2 text-[10px] text-zinc-500 mt-1">
                                            <span>{Math.floor(ep.duration / 60)} min</span>
                                            <span>•</span>
                                            <span>{new Date(ep.published_at || "").toLocaleDateString()}</span>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </ScrollArea>
                </div>
            )}
        </div>
    );
}

"use client";

import { Card, CardContent } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Swords, Milestone, Timer, Send } from "lucide-react";
import { sendTauntAction } from "@/actions/pvp/duel";
import { toast } from "sonner";
import { useState } from "react";

interface CardioDuelProps {
    duel: {
        id: string;
        duelType: string;
        targetDistance?: number;
        challenger: { heroName: string; level: number; id: string };
        defender: { heroName: string; level: number; id: string };
        challengerDistance: number;
        defenderDistance: number;
    };
    currentUserId: string;
}

export function CardioDuelCard({ duel, currentUserId }: CardioDuelProps) {
    const [isTaunting, setIsTaunting] = useState(false);
    const isChallenger = currentUserId === duel.challenger.id;

    const myDistance = isChallenger ? duel.challengerDistance : duel.defenderDistance;
    const oppDistance = isChallenger ? duel.defenderDistance : duel.challengerDistance;
    const opponentName = isChallenger ? duel.defender.heroName : duel.challenger.heroName;

    const target = duel.targetDistance || 10; // Default 10km if missing
    const myProgress = Math.min(100, (myDistance / target) * 100);
    const oppProgress = Math.min(100, (oppDistance / target) * 100);

    const handleTaunt = async () => {
        setIsTaunting(true);
        await sendTauntAction(duel.id);
        toast.success(`Taunted ${opponentName}!`);
        setTimeout(() => setIsTaunting(false), 5000); // Cooldown
    };

    return (
        <Card className="w-full mb-4 border-2 border-slate-700 bg-slate-900/50">
            <CardContent className="p-4">
                {/* Header */}
                <div className="flex justify-between items-center mb-4">
                    <div className="flex items-center gap-2">
                        <Swords className="h-5 w-5 text-red-400" />
                        <span className="font-bold text-slate-200">
                            {duel.duelType === "DISTANCE_RACE" ? "Distance Race" : "Cardio Duel"}
                        </span>
                        <Badge variant="outline" className="ml-2 text-xs">
                            Target: {target}km
                        </Badge>
                    </div>
                    <Button
                        size="sm"
                        variant="ghost"
                        disabled={isTaunting}
                        onClick={handleTaunt}
                        className="text-xs text-slate-400 hover:text-red-400"
                    >
                        <Send className="h-3 w-3 mr-1" />
                        Taunt
                    </Button>
                </div>

                {/* My Progress */}
                <div className="mb-4">
                    <div className="flex justify-between text-sm mb-1">
                        <span className="font-semibold text-emerald-400">You</span>
                        <span className="text-slate-400">
                            {myDistance.toFixed(1)} / {target} km
                        </span>
                    </div>
                    <Progress value={myProgress} className="h-3 bg-slate-800" indicatorClassName="bg-emerald-500" />
                </div>

                {/* Opponent Progress */}
                <div>
                    <div className="flex justify-between text-sm mb-1">
                        <span className="font-semibold text-red-400">{opponentName}</span>
                        <span className="text-slate-400">
                            {oppDistance.toFixed(1)} / {target} km
                        </span>
                    </div>
                    <Progress value={oppProgress} className="h-3 bg-slate-800" indicatorClassName="bg-red-500" />
                </div>
            </CardContent>
        </Card>
    );
}

"use client";

import { useState, useEffect } from "react";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { createDuelChallengeAction, getPotentialOpponentsAction } from "@/actions/pvp/duel";
import { toast } from "sonner";
import { Bike, Footprints, Trophy } from "lucide-react";

interface Opponent {
    id: string;
    heroName: string;
    level: number;
    titan?: { powerRating: number };
}

export function CardioDuelLobby() {
    const [opponents, setOpponents] = useState<Opponent[]>([]);
    const [selectedOpponent, setSelectedOpponent] = useState<string | null>(null);
    const [duelType, setDuelType] = useState("DISTANCE_RACE");
    const [activityType, setActivityType] = useState("RUNNING");
    const [distance, setDistance] = useState("5");
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        loadOpponents();
    }, []);

    const loadOpponents = async () => {
        const res = await getPotentialOpponentsAction();
        if (res.success && res.opponents) {
            // Fix: Map to ensure heroName is string (fallback for null)
            const sanitized: Opponent[] = res.opponents.map((opp: any) => ({
                id: opp.id,
                heroName: opp.heroName || "Unknown Titan",
                level: opp.level, // Assuming level is always present/valid based on schema, otherwise provide default
                titan: opp.titan ? { powerRating: opp.titan.powerRating } : undefined
            }));
            setOpponents(sanitized);
        }
    };

    const handleCreateDuel = async () => {
        if (!selectedOpponent) return;
        setLoading(true);

        const result = await createDuelChallengeAction(selectedOpponent, {
            duelType,
            activityType,
            targetDistance: parseFloat(distance),
        });

        if (result.success) {
            toast.success("Duel Challenge Sent!");
            setSelectedOpponent(null);
        } else {
            toast.error(result.error);
        }
        setLoading(false);
    };

    return (
        <div className="space-y-6">
            <Card className="bg-slate-900 border-slate-800">
                <CardHeader>
                    <CardTitle className="flex items-center gap-2">
                        <Trophy className="text-yellow-500" />
                        New Cardio Duel
                    </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">

                    {/* Settings */}
                    <div className="grid grid-cols-2 gap-4">
                        <div className="space-y-2">
                            <label className="text-sm font-medium text-slate-400">Activity</label>
                            <Select value={activityType} onValueChange={setActivityType}>
                                <SelectTrigger>
                                    <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="RUNNING">
                                        <div className="flex items-center gap-2"><Footprints className="h-4 w-4" /> Running</div>
                                    </SelectItem>
                                    <SelectItem value="CYCLING">
                                        <div className="flex items-center gap-2"><Bike className="h-4 w-4" /> Cycling</div>
                                    </SelectItem>
                                </SelectContent>
                            </Select>
                        </div>

                        <div className="space-y-2">
                            <label className="text-sm font-medium text-slate-400">Target Distance</label>
                            <Select value={distance} onValueChange={setDistance}>
                                <SelectTrigger>
                                    <SelectValue />
                                </SelectTrigger>
                                <SelectContent>
                                    <SelectItem value="3">3 km</SelectItem>
                                    <SelectItem value="5">5 km</SelectItem>
                                    <SelectItem value="10">10 km</SelectItem>
                                    <SelectItem value="21.1">Half Marathon</SelectItem>
                                </SelectContent>
                            </Select>
                        </div>
                    </div>

                    {/* Opponent Selection */}
                    <div className="space-y-2">
                        <label className="text-sm font-medium text-slate-400">Select Rival (Matched by Power Rating)</label>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-2 max-h-60 overflow-y-auto">
                            {opponents.map((opp) => (
                                <div
                                    key={opp.id}
                                    onClick={() => setSelectedOpponent(opp.id)}
                                    className={`p-3 rounded-lg border cursor-pointer transition-all flex items-center justify-between ${selectedOpponent === opp.id
                                        ? "border-emerald-500 bg-emerald-900/20"
                                        : "border-slate-700 hover:bg-slate-800"
                                        }`}
                                >
                                    <div className="flex items-center gap-3">
                                        <Avatar className="h-8 w-8">
                                            <AvatarFallback>{opp.heroName[0]}</AvatarFallback>
                                        </Avatar>
                                        <div>
                                            <div className="font-semibold text-sm">{opp.heroName}</div>
                                            <div className="text-xs text-slate-500">Lvl {opp.level} • PR: {Math.round(opp.titan?.powerRating || 0)}</div>
                                        </div>
                                    </div>
                                    {selectedOpponent === opp.id && <div className="h-2 w-2 rounded-full bg-emerald-500" />}
                                </div>
                            ))}
                        </div>
                    </div>

                    <Button
                        className="w-full bg-gradient-to-r from-emerald-600 to-teal-600 hover:from-emerald-500 hover:to-teal-500"
                        disabled={!selectedOpponent || loading}
                        onClick={handleCreateDuel}
                    >
                        {loading ? "Sending..." : "Send Challenge"}
                    </Button>

                </CardContent>
            </Card>
        </div>
    );
}

'use client';

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import Image from 'next/image';
import { cn } from '@/lib/utils';
import { Lock, Sparkles } from 'lucide-react';
import {
    getAvailableEmotesAction,
    sendBattleEmoteAction,
    type BattleEmoteWithUnlock,
} from '@/actions/combat/emotes';
import { Button } from '@/components/ui/button';
import {
    Popover,
    PopoverContent,
    PopoverTrigger,
} from '@/components/ui/popover';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { toast } from 'sonner';

interface EmotePickerProps {
    matchId: string;
    disabled?: boolean;
    className?: string;
    onSend?: (emote: BattleEmoteWithUnlock) => void;
}

const CATEGORY_LABELS: Record<string, { label: string; icon: string }> = {
    taunt: { label: 'Taunts', icon: '😤' },
    flex: { label: 'Flex', icon: '💪' },
    gg: { label: 'GG', icon: '🎉' },
    premium: { label: 'Premium', icon: '👑' },
    seasonal: { label: 'Seasonal', icon: '🎄' },
};

export function EmotePicker({ matchId, disabled, className, onSend }: EmotePickerProps) {
    const [emotes, setEmotes] = useState<BattleEmoteWithUnlock[]>([]);
    const [isLoading, setIsLoading] = useState(true);
    const [isSending, setIsSending] = useState(false);
    const [isOpen, setIsOpen] = useState(false);

    useEffect(() => {
        async function loadEmotes() {
            const result = await getAvailableEmotesAction();
            if (result.success && result.data) {
                setEmotes(result.data);
            }
            setIsLoading(false);
        }
        loadEmotes();
    }, []);

    const handleSendEmote = async (emote: BattleEmoteWithUnlock) => {
        if (!emote.isUnlocked) {
            toast.error(
                emote.isPremium
                    ? 'Upgrade to PRO to unlock this emote'
                    : `Reach level ${emote.unlockLevel} to unlock`
            );
            return;
        }

        setIsSending(true);
        const result = await sendBattleEmoteAction(matchId, emote.id);
        setIsSending(false);

        if (result.success) {
            setIsOpen(false);
            onSend?.(emote);
            toast.success('Emote sent!');
        } else {
            toast.error(result.error || 'Failed to send emote');
        }
    };

    const categories = [...new Set(emotes.map((e) => e.category))];

    return (
        <Popover open={isOpen} onOpenChange={setIsOpen}>
            <PopoverTrigger asChild>
                <Button
                    variant="outline"
                    size="icon"
                    disabled={disabled || isLoading}
                    className={cn(
                        'h-12 w-12 rounded-full border-2 border-amber-500/50',
                        'bg-gradient-to-br from-amber-500/20 to-orange-600/20',
                        'hover:from-amber-500/30 hover:to-orange-600/30',
                        'transition-all duration-200',
                        className
                    )}
                >
                    <span className="text-2xl">😈</span>
                </Button>
            </PopoverTrigger>
            <PopoverContent
                className="w-80 bg-slate-900/95 border-amber-500/30 p-0"
                side="top"
                align="end"
            >
                <div className="p-3 border-b border-white/10">
                    <h4 className="font-semibold text-amber-400 flex items-center gap-2">
                        <Sparkles className="w-4 h-4" />
                        Battle Emotes
                    </h4>
                </div>
                <Tabs defaultValue={categories[0] || 'taunt'} className="w-full">
                    <TabsList className="w-full justify-start bg-slate-800/50 rounded-none p-1 gap-1">
                        {categories.map((cat) => (
                            <TabsTrigger
                                key={cat}
                                value={cat}
                                className="data-[state=active]:bg-amber-500/20 text-xs px-2"
                            >
                                {CATEGORY_LABELS[cat]?.icon || '📦'}{' '}
                                {CATEGORY_LABELS[cat]?.label || cat}
                            </TabsTrigger>
                        ))}
                    </TabsList>
                    {categories.map((cat) => (
                        <TabsContent key={cat} value={cat} className="p-2 m-0">
                            <div className="grid grid-cols-4 gap-2">
                                {emotes
                                    .filter((e) => e.category === cat)
                                    .map((emote) => (
                                        <EmoteButton
                                            key={emote.id}
                                            emote={emote}
                                            onClick={() => handleSendEmote(emote)}
                                            disabled={isSending}
                                        />
                                    ))}
                            </div>
                        </TabsContent>
                    ))}
                </Tabs>
            </PopoverContent>
        </Popover>
    );
}

interface EmoteButtonProps {
    emote: BattleEmoteWithUnlock;
    onClick: () => void;
    disabled?: boolean;
}

function EmoteButton({ emote, onClick, disabled }: EmoteButtonProps) {
    return (
        <motion.button
            whileHover={{ scale: emote.isUnlocked ? 1.1 : 1 }}
            whileTap={{ scale: emote.isUnlocked ? 0.95 : 1 }}
            onClick={onClick}
            disabled={disabled}
            className={cn(
                'relative w-16 h-16 rounded-lg overflow-hidden',
                'border-2 transition-all duration-200',
                emote.isUnlocked
                    ? 'border-amber-500/50 hover:border-amber-400 cursor-pointer'
                    : 'border-slate-600/50 cursor-not-allowed opacity-60'
            )}
        >
            <Image
                src={emote.gifPath}
                alt={emote.name}
                fill
                className="object-cover"
                unoptimized // GIFs need unoptimized to animate
            />
            {!emote.isUnlocked && (
                <div className="absolute inset-0 bg-black/60 flex items-center justify-center">
                    <Lock className="w-5 h-5 text-slate-400" />
                </div>
            )}
            {emote.isPremium && emote.isUnlocked && (
                <div className="absolute top-0.5 right-0.5">
                    <span className="text-xs">👑</span>
                </div>
            )}
        </motion.button>
    );
}

"use client";

import React, { useState } from "react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Label } from "@/components/ui/label";
import { createDuelChallengeAction } from "@/actions/pvp/duel";
import { toast } from "sonner";

import { Bike, Footprints, Timer, Trophy, TrendingUp } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

interface CardioDuelModalProps {
  isOpen: boolean;
  onClose: () => void;
  opponentId: string;
}

type DuelType =
  | "TITAN_VS_TITAN"
  | "DISTANCE_RACE"
  | "SPEED_DEMON"
  | "ELEVATION_GRIND";
type ActivityType = "CYCLING" | "RUNNING";

export function CardioDuelModal({
  isOpen,
  onClose,
  opponentId,
}: CardioDuelModalProps) {
  const [duelType, setDuelType] = useState<DuelType>("DISTANCE_RACE");
  const [activityType, setActivityType] = useState<ActivityType>("CYCLING");
  const [duration, setDuration] = useState<number>(10); // minutes
  const [distance, setDistance] = useState<number>(5); // km
  const [wkgTier, setWkgTier] = useState<number>(2.5);
  const [isSubmitting, setIsSubmitting] = useState(false);

  const handleSubmit = async () => {
    setIsSubmitting(true);
    try {
      const result = await createDuelChallengeAction(opponentId, {
        duelType,
        activityType,
        durationMinutes:
          duelType === "DISTANCE_RACE" || duelType === "ELEVATION_GRIND"
            ? duration
            : undefined,
        targetDistance: duelType === "SPEED_DEMON" ? distance : undefined,
        wkgTier: activityType === "CYCLING" ? wkgTier : undefined,
      });

      if (result.success) {
        toast.success("Duel challenge sent!");
        onClose();
      } else {
        toast.error(result.error || "Failed to send challenge");
      }
    } catch (_error) {
      toast.error("Something went wrong");
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px] bg-slate-900 border-slate-700 text-slate-100">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Trophy className="w-5 h-5 text-yellow-500" />
            Create Cardio Duel
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6 py-4">
          {/* Activity Selection */}
          <div className="grid grid-cols-2 gap-4">
            <button
              type="button"
              aria-pressed={activityType === "CYCLING"}
              onClick={() => setActivityType("CYCLING")}
              className={`p-4 rounded-xl border-2 flex flex-col items-center gap-2 transition-all ${activityType === "CYCLING"
                ? "border-blue-500 bg-blue-500/10 text-blue-400"
                : "border-slate-700 hover:border-slate-600 text-slate-400"
                }`}
            >
              <Bike className="w-8 h-8" />
              <span className="font-bold">Cycling</span>
            </button>
            <button
              type="button"
              aria-pressed={activityType === "RUNNING"}
              onClick={() => setActivityType("RUNNING")}
              className={`p-4 rounded-xl border-2 flex flex-col items-center gap-2 transition-all ${activityType === "RUNNING"
                ? "border-emerald-500 bg-emerald-500/10 text-emerald-400"
                : "border-slate-700 hover:border-slate-600 text-slate-400"
                }`}
            >
              <Footprints className="w-8 h-8" />
              <span className="font-bold">Running</span>
            </button>
          </div>

          {/* Duel Type Selection */}
          <div className="space-y-3">
            <Label>Duel Format</Label>
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-2">
              <button
                type="button"
                aria-pressed={duelType === "DISTANCE_RACE"}
                onClick={() => setDuelType("DISTANCE_RACE")}
                className={`px-3 py-2 rounded-lg text-sm border transition-all ${duelType === "DISTANCE_RACE"
                  ? "bg-slate-800 border-indigo-500 text-white"
                  : "border-transparent text-slate-400 hover:bg-slate-800"
                  }`}
              >
                Distance Race
              </button>
              <button
                type="button"
                aria-pressed={duelType === "SPEED_DEMON"}
                onClick={() => setDuelType("SPEED_DEMON")}
                className={`px-3 py-2 rounded-lg text-sm border transition-all ${duelType === "SPEED_DEMON"
                  ? "bg-slate-800 border-indigo-500 text-white"
                  : "border-transparent text-slate-400 hover:bg-slate-800"
                  }`}
              >
                Speed Demon
              </button>
              {activityType === "CYCLING" && (
                <button
                  type="button"
                  aria-pressed={duelType === "ELEVATION_GRIND"}
                  onClick={() => setDuelType("ELEVATION_GRIND")}
                  className={`px-3 py-2 rounded-lg text-sm border transition-all ${duelType === "ELEVATION_GRIND"
                    ? "bg-slate-800 border-indigo-500 text-white"
                    : "border-transparent text-slate-400 hover:bg-slate-800"
                    }`}
                >
                  Elevation
                </button>
              )}
            </div>
          </div>

          {/* Dynamic Inputs based on Type */}
          <AnimatePresence mode="wait">
            <motion.div
              key={duelType}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              className="space-y-4"
            >
              {(duelType === "DISTANCE_RACE" ||
                duelType === "ELEVATION_GRIND") && (
                  <div className="space-y-2">
                    <Label className="flex items-center gap-2">
                      <Timer className="w-4 h-4" />
                      Duration (minutes)
                    </Label>
                    <div className="flex gap-2">
                      {[5, 10, 15, 30].map((m) => (
                        <button
                          key={m}
                          onClick={() => setDuration(m)}
                          className={`flex-1 py-2 rounded-md border text-sm font-medium ${duration === m
                            ? "bg-indigo-600 border-indigo-600 text-white"
                            : "bg-slate-900 border-slate-700 text-slate-400 hover:border-slate-500"
                            }`}
                        >
                          {m}m
                        </button>
                      ))}
                    </div>
                  </div>
                )}

              {duelType === "SPEED_DEMON" && (
                <div className="space-y-2">
                  <Label className="flex items-center gap-2">
                    <TrendingUp className="w-4 h-4" />
                    Target Distance (km)
                  </Label>
                  <div className="flex gap-2">
                    {(activityType === "RUNNING"
                      ? [1, 3, 5, 10]
                      : [5, 10, 20, 40]
                    ).map((d) => (
                      <button
                        key={d}
                        onClick={() => setDistance(d)}
                        className={`flex-1 py-2 rounded-md border text-sm font-medium ${distance === d
                          ? "bg-indigo-600 border-indigo-600 text-white"
                          : "bg-slate-900 border-slate-700 text-slate-400 hover:border-slate-500"
                          }`}
                      >
                        {d}km
                      </button>
                    ))}
                  </div>
                </div>
              )}

              {activityType === "CYCLING" && (
                <div className="space-y-2 pt-2 border-t border-slate-800">
                  <Label className="text-yellow-400">
                    Fairness Tier (W/kg)
                  </Label>
                  <p className="text-xs text-slate-500 mb-2">
                    Kickr resistance will auto-adjust to your weight.
                  </p>
                  <input
                    type="range"
                    min="1.5"
                    max="5.0"
                    step="0.1"
                    value={wkgTier}
                    onChange={(e) => setWkgTier(parseFloat(e.target.value))}
                    className="w-full accent-yellow-500"
                  />
                  <div className="flex justify-between text-xs text-slate-400 font-mono">
                    <span>Beginner (1.5)</span>
                    <span className="text-yellow-400 font-bold text-base">
                      {wkgTier.toFixed(1)} W/kg
                    </span>
                    <span>Pro (5.0)</span>
                  </div>
                </div>
              )}
            </motion.div>
          </AnimatePresence>
        </div>

        <DialogFooter>
          <Button variant="ghost" onClick={onClose} className="text-slate-400">
            Cancel
          </Button>
          <Button
            onClick={handleSubmit}
            disabled={isSubmitting}
            className="bg-gradient-to-r from-indigo-600 to-purple-600"
          >
            {isSubmitting ? "Sending..." : "Challenge Titan"}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import { useEffect, useState } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription, DialogFooter } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Swords, User as UserIcon, Loader2 } from 'lucide-react';
import { createDuelChallengeAction, getPotentialOpponentsAction } from '@/actions/pvp/duel';
import { toast } from 'sonner';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { ScrollArea } from '@/components/ui/scroll-area';

interface ChallengeModalProps {
  isOpen: boolean;
  onClose: () => void;
  potentialOpponents?: Array<{ id: string, heroName: string }>;
  onSelectOpponent?: (id: string) => void;
}

export function ChallengeModal(props: ChallengeModalProps) {
  const { isOpen, onClose } = props;
  const [targetId, setTargetId] = useState('');
  const [loading, setLoading] = useState(false);
  const [opponents, setOpponents] = useState<any[]>([]);
  const [loadingOpponents, setLoadingOpponents] = useState(false);

  useEffect(() => {
    if (isOpen) {
      setLoadingOpponents(true);
      getPotentialOpponentsAction().then(res => {
        if (res.success && res.opponents) {
          setOpponents(res.opponents);
        }
      }).finally(() => setLoadingOpponents(false));
    }
  }, [isOpen]);

  const handleChallenge = async () => {
    if (!targetId) return;

    // If selection callback provided, use it instead of immediate creation
    if (props.onSelectOpponent) {
      props.onSelectOpponent(targetId);
      return;
    }

    setLoading(true);
    try {
      const result = await createDuelChallengeAction(targetId);
      if (result.success) {
        toast.success('Challenge sent successfully!', {
          icon: <Swords className="w-4 h-4" />
        });
        onClose();
      } else {
        toast.error(result.error || 'Failed to send challenge');
      }
    } catch (err) {
      toast.error('An unexpected error occurred');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="bg-slate-900 border-slate-800 text-slate-100 sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Swords className="text-amber-500" />
            Issue Challenge
          </DialogTitle>
          <DialogDescription>
            Select a Titan to challenge. Victory goes to the one with the highest training score.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4 py-4">
          <div className="space-y-2">
            <Label>Select Opponent</Label>
            {loadingOpponents ? (
              <div className="flex justify-center p-4">
                <Loader2 className="w-6 h-6 animate-spin text-slate-500" />
              </div>
            ) : (
              <ScrollArea className="h-[300px] border border-slate-800 rounded-md p-2">
                <div className="space-y-2">
                  {opponents.map(opp => (
                    <button
                      key={opp.id}
                      onClick={() => setTargetId(opp.id)}
                      data-testid="opponent-card"
                      className={`w-full flex items-center gap-3 p-3 rounded-lg transition-all border ${targetId === opp.id
                        ? 'bg-amber-900/20 border-amber-500/50'
                        : 'bg-slate-950 border-transparent hover:bg-slate-800'
                        }`}
                    >
                      <Avatar className="w-10 h-10 border border-slate-700">
                        <AvatarImage src={opp.image || undefined} />
                        <AvatarFallback>{opp.heroName?.[0]}</AvatarFallback>
                      </Avatar>
                      <div className="text-left flex-1">
                        <div className="font-bold text-sm text-slate-200">{opp.heroName || 'Unknown Titan'}</div>
                        <div className="text-xs text-slate-500">
                          Lvl {opp.level} • {opp.faction}
                          {opp.titan?.powerRating !== undefined && (
                            <span className="ml-2 text-amber-500 font-mono" data-testid="opponent-rating">
                              PR: {Math.round(opp.titan.powerRating)}
                            </span>
                          )}
                        </div>
                      </div>
                      {targetId === opp.id && <Swords className="w-4 h-4 text-amber-500" />}
                    </button>
                  ))}
                  {opponents.length === 0 && (
                    <p className="text-center text-slate-500 py-4">No active Titans found.</p>
                  )}
                </div>
              </ScrollArea>
            )}
          </div>
        </div>

        <DialogFooter>
          <Button variant="ghost" onClick={onClose} disabled={loading}>
            Cancel
          </Button>
          <Button
            onClick={handleChallenge}
            disabled={!targetId || loading}
            className="bg-amber-600 hover:bg-amber-700 text-white font-bold"
          >
            {loading ? 'Sending...' : 'Challenge Titan'}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Progress } from "@/components/ui/progress";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Swords, Trophy, Timer } from "lucide-react";
import { motion } from "framer-motion";
import { useState } from "react";
import { DuelArena } from "@/features/game/DuelArena";

import { ExtendedDuel } from "@/app/iron-arena/ArenaClient";

interface DuelCardProps {
  duel: ExtendedDuel;
  currentUserId: string;
  onTaunt?: () => void;
}


export function DuelCard({ duel, currentUserId, onTaunt }: DuelCardProps) {
  const [showArena, setShowArena] = useState(false);
  const isChallenger = currentUserId === duel.challenger.id;
  const userScore = isChallenger ? duel.challengerScore : duel.defenderScore;
  const opponentScore = isChallenger
    ? duel.defenderScore
    : duel.challengerScore;
  const user = isChallenger ? duel.challenger : duel.defender;
  const opponent = isChallenger ? duel.defender : duel.challenger;

  // Cardio Logic
  const isCardio =
    duel.duelType !== "TITAN_VS_TITAN" && duel.duelType !== undefined;
  const userDistance = isChallenger
    ? duel.challengerDistance || 0
    : duel.defenderDistance || 0;
  const opponentDistance = isChallenger
    ? duel.defenderDistance || 0
    : duel.challengerDistance || 0;

  // Calculate percentages
  let maxMetric = 100;
  if (isCardio) {
    if (
      duel.duelType === "DISTANCE_RACE" ||
      duel.duelType === "ELEVATION_GRIND"
    ) {
      // For distance/elevation race, relative scale
      maxMetric = Math.max(userDistance, opponentDistance, 1);
    } else if (duel.duelType === "SPEED_DEMON") {
      // For speed demon, target is max
      maxMetric = duel.targetDistance || 10;
    }
  } else {
    // Standard Score
    maxMetric = Math.max(userScore, opponentScore, 500);
  }

  const userProgress = isCardio
    ? (userDistance / maxMetric) * 100
    : (userScore / maxMetric) * 100;

  const opponentProgress = isCardio
    ? (opponentDistance / maxMetric) * 100
    : (opponentScore / maxMetric) * 100;

  const daysLeft = duel.endDate
    ? Math.max(
      0,
      Math.ceil(
        (new Date(duel.endDate).getTime() - Date.now()) /
        (1000 * 60 * 60 * 24),
      ),
    )
    : 7;

  return (
    <Card className="w-full bg-slate-900 border-slate-800 text-slate-100 overflow-hidden relative">
      {/* Background Ambient Effect */}
      <div className="absolute inset-0 bg-gradient-to-br from-red-900/10 to-blue-900/10 pointer-events-none" />

      <CardHeader className="pb-2 border-b border-white/5 relative z-10">
        <div className="flex justify-between items-center">
          <div className="flex items-center gap-2 text-amber-500 font-bold tracking-wider">
            <Swords className="w-5 h-5" />
            <span>
              {duel.duelType === "SPEED_DEMON"
                ? "SPEED DEMON"
                : duel.duelType === "DISTANCE_RACE"
                  ? "DISTANCE RACE"
                  : "ACTIVE DUEL"}
            </span>
          </div>
          <div className="flex items-center gap-2 text-slate-400 text-sm">
            <Timer className="w-4 h-4" />
            <span>{daysLeft}d remaining</span>
          </div>
        </div>
      </CardHeader>

      <CardContent className="pt-6 relative z-10 space-y-6">
        {/* Head to Head */}
        <div className="flex justify-between items-center px-2">
          {/* User Side */}
          <div className="flex flex-col items-center gap-2 text-center w-1/3">
            <div className="relative">
              <Avatar className="w-16 h-16 border-2 border-blue-500 shadow-[0_0_15px_rgba(59,130,246,0.5)]">
                <AvatarImage src={undefined} />
                <AvatarFallback className="bg-slate-800 text-blue-500">
                  {user.heroName?.[0] || "U"}
                </AvatarFallback>
              </Avatar>
              <Badge className="absolute -bottom-2 -right-2 bg-blue-600 border-blue-400 px-1 text-[10px]">
                Lvl {user.level}
              </Badge>
            </div>
            <div>
              <div className="font-bold text-lg truncate max-w-[100px]">
                {user.heroName || "You"}
              </div>
              <div className="text-2xl font-black text-blue-400">
                {isCardio ? `${userDistance.toFixed(2)} km` : userScore}
              </div>
            </div>
          </div>

          {/* VS Divider */}
          <div className="flex flex-col items-center justify-center w-1/3 -mt-6">
            <div className="text-4xl font-black text-white/10 italic">VS</div>
          </div>

          {/* Opponent Side */}
          <div className="flex flex-col items-center gap-2 text-center w-1/3">
            <div className="relative">
              <Avatar className="w-16 h-16 border-2 border-red-500 shadow-[0_0_15px_rgba(239,68,68,0.5)]">
                <AvatarImage src={undefined} />
                <AvatarFallback className="bg-slate-800 text-red-500">
                  {opponent.heroName?.[0] || "O"}
                </AvatarFallback>
              </Avatar>
              <Badge className="absolute -bottom-2 -right-2 bg-red-600 border-red-400 px-1 text-[10px]">
                Lvl {opponent.level}
              </Badge>
            </div>
            <div>
              <div className="font-bold text-lg truncate max-w-[100px]">
                {opponent.heroName || "Opponent"}
              </div>
              <div className="text-2xl font-black text-red-400">
                {isCardio ? `${opponentDistance.toFixed(2)} km` : opponentScore}
              </div>
            </div>
          </div>
        </div>

        {/* Semantic Comparison Bar */}
        <div className="space-y-4">
          <div className="space-y-1">
            <div className="flex justify-between text-xs text-slate-400 uppercase tracking-widest font-semibold">
              <span>Dominance</span>
              <span>{userScore > opponentScore ? "Leading" : "Trailing"}</span>
            </div>
            <div className="h-4 bg-slate-800 rounded-full overflow-hidden flex relative">
              {/* Center Marker */}
              <div className="absolute left-1/2 top-0 bottom-0 w-0.5 bg-white/20 z-20" />

              <motion.div
                initial={{ width: 0 }}
                animate={{ width: `${userProgress}%` }}
                className="h-full bg-gradient-to-r from-blue-900 to-blue-500 rounded-r-sm z-10"
              />
              {/* Overlap logic is tricky in CSS flex, simplifying to two bars for now */}
            </div>
            {/* Alternative Visualization: Tug of War */}
            <div className="h-2 bg-slate-800 rounded-full overflow-hidden flex mt-2">
              <motion.div
                initial={{ flex: 1 }}
                animate={{ flex: userScore + 1 }}
                className="bg-blue-600 h-full"
              />
              <div className="w-1 bg-black z-10"></div>
              <motion.div
                initial={{ flex: 1 }}
                animate={{ flex: opponentScore + 1 }}
                className="bg-red-600 h-full"
              />
            </div>
          </div>
        </div>

        {/* Actions */}
        <div className="flex gap-2">
          <Button
            variant="outline"
            className="flex-1 border-slate-700 hover:bg-slate-800"
            size="sm"
          >
            View Details
          </Button>

          {duel.status === "ACTIVE" && (
            <Button
              className="flex-1 bg-amber-600 hover:bg-amber-500 text-white font-bold tracking-wider animate-pulse"
              size="sm"
              onClick={() => setShowArena(true)}
            >
              ENTER ARENA ⚔️
            </Button>
          )}

          <Button
            variant="ghost"
            className="flex-1 text-amber-500 hover:text-amber-400 hover:bg-amber-950/30"
            size="sm"
            onClick={async () => {
              if (onTaunt) {
                await onTaunt();
              }
            }}
          >
            Send Taunt 💬
          </Button>
        </div>
      </CardContent>

      {/* Arena Modal */}
      {showArena && (
        <DuelArena
          duelId={duel.id}
          currentUserId={currentUserId}
          onClose={() => setShowArena(false)}
        />
      )}
    </Card>
  );
}

