"use client";

import React, { useEffect } from "react";
import { Timer, Play, Pause, RotateCcw, Plus, Minus } from "lucide-react";
import { useRestTimer } from "@/hooks/useRestTimer";
import { cn } from "@/lib/utils";
import { playSound } from "@/utils"; // Ensure this exists or mock it

interface RestTimerProps {
  className?: string;
}

export const RestTimer: React.FC<RestTimerProps> = ({ className }) => {
  const { isActive, timeLeft, start, stop, addTime, reset, check, initialSeconds, hasFinished } = useRestTimer();
  const [hasPlayedSound, setHasPlayedSound] = React.useState(false);

  // Tick effect
  useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isActive) {
      interval = setInterval(() => {
        check();
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [isActive, check]);

  // Sound effect on completion
  useEffect(() => {
    if (hasFinished && !hasPlayedSound) {
      playSound("ding");
      setHasPlayedSound(true);
    }
    // Reset flag when timer restarts
    if (!hasFinished && hasPlayedSound) {
      setHasPlayedSound(false);
    }
  }, [hasFinished, hasPlayedSound]);

  const formatTime = (seconds: number) => {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m}:${s < 10 ? "0" : ""}${s}`;
  };

  if (!isActive && timeLeft === 90) {
    // Minimal state? Or always show?
  }

  return (
    <div className={cn("bg-zinc-900 border border-white/5 rounded-xl p-4 flex flex-col items-center gap-4 shadow-lg", className)}>
      <div className="relative">
        <div className={cn(
          "text-6xl font-black font-mono tracking-tighter tabular-nums transition-colors",
          isActive ? "text-magma" : "text-zinc-600"
        )}>
          {formatTime(timeLeft)}
        </div>
        <Timer className={cn(
          "absolute -top-2 -right-6 w-6 h-6",
          isActive ? "text-magma animate-pulse" : "text-zinc-700"
        )} />
      </div>

      <div className="flex items-center gap-3 w-full justify-center">
        <button
          onClick={() => isActive ? stop() : start(timeLeft > 0 ? timeLeft : 90)}
          className={cn(
            "p-4 rounded-full transition-all shadow-lg active:scale-95",
            isActive ? "bg-zinc-800 text-zinc-400 hover:text-white" : "bg-magma text-black hover:bg-magma/90"
          )}
        >
          {isActive ? (
            <Pause className="w-6 h-6 fill-current" />
          ) : (
            <Play className="w-6 h-6 fill-current ml-1" />
          )}
        </button>

        <button
          onClick={() => reset()}
          className="p-4 bg-zinc-800 text-zinc-500 rounded-full hover:bg-zinc-700 hover:text-white transition-colors"
        >
          <RotateCcw className="w-6 h-6" />
        </button>
      </div>

      <div className="flex gap-2">
        <button
          onClick={() => addTime(-10)}
          className="px-3 py-1.5 bg-zinc-800 rounded-lg text-xs font-bold text-zinc-500 hover:text-white flex items-center gap-1 transition-colors"
        >
          <Minus className="w-3 h-3" /> 10s
        </button>
        <button
          onClick={() => addTime(30)}
          className="px-3 py-1.5 bg-zinc-800 rounded-lg text-xs font-bold text-zinc-500 hover:text-white flex items-center gap-1 transition-colors"
        >
          <Plus className="w-3 h-3" /> 30s
        </button>
      </div>
    </div>
  );
};

"use client";

import React, { useState, useEffect } from "react";
import { SetData } from "@/actions/training/strength";
import { CheckCircle, Circle, Flame, ChevronDown } from "lucide-react";
import { cn } from "@/lib/utils";

// Set types for different training techniques
export type SetType = "normal" | "failure" | "dropset" | "warmup" | "myoreps";

const SET_TYPE_CONFIG: Record<SetType, { label: string; icon: string; color: string }> = {
  normal: { label: "Normal", icon: "", color: "text-zinc-400" },
  failure: { label: "Failure", icon: "🔥", color: "text-orange-500" },
  dropset: { label: "Drop", icon: "⬇️", color: "text-blue-500" },
  warmup: { label: "Warm", icon: "🌡️", color: "text-yellow-500" },
  myoreps: { label: "Myo", icon: "⚡", color: "text-purple-500" },
};

interface SetRowProps {
  index?: number; // Optional as index might be passed or calculated
  setNumber?: number; // Explicit number from parent
  set: SetData;
  onChange?: (updates: Partial<SetData>) => void; // Optional if read-only or handled differently
  onDelete?: () => void;
  onComplete?: () => void;
  lastPerformance?: { weight: number; reps: number } | null;
}

export const SetRow: React.FC<SetRowProps> = ({
  index,
  setNumber,
  set,
  onChange,
  onDelete,
  onComplete,
  lastPerformance,
}) => {
  const displayIndex = setNumber || (index !== undefined ? index + 1 : 1);

  const [reps, setReps] = useState(set.reps || 0);
  const [weight, setWeight] = useState(set.weight || 0);
  const [rpe, setRpe] = useState(set.rpe || 8);
  const [setType, setSetType] = useState<SetType>((set.setType as SetType) || "normal");
  const [showTypeMenu, setShowTypeMenu] = useState(false);

  // Sync local state if prop changes upstream
  useEffect(() => {
    setReps(set.reps);
    setWeight(set.weight);
    if (set.setType) setSetType(set.setType as SetType);
  }, [set]);

  // Pre-fill history if empty (Auto-Fill)
  useEffect(() => {
    if (lastPerformance && !set.completedAt && weight === 0) {
      setWeight(lastPerformance.weight);
      setReps(lastPerformance.reps);
    }
  }, [lastPerformance, set.completedAt]); // Run once when history loads or if it changes (rare)

  const handleComplete = () => {
    onChange?.({ reps, weight, rpe, setType });
    onComplete?.();
  };

  const handleSetTypeChange = (newType: SetType) => {
    setSetType(newType);
    onChange?.({ setType: newType });
    setShowTypeMenu(false);
  };

  const currentTypeConfig = SET_TYPE_CONFIG[setType];

  return (
    <div
      className={cn(
        "grid grid-cols-12 gap-2 items-center p-2 rounded-md transition-colors relative",
        set.completedAt ? "bg-green-500/10" : "bg-black/20"
      )}
    >
      {/* Set Number + Type Indicator */}
      <div className="col-span-1 text-center relative">
        <button
          onClick={() => setShowTypeMenu(!showTypeMenu)}
          className={cn(
            "font-bold transition-colors",
            currentTypeConfig.color
          )}
          title={`Set Type: ${currentTypeConfig.label}`}
        >
          {currentTypeConfig.icon || displayIndex}
          {!currentTypeConfig.icon && (
            <span className="text-zinc-500">{displayIndex}</span>
          )}
        </button>

        {/* Set Type Dropdown */}
        {showTypeMenu && (
          <div className="absolute top-full left-0 z-50 mt-1 bg-zinc-800 border border-zinc-700 rounded-lg shadow-xl py-1 min-w-[100px]">
            {Object.entries(SET_TYPE_CONFIG).map(([type, config]) => (
              <button
                key={type}
                onClick={() => handleSetTypeChange(type as SetType)}
                className={cn(
                  "w-full px-3 py-1.5 text-left text-xs hover:bg-zinc-700 transition-colors flex items-center gap-2",
                  setType === type && "bg-zinc-700"
                )}
              >
                <span>{config.icon || "○"}</span>
                <span className={config.color}>{config.label}</span>
              </button>
            ))}
          </div>
        )}
      </div>

      {/* Weight Input */}
      <div className="col-span-3 relative">
        <input
          type="number"
          value={weight || ""}
          onChange={(e) => setWeight(parseFloat(e.target.value) || 0)}
          placeholder={lastPerformance ? `${lastPerformance.weight}kg` : "kg"}
          className="w-full bg-transparent border-b border-white/10 text-center focus:border-magma focus:outline-none font-mono relative z-10"
        />
      </div>

      {/* Reps Input */}
      <div className="col-span-3">
        <input
          type="number"
          value={reps || ""}
          onChange={(e) => setReps(parseFloat(e.target.value) || 0)}
          placeholder={lastPerformance ? `${lastPerformance.reps}` : "reps"}
          className={cn(
            "w-full bg-transparent border-b border-white/10 text-center focus:border-magma focus:outline-none font-mono",
            setType === "failure" && "text-orange-400 font-bold"
          )}
        />
      </div>

      {/* RPE Input */}
      <div className="col-span-3">
        <input
          type="number"
          value={rpe || ""}
          onChange={(e) => setRpe(parseFloat(e.target.value) || 0)}
          placeholder="RPE"
          className="w-full bg-transparent border-b border-white/10 text-center focus:border-magma focus:outline-none font-mono text-xs text-zinc-400"
        />
      </div>

      {/* Complete Button */}
      <div className="col-span-2 flex items-center justify-end gap-2">
        <button
          onClick={handleComplete}
          className={cn(
            "transition-colors",
            set.completedAt ? "text-green-500" : "text-zinc-500 hover:text-white"
          )}
        >
          {set.completedAt ? (
            <CheckCircle className="w-5 h-5" />
          ) : (
            <Circle className="w-5 h-5" />
          )}
        </button>
      </div>
    </div>
  );
};

"use client";

import React, { useState } from "react";
import { SetRow } from "./SetRow";
import { logSetAction, SetData } from "@/actions/training/strength";
import { Plus, Save, Dumbbell } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMaxReps } from "@/hooks/useMaxReps";
import { PRBadge } from "@/components/ui/PRBadge";
import { PRCelebration } from "@/components/ui/PRCelebration";

interface ExerciseLogProps {
  userId: string;
  exerciseId: string;
  exerciseName: string;
  initialSets?: SetData[];
  currentWeight?: number;
}

export const StrengthLog: React.FC<ExerciseLogProps> = ({
  userId,
  exerciseId,
  exerciseName,
  initialSets = [],
  currentWeight,
}) => {
  const [sets, setSets] = useState<SetData[]>(initialSets);
  const { toast } = useToast();

  // Max Reps PR tracking
  const { maxReps, isLoading, checkPR } = useMaxReps(exerciseId, currentWeight);
  const [showCelebration, setShowCelebration] = useState(false);
  const [newPRReps, setNewPRReps] = useState(0);
  const [previousMax, setPreviousMax] = useState<number | null>(null);

  const addSet = () => {
    // Copy previous set values for convenience
    const previousSet = sets[sets.length - 1] || {
      reps: 10,
      weight: 20,
      rpe: 8,
    };
    const newSet: SetData = {
      id: crypto.randomUUID(),
      reps: previousSet.reps,
      weight: previousSet.weight,
      rpe: previousSet.rpe,
      isWarmup: false,
    };
    setSets([...sets, newSet]);
  };

  const handleSetChange = (index: number, updates: Partial<SetData>) => {
    const newSets = [...sets];
    newSets[index] = { ...newSets[index], ...updates };
    setSets(newSets);
  };

  const handleSetComplete = async (index: number) => {
    const set = sets[index];
    // Optimistic update
    handleSetChange(index, { completedAt: new Date().toISOString() });

    // Check for rep PR
    if (set.reps && set.setType === "failure") {
      const isPR = await checkPR(set.reps);
      if (isPR) {
        setPreviousMax(maxReps);
        setNewPRReps(set.reps);
        setShowCelebration(true);
      }
    }

    // Server Action
    const result = await logSetAction(userId, exerciseId, set);
    if (!result.success) {
      toast({
        title: "Error",
        description: "Failed to log set",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="bg-zinc-900 border border-white/5 rounded-xl p-4 w-full max-w-md mx-auto">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          <Dumbbell className="w-5 h-5 text-magma" />
          <h3 className="font-bold text-lg text-white">{exerciseName}</h3>
        </div>
        <button className="text-xs text-zinc-500 hover:text-white">
          History
        </button>
      </div>

      {/* PR Badge */}
      <PRBadge
        maxReps={maxReps}
        weight={currentWeight}
        isLoading={isLoading}
        className="mb-4"
      />

      <div className="space-y-1">
        <div className="grid grid-cols-12 gap-2 text-xs text-zinc-500 uppercase font-bold px-2 mb-2">
          <div className="col-span-1 text-center">Set</div>
          <div className="col-span-3 text-center">kg</div>
          <div className="col-span-3 text-center">Reps</div>
          <div className="col-span-3 text-center">RPE</div>
          <div className="col-span-2"></div>
        </div>

        {sets.map((set, index) => (
          <SetRow
            key={set.id || index}
            index={index}
            set={set}
            onChange={(updates) => handleSetChange(index, updates)}
            onDelete={() => {
              /* impl delete */
            }}
            onComplete={() => handleSetComplete(index)}
          />
        ))}
      </div>

      <button
        onClick={addSet}
        className="w-full mt-4 py-2 flex items-center justify-center gap-2 bg-white/5 hover:bg-white/10 rounded-md text-sm font-medium transition-colors text-zinc-300"
      >
        <Plus className="w-4 h-4" />
        Add Set
      </button>

      {/* PR Celebration Modal */}
      <PRCelebration
        isVisible={showCelebration}
        newReps={newPRReps}
        previousReps={previousMax}
        exerciseName={exerciseName}
        onClose={() => setShowCelebration(false)}
      />
    </div>
  );
};

'use server';

import { createClient } from "@/utils/supabase/server";
import { prisma } from "@/lib/prisma";

interface CustomExerciseInput {
    name: string;
    muscle: string;
    equipment: string;
}

export async function createCustomExercise(data: CustomExerciseInput) {
    const supabase = await createClient();
    const { data: { user } } = await supabase.auth.getUser();
    const userId = user?.id;

    if (!userId) throw new Error("Unauthorized");

    try {
        // Use the Exercise model (which supports custom exercises via a createdBy pattern if needed)
        // For now, create in Exercise table directly
        const exercise = await prisma.exercise.create({
            data: {
                id: `custom_${Date.now()}`,
                name: data.name,
                muscleGroup: data.muscle,
                equipment: data.equipment,
                secondaryMuscles: [],
            }
        });
        return { ...exercise, isCustom: true };
    } catch (e) {
        console.error("Create custom exercise failed", e);
        // Fallback for demo
        return {
            id: `custom_${Date.now()}`,
            name: data.name,
            muscle: data.muscle,
            equipment: data.equipment,
            isCustom: true
        };
    }
}

export async function getCustomExercises() {
    const supabase = await createClient();
    const { data: { user } } = await supabase.auth.getUser();
    const userId = user?.id;

    if (!userId) return [];

    try {
        // Get exercises that start with 'custom_' prefix (our convention)
        const custom = await prisma.exercise.findMany({
            where: {
                id: { startsWith: 'custom_' }
            }
        });
        return custom.map(ex => ({ ...ex, isCustom: true }));
    } catch (e) {
        return [];
    }
}

