import { create } from "zustand";

interface HRState {
    currentHR: number;
    maxHR: number;
    restingHR: number;
    recoveryStartTime: number | null;
    recoveryStartHR: number | null;
    recoveryStatus: "none" | "recovering" | "recovered";
    metrics: {
        zone: number; // 1-5
        drift: number; // Percentage drift from baseline
    };
}

interface HRStore extends HRState {
    updateHR: (hr: number) => void;
    setZones: (max: number, resting: number) => void;
    startRecovery: () => void;
    stopRecovery: () => void;
    reset: () => void;
}

const DEFAULT_STATE: HRState = {
    currentHR: 0,
    maxHR: 190, // Default fallback
    restingHR: 60, // Default fallback
    recoveryStartTime: null,
    recoveryStartHR: null,
    recoveryStatus: "none",
    metrics: {
        zone: 0,
        drift: 0,
    }
};

export const useHRStore = create<HRStore>((set, get) => ({
    ...DEFAULT_STATE,

    setZones: (max, resting) => set({ maxHR: max, restingHR: resting }),

    updateHR: (hr) => {
        const { maxHR, restingHR, recoveryStatus, recoveryStartHR, recoveryStartTime } = get();

        // Calculate Zone (Karvonen)
        const hrr = maxHR - restingHR;
        const intensity = (hr - restingHR) / hrr;
        let zone = 1;
        if (intensity > 0.6) zone = 2;
        if (intensity > 0.7) zone = 3;
        if (intensity > 0.8) zone = 4;
        if (intensity > 0.9) zone = 5;

        // Check Recovery
        let newStatus = recoveryStatus;
        if (recoveryStatus === "recovering" && recoveryStartHR && recoveryStartTime) {
            const drop = recoveryStartHR - hr;
            const duration = (Date.now() - recoveryStartTime) / 1000;

            // Simple Logic: If HR drops by 20bpm or falls below 100bpm, considered recovered
            if (drop >= 20 || hr < 100) {
                newStatus = "recovered";
            }
        }

        set({
            currentHR: hr,
            metrics: { ...get().metrics, zone },
            recoveryStatus: newStatus
        });
    },

    startRecovery: () => set((state) => ({
        recoveryStatus: "recovering",
        recoveryStartTime: Date.now(),
        recoveryStartHR: state.currentHR
    })),

    stopRecovery: () => set({ recoveryStatus: "none", recoveryStartTime: null }),

    reset: () => set(DEFAULT_STATE)
}));

// Derived hook for use in components
export const useHRRecoveryTimer = () => {
    const store = useHRStore();

    // We could add polling logic or simulation here if we don't have a real source

    return store;
};

import { useState, useEffect, useMemo } from "react";
import type { Exercise } from "@/types";

export interface VolumeFeedback {
  muscleGroup: string;
  currentSets: number;
  mrv: number; // Maximum Recoverable Volume
  percentage: number;
  status: "LOW" | "OPTIMAL" | "OVER";
}

// Muscle group MRV guidelines (sets per week)
const MRV_GUIDELINES: Record<string, number> = {
  Chest: 22,
  Back: 25,
  Legs: 20,
  Shoulders: 20,
  Biceps: 26,
  Triceps: 26,
  Abs: 25,
  Quads: 20,
  Hamstrings: 18,
  Glutes: 22,
};

// Simple muscle group mapper (could be enhanced with exercise database)
function detectMuscleGroup(exerciseName: string): string {
  const name = exerciseName.toLowerCase();

  if (
    name.includes("bench") ||
    (name.includes("press") && name.includes("chest"))
  )
    return "Chest";
  if (name.includes("squat") || name.includes("leg press")) return "Legs";
  if (name.includes("deadlift") || name.includes("row")) return "Back";
  if (name.includes("shoulder") || name.includes("overhead"))
    return "Shoulders";
  if (name.includes("curl")) return "Biceps";
  if (name.includes("extension") || name.includes("dip")) return "Triceps";
  if (name.includes("lunge")) return "Legs";

  return "Unknown";
}

/**
 * Enhanced Volume Tracking Hook
 * Tracks sets per muscle group for current workout session
 * Provides real-time feedback based on MRV landmarks
 */
export function useVolumeTracking(exercises: Exercise[]) {
  const volumeData = useMemo(() => {
    const muscleVolumes: Record<string, number> = {};

    exercises.forEach((exercise) => {
      const muscleGroup = detectMuscleGroup(exercise.name);
      const completedSets = exercise.sets.filter((s) => s.completed).length;

      if (muscleGroup !== "Unknown") {
        muscleVolumes[muscleGroup] =
          (muscleVolumes[muscleGroup] || 0) + completedSets;
      }
    });

    return muscleVolumes;
  }, [exercises]);

  const getVolumeFeedback = (exerciseName: string): VolumeFeedback | null => {
    const muscleGroup = detectMuscleGroup(exerciseName);
    if (muscleGroup === "Unknown") return null;

    const currentSets = volumeData[muscleGroup] || 0;
    const mrv = MRV_GUIDELINES[muscleGroup] || 20;
    const percentage = Math.round((currentSets / mrv) * 100);

    let status: "LOW" | "OPTIMAL" | "OVER" = "LOW";
    if (percentage >= 80) status = "OPTIMAL";
    if (percentage >= 100) status = "OVER";

    return {
      muscleGroup,
      currentSets,
      mrv,
      percentage,
      status,
    };
  };

  return {
    volumeData,
    getVolumeFeedback,
  };
}

