import React, { useState, useEffect, useMemo } from "react";
import { SESSIONS } from "../../../data/static";
import {
  ExerciseLog,
  MeditationLog,
} from "../../../types";
import {
  X,
  Shield,
  Swords,
  Zap,
  Activity,
  Brain,
  User,
  TrendingUp,
  Heart,
  Target,
  Calendar,
