                    {/* Podcast Toggle Button */}
                    {pocketCastsConnected && (
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          setPodcastOpen((prev) => !prev);
                        }}
                        className={cn(
                          "p-2 rounded-lg transition-colors",
                          podcastOpen
                            ? "bg-magma text-white"
                            : "bg-zinc-800 hover:bg-zinc-700 text-zinc-400"
