// Test the zone calculation logic directly
describe("getZoneFromHr (Zone Calculation)", () => {
  // Extract the function logic for direct testing
  const getZoneFromHr = (hr: number, maxHr: number = 190) => {
    const pct = (hr / maxHr) * 100;
    if (pct < 60) return 1;
    if (pct < 70) return 2;
    if (pct < 80) return 3;
    if (pct < 90) return 4;
    return 5;
  };

  it("should return Zone 1 for HR below 60% of maxHr", () => {
    expect(getZoneFromHr(100, 190)).toBe(1); // 52.6%
    expect(getZoneFromHr(110, 190)).toBe(1); // 57.9%
  });

  it("should return Zone 2 for HR between 60-70% of maxHr", () => {
    expect(getZoneFromHr(114, 190)).toBe(2); // 60%
    expect(getZoneFromHr(130, 190)).toBe(2); // 68.4%
  });

  it("should return Zone 3 for HR between 70-80% of maxHr", () => {
    expect(getZoneFromHr(133, 190)).toBe(3); // 70%
    expect(getZoneFromHr(150, 190)).toBe(3); // 78.9%
  });

  it("should return Zone 4 for HR between 80-90% of maxHr", () => {
    expect(getZoneFromHr(152, 190)).toBe(4); // 80%
    expect(getZoneFromHr(170, 190)).toBe(4); // 89.5%
  });

  it("should return Zone 5 for HR above 90% of maxHr", () => {
    expect(getZoneFromHr(171, 190)).toBe(5); // 90%
    expect(getZoneFromHr(185, 190)).toBe(5); // 97.4%
  });

  it("should correctly calculate zones with custom maxHr", () => {
    // For maxHr = 170
    expect(getZoneFromHr(100, 170)).toBe(1); // 58.8%
    expect(getZoneFromHr(103, 170)).toBe(2); // 60.6%
    expect(getZoneFromHr(136, 170)).toBe(4); // 80%
    expect(getZoneFromHr(155, 170)).toBe(5); // 91.2%
  });

  it("should handle edge cases at zone boundaries", () => {
    // Exactly at boundary percentages
    const maxHr = 200;
    expect(getZoneFromHr(119, maxHr)).toBe(1); // 59.5% < 60
    expect(getZoneFromHr(120, maxHr)).toBe(2); // 60% exactly
    expect(getZoneFromHr(140, maxHr)).toBe(3); // 70% exactly
    expect(getZoneFromHr(160, maxHr)).toBe(4); // 80% exactly
    expect(getZoneFromHr(180, maxHr)).toBe(5); // 90% exactly
  });
});

"use client";

import React, { useMemo } from "react";
import { motion, AnimatePresence, useAnimation } from "framer-motion";
import { AlertTriangle, Trophy, Skull, Footprints, Zap, HeartPulse } from "lucide-react";
import { ChaseState, ChaseDifficulty } from "@/types/chase";
import { ChaseEngine } from "@/services/game/ChaseEngine";
import { cn } from "@/lib/utils";
import { playSound } from "@/utils/root_utils";

interface ChaseOverlayProps {
    chaseState: ChaseState;
    difficulty: ChaseDifficulty;
    currentPaceKph: number;
    onCaughtConfirm: () => void;
    onEscapedConfirm: () => void;
}

/**
 * ChaseOverlay - Visual HUD for chase mode
 *
 * Shows:
 * - Distance gap progress bar
 * - Monster approaching indicator
 * - Status messages
 * - Victory/defeat states
 */
export function ChaseOverlay({
    chaseState,
    difficulty,
    currentPaceKph,
    onCaughtConfirm,
    onEscapedConfirm,
}: ChaseOverlayProps) {
    const dangerLevel = ChaseEngine.getDangerLevel(chaseState);
    const statusMessage = ChaseEngine.getStatusMessage(chaseState);
    const requiredPace = ChaseEngine.getRequiredPace(chaseState, difficulty);

    // Color based on danger level
    const dangerColor = useMemo(() => {
        if (dangerLevel < 0.3) return "from-green-500 to-green-600";
        if (dangerLevel < 0.6) return "from-yellow-500 to-orange-500";
        if (dangerLevel < 0.85) return "from-orange-500 to-red-500";
        return "from-red-600 to-red-800";
    }, [dangerLevel]);

    const bgPulse = dangerLevel > 0.7 && !chaseState.isCaught && !chaseState.hasEscaped;

    // Heartbeat Effect
    React.useEffect(() => {
        if (dangerLevel > 0.8 && !chaseState.isCaught && !chaseState.hasEscaped) {
            const interval = setInterval(() => {
                playSound("heartbeat");
            }, 1000 * (1 - dangerLevel * 0.5)); // Faster heartbeat as danger increases
            return () => clearInterval(interval);
        }
    }, [dangerLevel, chaseState.isCaught, chaseState.hasEscaped]);

    // Gap percentage for progress bar (0-100)
    const gapPercentage = Math.max(
        0,
        Math.min(100, (chaseState.distanceGapMeters / chaseState.escapeDistanceMeters) * 100)
    );

    const paceDelta = currentPaceKph - requiredPace;

    // Screen Shake Animation Control
    const shakeControls = useAnimation();

    React.useEffect(() => {
        if (dangerLevel > 0.8) {
            shakeControls.start({
                x: [0, -5, 5, -5, 5, 0],
                transition: { duration: 0.5, repeat: Infinity, repeatDelay: 1 }
            });
        } else {
            shakeControls.stop();
            shakeControls.set({ x: 0 });
        }
    }, [dangerLevel, shakeControls]);

    return (
        <div className="absolute inset-0 pointer-events-none overflow-hidden z-50">

            {/* 1. Dynamic Vignette (Red/Black gradient on edges) */}
            <div
                className="absolute inset-0 transition-opacity duration-1000"
                style={{
                    background: `radial-gradient(circle, transparent 50%, rgba(0,0,0,${dangerLevel * 0.8}) 90%, rgba(${dangerLevel > 0.8 ? '100,0,0' : '0,0,0'}, ${dangerLevel * 0.6}) 100%)`,
                    opacity: 0.8 + (dangerLevel * 0.2)
                }}
            />

            {/* 2. Looming Monster Background Presence */}
            <motion.div
                animate={{
                    scale: 1 + (dangerLevel * 0.5),
                    opacity: 0.1 + (dangerLevel * 0.2),
                    y: (1 - dangerLevel) * 100
                }}
                className="absolute inset-0 flex items-center justify-center pointer-events-none overflow-hidden"
            >
                <div className="text-[40vh] filter blur-sm transform transition-all duration-1000 select-none opacity-20">
                    {chaseState.chaser.image}
                </div>
            </motion.div>

            {/* 3. Main HUD Layer (with Screen Shake) */}
            <motion.div
                animate={shakeControls}
                className={cn(
                    "relative w-full h-full flex flex-col pt-4",
                    bgPulse && "animate-pulse" // Keep pulse on container if critically low
                )}
            >
                <div className="pointer-events-auto mx-auto max-w-2xl px-4 w-full">
                    <div className="bg-black/80 backdrop-blur-sm border border-white/10 rounded-xl p-4 shadow-2xl">
                        {/* Header: Monster Info */}
                        <div className="flex items-center justify-between mb-3">
                            <div className="flex items-center gap-3">
                                <span className="text-3xl">{chaseState.chaser.image}</span>
                                <div>
                                    <h3 className="font-bold text-white text-lg">
                                        {chaseState.chaser.name}
                                    </h3>
                                    <p className="text-xs text-zinc-400 uppercase tracking-wider">
                                        {chaseState.chaser.type} • Lvl {chaseState.chaser.level}
                                    </p>
                                </div>
                            </div>

                            {/* Required Pace Indicator */}
                            <div className="text-right">
                                <p className="text-xs text-zinc-400 uppercase">Required Pace</p>
                                <p className="font-mono text-lg font-bold text-cyan-400">
                                    {requiredPace.toFixed(1)} km/h
                                </p>
                            </div>
                        </div>

                        {/* Status Message */}
                        <motion.div
                            key={statusMessage}
                            initial={{ opacity: 0, y: -10 }}
                            animate={{ opacity: 1, y: 0 }}
                            className={cn(
                                "text-center py-2 mb-3 rounded-lg font-bold uppercase tracking-wider text-sm",
                                dangerLevel > 0.85
                                    ? "bg-red-900/50 text-red-300"
                                    : dangerLevel > 0.6
                                        ? "bg-orange-900/50 text-orange-300"
                                        : "bg-zinc-800/50 text-zinc-300"
                            )}
                        >
                            {statusMessage}
                        </motion.div>

                        {/* Distance Gap Progress */}
                        <div className="space-y-2">
                            <div className="flex justify-between text-xs text-zinc-400">
                                <span className="flex items-center gap-1">
                                    <Skull className="w-3 h-3" />
                                    CAUGHT
                                </span>
                                <span className="font-mono text-white">
                                    {Math.round(chaseState.distanceGapMeters)}m gap
                                </span>
                                <span className="flex items-center gap-1">
                                    ESCAPE
                                    <Trophy className="w-3 h-3" />
                                </span>
                            </div>

                            {/* Styled Progress Track */}
                            <div className="h-6 bg-zinc-950/80 rounded-full overflow-hidden border border-zinc-800 relative shadow-inner">
                                {/* Danger zone indicator (Gradient) */}
                                <div className="absolute inset-y-0 left-0 w-[20%] bg-gradient-to-r from-red-900/60 to-transparent" />

                                {/* Track Ticks */}
                                <div className="absolute inset-0 flex justify-between px-2 items-center opacity-20">
                                    {[...Array(10)].map((_, i) => (
                                        <div key={i} className="w-px h-2 bg-white" />
                                    ))}
                                </div>

                                {/* Monster Position (Left Side / 0%) */}
                                <motion.div
                                    className="absolute left-1 top-1/2 -translate-y-1/2 text-lg z-10 filter drop-shadow-md"
                                    animate={{
                                        scale: dangerLevel > 0.8 ? [1, 1.2, 1] : 1,
                                        rotate: dangerLevel > 0.8 ? [0, -10, 10, 0] : 0
                                    }}
                                    transition={{ repeat: Infinity, duration: 2 }} // Breathing effect
                                >
                                    {chaseState.chaser.image}
                                </motion.div>

                                {/* Player Position Indicator - Moves Right -> Left as gap shrinks */}
                                {/* Wait, gap percentage: 100% = Safe, 0% = Caught. 
                                Visual logic: 
                                - Monster is at LEFT (0%). 
                                - Player is at RIGHT (100%) if safe?
                                - Usually: Distance Gap. 
                                Let's visualize: 
                                |[M]---------[P]-------| 
                                If gap shrinks, P moves closer to M.
                                Gap % = (CurrentGap / EscapeGap) * 100.
                                100% gap = Max distance (Start).
                                0% gap = Caught.
                                
                                So: 
                                Player should be at `GapPercentage` from Left? 
                                If Gap=100%, Player at 100% (Right).
                                If Gap=0%, Player at 0% (Left/Caught).
                            */}

                                <motion.div
                                    className="absolute top-0 bottom-0 bg-white/10"
                                    style={{
                                        left: 0,
                                        right: `${100 - gapPercentage}%`
                                        // This fills the space BETWEEN Monster (0) and Player (gap%)
                                        // Giving visual context to the "Gap"
                                    }}
                                />

                                {/* Player Icon */}
                                <motion.div
                                    className="absolute top-1/2 -translate-y-1/2 z-20 flex flex-col items-center"
                                    style={{ left: `${Math.min(96, Math.max(4, gapPercentage))}%` }}
                                    animate={{ left: `${Math.min(96, Math.max(4, gapPercentage))}%` }}
                                >
                                    <div className="relative">
                                        <Footprints className="w-5 h-5 text-cyan-400 drop-shadow-[0_0_8px_rgba(34,211,238,0.8)]" />
                                        <div className="absolute -bottom-1 left-1/2 -translate-x-1/2 w-8 h-1 bg-cyan-500/50 blur-sm rounded-full" />
                                    </div>
                                </motion.div>
                            </div>
                        </div>

                        {/* Current Pace Display */}
                        <div className="mt-3 flex justify-center gap-6 text-center">
                            <div>
                                <p className="text-xs text-zinc-500">Your Pace</p>
                                <p
                                    className={cn(
                                        "font-mono text-xl font-bold",
                                        currentPaceKph >= requiredPace ? "text-green-400" : "text-red-400"
                                    )}
                                >
                                    {currentPaceKph.toFixed(1)} km/h
                                </p>
                                <p className={cn("text-xs font-mono", paceDelta >= 0 ? "text-green-500" : "text-red-500")}>
                                    {paceDelta >= 0 ? "+" : ""}{paceDelta.toFixed(1)}
                                </p>
                            </div>
                            <div className="border-l border-zinc-700 pl-6">
                                <p className="text-xs text-zinc-400">Time</p>
                                <p className="font-mono text-xl font-bold text-white">
                                    {Math.floor(chaseState.elapsedSeconds / 60)}:
                                    {String(Math.floor(chaseState.elapsedSeconds % 60)).padStart(2, "0")}
                                </p>
                            </div>
                            <div className="border-l border-zinc-700 pl-6">
                                <p className="text-xs text-zinc-400">Distance</p>
                                <p className="font-mono text-xl font-bold text-cyan-400">
                                    {(chaseState.playerDistanceMeters / 1000).toFixed(2)} km
                                </p>
                            </div>
                        </div>
                    </div>
                </div>
            </motion.div>

            {/* Danger Overlay Flash (When damaged/caught imminent) */}
            <div className={cn(
                "absolute inset-0 z-40 bg-red-500/0 transition-all duration-300 pointer-events-none mix-blend-overlay",
                dangerLevel > 0.9 && "animate-pulse bg-red-500/20"
            )} />

            {/* Victory/Defeat Overlays */}
            <AnimatePresence>
                {chaseState.hasEscaped && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="fixed inset-0 bg-green-900/80 backdrop-blur-sm flex items-center justify-center z-[100] pointer-events-auto"
                    >
                        <motion.div
                            initial={{ scale: 0.5, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            className="text-center p-8"
                        >
                            <Trophy className="w-24 h-24 text-yellow-400 mx-auto mb-4" />
                            <h2 className="text-4xl font-bold text-white mb-2">ESCAPED!</h2>
                            <p className="text-xl text-green-200 mb-6">
                                You outran {chaseState.chaser.name}!
                            </p>
                            <button
                                onClick={onEscapedConfirm}
                                className="px-8 py-3 bg-green-600 hover:bg-green-500 text-white font-bold rounded-lg transition-colors"
                            >
                                Claim Rewards
                            </button>
                        </motion.div>
                    </motion.div>
                )}

                {chaseState.isCaught && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="fixed inset-0 bg-red-900/80 backdrop-blur-sm flex items-center justify-center z-[100] pointer-events-auto"
                    >
                        <motion.div
                            initial={{ scale: 0.5, opacity: 0 }}
                            animate={{ scale: 1, opacity: 1 }}
                            className="text-center p-8"
                        >
                            <div className="text-6xl mb-4">{chaseState.chaser.image}</div>
                            <h2 className="text-4xl font-bold text-white mb-2">CAUGHT!</h2>
                            <p className="text-xl text-red-200 mb-6">
                                {chaseState.chaser.name} has caught up!
                            </p>
                            <button
                                onClick={onCaughtConfirm}
                                className="px-8 py-3 bg-red-600 hover:bg-red-500 text-white font-bold rounded-lg transition-colors flex items-center gap-2 mx-auto"
                            >
                                <Zap className="w-5 h-5" />
                                Fight Back!
                            </button>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
}

import React, { useState } from "react";
import { motion, useMotionValue, useTransform, PanInfo } from "framer-motion";
import { ChevronDown, ChevronsRight, CheckCircle, PlayCircle } from "lucide-react";
import { Exercise } from "@/types";
import { cn } from "@/lib/utils";
import { SetRow } from "@/features/strength/SetRow";
import SetInput from "./SetInput";
import ForgeCard from "../../../components/ui/ForgeCard";
import { DemoVideoModal } from "@/components/ui/DemoVideoModal";
import { useRestTimer } from "@/hooks/useRestTimer";
import { useSetHistory } from "@/features/strength/hooks/useSetHistory";
import { ExerciseProgressChart } from "@/components/charts/ExerciseProgressChart";
import { getExerciseHistory } from "@/features/strength/actions/history";
import { LineChart, BarChart2 } from "lucide-react";
import PRBadge from "@/components/ui/PRBadge";
import { useMaxReps } from "@/hooks/useMaxReps";

import { SetData } from "@/actions/training/strength";

interface ExerciseViewProps {
  exercise: Exercise;
  isActive: boolean;
  isCompleted: boolean;
  onSetLog: (weight: number, reps: number, rpe: number) => void;
  onNotesChange?: (notes: string) => void;
  onSetUpdate?: (setIndex: number, updates: Partial<SetData>) => void;
}

const cardVariants = {
  inactive: { opacity: 0.5, scale: 0.95 },
  active: { opacity: 1, scale: 1 },
  completed: { opacity: 0.35, scale: 0.92 },
};

const ExerciseView: React.FC<ExerciseViewProps> = ({
  exercise,
  isActive,
  isCompleted,
  onSetLog,
  onNotesChange,
  onSetUpdate,
}) => {
  const activeSet = exercise.sets.find((s) => !s.completed);
  const allSetsCompleted = exercise.sets.every((s) => s.completed);
  const [showDemo, setShowDemo] = useState(false);
  const { start } = useRestTimer();

  // PR Tracking
  const { maxReps, isLoading: isMaxRepsLoading } = useMaxReps(
    exercise.id,
    activeSet?.weight // Auto-fetch when weight changes or active set changes
  );

  // Pre-fill history
  const { history } = useSetHistory(exercise.id, exercise.name);
  const [showHistory, setShowHistory] = useState(false);
  const [chartData, setChartData] = useState<any[]>([]);
  const [isChartLoading, setIsChartLoading] = useState(false);

  React.useEffect(() => {
    if (showHistory && chartData.length === 0) {
      setIsChartLoading(true);
      getExerciseHistory(exercise.id)
        .then(data => setChartData(data))
        .finally(() => setIsChartLoading(false));
    }
  }, [showHistory, exercise.id]);

  // Gestures
  const x = useMotionValue(0);
  const opacity = useTransform(x, [0, 100], [1, 0]);
  const bg = useTransform(x, [0, 100], ["rgba(0,0,0,0)", "rgba(34, 197, 94, 0.2)"]); // Green tint on swipe right

  const handleDragEnd = (event: MouseEvent | TouchEvent | PointerEvent, info: PanInfo) => {
    if (info.offset.x > 100 && isActive && activeSet) {
      // Haptic Feedback
      if (typeof navigator !== 'undefined' && navigator.vibrate) {
        navigator.vibrate(50);
      }
      // Swipe Right -> Complete Set
      onSetLog(activeSet.weight || 0, (typeof activeSet.reps === 'number' ? activeSet.reps : 0), activeSet.rpe || 8);
    }
  };

  const getAnimationState = () => {
    if (allSetsCompleted || isCompleted) return "completed";
    if (isActive) return "active";
    return "inactive";
  };

  // Helper to determine rest time based on set type
  const getRestTime = (type?: string, isWarmup?: boolean) => {
    if (isWarmup || type === "warmup") return 60;
    if (type === "failure") return 120;
    if (type === "myoreps") return 30; // Shorter rest
    return 90; // Normal
  };

  const handleSetLogWrapper = (weight: number, reps: number, rpe: number) => {
    onSetLog(weight, reps, rpe);

    // Auto-start timer if more sets remain, or even if done (cooldown)
    // Checking remaining sets from current active index onwards
    const remainingSets = exercise.sets.filter(s => !s.completed).length;

    if (remainingSets > 0) { // If we just finished a set, remaining count acts weird before update?
      // Actually activeSet is the one currently being logged.
      const restSeconds = getRestTime(activeSet?.setType, activeSet?.isWarmup);
      start(restSeconds);
    }
  };

  return (
    <motion.div
      variants={cardVariants}
      animate={getAnimationState()}
      initial="inactive"
      transition={{ duration: 0.5, ease: "easeInOut" }}
      layout
      style={{ x, backgroundColor: bg }}
      drag={isActive ? "x" : false}
      dragConstraints={{ left: 0, right: 0 }}
      dragElastic={0.1}
      onDragEnd={handleDragEnd}
    >
      <ForgeCard
        className={`transition-all duration-500 ${isActive ? "border-magma/80 shadow-glow-magma/40" : "border-white/10"}`}
      >
        {/* Swipe Hint - only on first active set */}
        {isActive && activeSet && !activeSet.completed && exercise.sets.filter(s => s.completed).length === 0 && (
          <div className="text-center text-[10px] text-zinc-500 mb-2 flex items-center justify-center gap-1 animate-pulse">
            <span>← Swipe right to complete →</span>
          </div>
        )}
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center gap-2">
            <h3 className="font-heading text-xl text-white tracking-wider">
              {exercise.name}
            </h3>
            {/* Demo Button */}
            <button
              onClick={(e) => {
                e.stopPropagation();
                setShowDemo(true);
              }}
              className="text-zinc-500 hover:text-magma transition-colors"
              title="Watch Demo"
            >
              <PlayCircle className="w-4 h-4" />
            </button>
            <button
              onClick={(e) => {
                e.stopPropagation();
                setShowHistory(!showHistory);
              }}
              className={cn(
                "transition-colors",
                showHistory ? "text-magma" : "text-zinc-500 hover:text-magma"
              )}
              title="View Progress"
            >
              <BarChart2 className="w-4 h-4" />
            </button>
          </div>

          <div className="flex items-center gap-2">
            {isActive && activeSet && (
              <PRBadge
                maxReps={maxReps}
                weight={activeSet.weight}
                isLoading={isMaxRepsLoading}
                size="sm"
              />
            )}
            {allSetsCompleted ? (
              <CheckCircle className="text-green-500" />
            ) : isActive ? (
              <ChevronsRight className="text-magma animate-pulse" />
            ) : (
              <ChevronDown className="text-forge-muted" />
            )}
          </div>
        </div>

        {/* Notes Field */}
        {isActive && (
          <div className="mb-4 animate-fade-in">
            <textarea
              placeholder="Workout notes..."
              value={exercise.notes || ""}
              onChange={(e) => onNotesChange?.(e.target.value)}
              className="w-full bg-black/20 text-zinc-400 text-sm p-2 rounded-md border border-white/5 focus:border-zinc-500 focus:outline-none resize-none h-16"
            />
          </div>
        )}

        {/* History Chart */}
        {showHistory && (
          <div className="mb-4 animate-fade-in">
            <ExerciseProgressChart data={chartData} isLoading={isChartLoading} />
          </div>
        )}

        <div className="space-y-2 mb-4">
          {exercise.sets.map((set, index) => (
            <SetRow
              key={`${exercise.id}-${set.id}-${index}`}
              set={{
                ...set,
                reps: typeof set.reps === "string" ? parseInt(set.reps) || 0 : set.reps,
                isWarmup: set.isWarmup ?? false,
              } as any}
              setNumber={index + 1}
              onChange={(updates) => onSetUpdate?.(index, updates)}
            />
          ))}
        </div>

        {isActive && activeSet && (
          <motion.div
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            <SetInput
              onSetLog={handleSetLogWrapper}
              targetReps={
                typeof activeSet.reps === "string" ? 0 : activeSet.reps || 0
              }
              targetRPE={activeSet.rpe || 8}
            />
          </motion.div>
        )}
      </ForgeCard>

      <DemoVideoModal
        isOpen={showDemo}
        onClose={() => setShowDemo(false)}
        exerciseName={exercise.name}
        videoUrl={exercise.demoUrl}
      />
    </motion.div>
  );
};

export default ExerciseView;

import React, { useEffect, useState, useRef } from "react";
import { Mic, MicOff, Brain, Volume2, X } from "lucide-react";
import { GoogleGenAI, LiveServerMessage, Modality } from "@google/genai";

interface GeminiLiveCoachProps {
  isOpen: boolean;
  onClose: () => void;
}

const GeminiLiveCoach: React.FC<GeminiLiveCoachProps> = ({
  isOpen,
  onClose,
}) => {
  const [isConnected, setIsConnected] = useState(false);
  const [isMuted, setIsMuted] = useState(false);
  const [status, setStatus] = useState("Initializing Neural Link...");

  // Audio Refs
  const audioContextRef = useRef<AudioContext | null>(null);
  const inputSourceRef = useRef<MediaStreamAudioSourceNode | null>(null);
  const processorRef = useRef<ScriptProcessorNode | null>(null);
  const streamRef = useRef<MediaStream | null>(null);
  const mountedRef = useRef(false);

  // Gemini Session
  const sessionPromiseRef = useRef<Promise<any> | null>(null);

  // Audio Playback Queue
  const nextStartTime = useRef<number>(0);
  const outputSources = useRef<Set<AudioBufferSourceNode>>(new Set());

  useEffect(() => {
    mountedRef.current = true;
    if (isOpen) {
      connectLive();
    } else {
      disconnectLive();
    }
    return () => {
      mountedRef.current = false;
      disconnectLive();
    };

  }, [isOpen]);

  const connectLive = async () => {
    if (!process.env.API_KEY) {
      if (mountedRef.current) setStatus("Error: No API Key");
      return;
    }

    try {
      const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

      // Setup Audio Context
      audioContextRef.current = new (
        window.AudioContext || (window as any).webkitAudioContext
      )({ sampleRate: 16000 });
      streamRef.current = await navigator.mediaDevices.getUserMedia({
        audio: true,
      });

      if (mountedRef.current) setStatus("Connecting to Spirit Realm...");

      // Connect to Gemini
      const sessionPromise = ai.live.connect({
        model: "gemini-2.5-flash-native-audio-preview-09-2025",
        callbacks: {
          onopen: () => {
            if (!mountedRef.current) return;
            setIsConnected(true);
            setStatus("Coach Online. Speaking...");

            // Start Input Stream
            if (!audioContextRef.current || !streamRef.current) return;

            inputSourceRef.current =
              audioContextRef.current.createMediaStreamSource(
                streamRef.current,
              );
            processorRef.current =
              audioContextRef.current.createScriptProcessor(4096, 1, 1);

            processorRef.current.onaudioprocess = (e) => {
              if (isMuted || !mountedRef.current) return;
              const inputData = e.inputBuffer.getChannelData(0);
              const pcmBlob = createBlob(inputData);

              sessionPromise.then((session) => {
                session.sendRealtimeInput({ media: pcmBlob });
              });
            };

            inputSourceRef.current.connect(processorRef.current);
            processorRef.current.connect(audioContextRef.current.destination);
          },
          onmessage: async (msg: LiveServerMessage) => {
            if (!mountedRef.current) return;
            const audioData =
              msg.serverContent?.modelTurn?.parts?.[0]?.inlineData?.data;
            if (audioData) {
              playAudioChunk(audioData);
            }
            if (msg.serverContent?.turnComplete) {
              setStatus("Listening...");
            }
          },
          onclose: () => {
            if (mountedRef.current) {
              setIsConnected(false);
              setStatus("Connection Closed");
            }
          },
          onerror: (e) => {
            console.error(e);
            if (mountedRef.current) setStatus("Neural Link Severed");
          },
        },
        config: {
          responseModalities: [Modality.AUDIO],
          systemInstruction:
            "You are a hardcore strength coach. Brief, intense, motivational commands only. If the user complains, tell them to embrace the pain.",
        },
      });

      sessionPromiseRef.current = sessionPromise;
    } catch (e) {
      console.error(e);
      if (mountedRef.current) setStatus("Connection Failed");
    }
  };

  const disconnectLive = () => {
    // 1. Close Gemini Session
    if (sessionPromiseRef.current) {
      sessionPromiseRef.current.then((session) => {
        try {
          session.close();
        } catch (e) {
          console.warn("Session close error", e);
        }
      });
      sessionPromiseRef.current = null;
    }

    // 2. Stop Audio Tracks (Mic)
    if (streamRef.current) {
      streamRef.current.getTracks().forEach((t) => t.stop());
      streamRef.current = null;
    }

    // 3. Close Audio Context & Nodes
    if (audioContextRef.current) {
      const ctx = audioContextRef.current;

      if (inputSourceRef.current) {
        try {
          inputSourceRef.current.disconnect();
        } catch (e) {}
        inputSourceRef.current = null;
      }
      if (processorRef.current) {
        try {
          processorRef.current.disconnect();
          processorRef.current.onaudioprocess = null;
        } catch (e) {}
        processorRef.current = null;
      }

      if (ctx.state !== "closed") {
        ctx.close().catch(console.error);
      }
      audioContextRef.current = null;
    }

    // 4. Clear Queued Audio
    outputSources.current.forEach((s) => {
      try {
        s.stop();
      } catch (e) {}
    });
    outputSources.current.clear();

    if (mountedRef.current) {
      setIsConnected(false);
      setStatus("Neural Link Terminated");
    }
  };

  // Helper: Float32 to PCM Blob
  const createBlob = (data: Float32Array) => {
    const l = data.length;
    const int16 = new Int16Array(l);
    for (let i = 0; i < l; i++) {
      int16[i] = data[i] * 32768;
    }
    let binary = "";
    const bytes = new Uint8Array(int16.buffer);
    const len = bytes.byteLength;
    for (let i = 0; i < len; i++) {
      binary += String.fromCharCode(bytes[i]);
    }
    return {
      data: btoa(binary),
      mimeType: "audio/pcm;rate=16000",
    };
  };

  // Helper: Decode and Play
  const playAudioChunk = async (base64: string) => {
    if (!audioContextRef.current || audioContextRef.current.state === "closed")
      return;

    try {
      const binaryString = atob(base64);
      const len = binaryString.length;
      const bytes = new Uint8Array(len);
      for (let i = 0; i < len; i++) {
        bytes[i] = binaryString.charCodeAt(i);
      }

      const dataInt16 = new Int16Array(bytes.buffer);
      const float32 = new Float32Array(dataInt16.length);
      for (let i = 0; i < dataInt16.length; i++) {
        float32[i] = dataInt16[i] / 32768.0;
      }

      const buffer = audioContextRef.current.createBuffer(
        1,
        float32.length,
        24000,
      );
      buffer.copyToChannel(float32, 0);

      const source = audioContextRef.current.createBufferSource();
      source.buffer = buffer;
      source.connect(audioContextRef.current.destination);

      const now = audioContextRef.current.currentTime;
      const start = Math.max(now, nextStartTime.current);
      source.start(start);
      nextStartTime.current = start + buffer.duration;

      outputSources.current.add(source);
      source.onended = () => outputSources.current.delete(source);
    } catch (e) {
      console.error("Audio Decode Error", e);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed bottom-4 left-4 z-50 flex flex-col gap-2 animate-slide-up">
      <div className="bg-black/90 border border-purple-500 rounded-lg p-4 shadow-[0_0_20px_rgba(168,85,247,0.3)] w-72 backdrop-blur-md">
        <div className="flex justify-between items-center mb-3">
          <div className="flex items-center gap-2 text-purple-400">
            <Brain className="w-5 h-5 animate-pulse" />
            <span className="font-bold uppercase text-xs tracking-widest">
              Gemini Coach
            </span>
          </div>
          <button onClick={onClose} className="text-zinc-500 hover:text-white">
            <X className="w-4 h-4" />
          </button>
        </div>

        <div className="h-24 bg-zinc-900 rounded border border-zinc-800 flex items-center justify-center relative overflow-hidden">
          {/* Visualizer (Fake) */}
          <div className="flex gap-1 items-end h-10">
            {[...Array(8)].map((_, i) => (
              <div
                key={i}
                className={`w-2 bg-purple-500 rounded-t ${isConnected ? "animate-bounce" : "h-1"}`}
                style={{ animationDelay: `${i * 0.1}s`, height: "50%" }}
              ></div>
            ))}
          </div>
          <div className="absolute top-2 left-2 text-[9px] text-zinc-500 font-mono">
            STATUS: {status}
          </div>
        </div>

        <div className="flex gap-2 mt-3">
          <button
            onClick={() => setIsMuted(!isMuted)}
            className={`flex-1 py-3 rounded border font-bold uppercase text-xs flex items-center justify-center gap-2 ${isMuted ? "bg-red-900/50 border-red-500 text-red-400" : "bg-purple-900/50 border-purple-500 text-white"}`}
          >
            {isMuted ? (
              <MicOff className="w-4 h-4" />
            ) : (
              <Mic className="w-4 h-4" />
            )}
            {isMuted ? "Muted" : "Live"}
          </button>
        </div>
      </div>
    </div>
  );
};

