"use client";

import { create } from "zustand";
import { persist, createJSONStorage } from "zustand/middleware";
import { playSound } from "@/utils"; // Assuming sound util exists or I'll standardise it

interface RestTimerState {
  isActive: boolean;
  endTime: number | null; // Unix timestamp
  initialSeconds: number;
  totalSeconds: number; // For progress bar calculation
  start: (seconds: number) => void;
  stop: () => void;
  addTime: (seconds: number) => void;
  reset: () => void;
  check: () => void; // call in component loop
  timeLeft: number;
  hasFinished: boolean;
}

export const useRestTimer = create<RestTimerState>()(
  persist(
    (set, get) => ({
      isActive: false,
      endTime: null,
      initialSeconds: 90,
      totalSeconds: 90,
      timeLeft: 90,
      hasFinished: false,

      start: (seconds: number) => {
        const now = Date.now();
        set({
          isActive: true,
          initialSeconds: seconds,
          totalSeconds: seconds,
          endTime: now + seconds * 1000,
          timeLeft: seconds,
          hasFinished: false,
        });
      },

      stop: () => {
        set({ isActive: false, endTime: null, hasFinished: false });
      },

      addTime: (seconds: number) => {
        const { endTime, isActive } = get();
        if (!isActive || !endTime) return;
        const newEndTime = endTime + seconds * 1000;
        set({ endTime: newEndTime });
        // Update immediately
        const now = Date.now();
        const diff = Math.ceil((newEndTime - now) / 1000);
        set({ timeLeft: Math.max(0, diff) });
      },

      reset: () => {
        set({ isActive: false, endTime: null, timeLeft: 90, hasFinished: false });
      },

      check: () => {
        const { endTime, isActive, hasFinished } = get();
        if (isActive && endTime) {
          const now = Date.now();
          const diff = Math.ceil((endTime - now) / 1000);

          if (diff <= 0) {
            set({ isActive: false, endTime: null, timeLeft: 0, hasFinished: true });
          } else {
            set({ timeLeft: diff });
          }
        }
      }
    }),
    {
      name: "ironforge-rest-timer",
      storage: createJSONStorage(() => localStorage),
      // Only persist essential state to restore
      partialize: (state) => ({
        isActive: state.isActive,
        endTime: state.endTime,
        initialSeconds: state.initialSeconds,
        totalSeconds: state.totalSeconds,
        hasFinished: state.hasFinished
      }),
    }
  )
);

"use client";

import {
  createContext,
  useContext,
  useEffect,
  useState,
  ReactNode,
} from "react";

type Theme = "dark" | "light" | "system";

interface ThemeContextType {
  theme: Theme;
  resolvedTheme: "dark" | "light";
  setTheme: (theme: Theme) => void;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

const STORAGE_KEY = "ironforge-theme";

export function ThemeProvider({ children }: { children: ReactNode }) {
  const [theme, setThemeState] = useState<Theme>("system");
  const [resolvedTheme, setResolvedTheme] = useState<"dark" | "light">("dark");

  useEffect(() => {
    // Load saved preference
    const saved = localStorage.getItem(STORAGE_KEY) as Theme | null;
    if (saved && ["dark", "light", "system"].includes(saved)) {
      setThemeState(saved);
    }
  }, []);

  useEffect(() => {
    // Resolve system preference
    const mediaQuery = window.matchMedia("(prefers-color-scheme: dark)");

    const updateResolvedTheme = () => {
      if (theme === "system") {
        setResolvedTheme(mediaQuery.matches ? "dark" : "light");
      } else {
        setResolvedTheme(theme);
      }
    };

    updateResolvedTheme();
    mediaQuery.addEventListener("change", updateResolvedTheme);

    return () => mediaQuery.removeEventListener("change", updateResolvedTheme);
  }, [theme]);

  useEffect(() => {
    // Apply theme to document
    const root = document.documentElement;
    root.classList.remove("dark", "light");
    root.classList.add(resolvedTheme);

    // Update meta theme-color
    const metaTheme = document.querySelector('meta[name="theme-color"]');
    if (metaTheme) {
      metaTheme.setAttribute(
        "content",
        resolvedTheme === "dark" ? "#0f172a" : "#f8fafc",
      );
    }
  }, [resolvedTheme]);

  const setTheme = (newTheme: Theme) => {
    setThemeState(newTheme);
    localStorage.setItem(STORAGE_KEY, newTheme);
  };

  const toggleTheme = () => {
    setTheme(resolvedTheme === "dark" ? "light" : "dark");
  };

  return (
    <ThemeContext.Provider
      value={{ theme, resolvedTheme, setTheme, toggleTheme }}
    >
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error("useTheme must be used within ThemeProvider");
  }
  return context;
}

// Theme Toggle Component
import { Moon, Sun, Monitor } from "lucide-react";

export function ThemeToggle() {
  const { theme, setTheme, resolvedTheme, toggleTheme } = useTheme();

  return (
    <button
      onClick={toggleTheme}
      className="p-2 rounded-lg bg-slate-200 dark:bg-slate-800 hover:bg-slate-300 dark:hover:bg-slate-700 transition-colors"
      title={`Switch to ${resolvedTheme === "dark" ? "light" : "dark"} mode`}
    >
      {resolvedTheme === "dark" ? (
        <Sun className="w-5 h-5 text-yellow-500" />
      ) : (
        <Moon className="w-5 h-5 text-slate-700" />
      )}
    </button>
  );
}

// Theme Selector (multiple options)
export function ThemeSelector() {
  const { theme, setTheme } = useTheme();

  const options: { value: Theme; label: string; icon: typeof Sun }[] = [
    { value: "light", label: "Light", icon: Sun },
    { value: "dark", label: "Dark", icon: Moon },
    { value: "system", label: "System", icon: Monitor },
  ];

  return (
    <div className="flex gap-2">
      {options.map((option) => {
        const Icon = option.icon;
        const isActive = theme === option.value;

        return (
          <button
            key={option.value}
            onClick={() => setTheme(option.value)}
            className={`flex items-center gap-2 px-3 py-2 rounded-lg transition-colors ${
              isActive
                ? "bg-amber-500 text-white"
                : "bg-slate-200 dark:bg-slate-800 hover:bg-slate-300 dark:hover:bg-slate-700"
            }`}
          >
            <Icon className="w-4 h-4" />
            <span>{option.label}</span>
          </button>
        );
      })}
    </div>
  );
}

