    MuscleHeatmap
} from '@/types/goals';
import { WORKOUT_LIBRARY } from '@/data/workouts';
import { WorkoutDefinition, MuscleGroup } from '@/types/training';
import { HEVY_EXERCISE_MAP } from '@/data/hevyExercises';


// Phase Allocation Matrix
const PHASE_ALLOCATION: Record<MacroPhase, { strength: number; cardio: number; mobility: number; description: string }> = {
    CARDIO_BUILD: {
        strength: 0.20,
        cardio: 0.70,
        mobility: 0.10,
        description: "Building aerobic engine. Strength maintenance only.",
    },
    STRENGTH_BUILD: {
        strength: 0.70,
        cardio: 0.20,
        mobility: 0.10,
        description: "Building strength. Light cardio for recovery.",
    },
    BALANCED: {
        strength: 0.45,
        cardio: 0.45,
        mobility: 0.10,
        description: "Maintaining both systems. No peak adaptation.",
    },
    PEAK: {
        strength: 0.30,
        cardio: 0.50,
        mobility: 0.20,
        description: "Competition prep. Reduced volume, high intensity.",
    },
    DELOAD: {
        strength: 0.25,
        cardio: 0.25,
        mobility: 0.50,
        description: "Recovery week. Focus on mobility and light movement.",
    },
};

export class GoalPriorityEngine {

    /**
     * Determines optimal phase based on goal priorities and current state.
     */
    static selectPhase(
        manifest: WardensManifest,
        metrics: SystemMetrics
    ): MacroPhase {

        // 1. Safety Override - Always check first
        if (this.needsDeload(metrics)) return "DELOAD";

        // 2. Deadline Check - Peak if event within 2 weeks
        const upcomingDeadline = this.getUpcomingDeadline(manifest.goals);
        if (upcomingDeadline && upcomingDeadline.daysUntil <= 14) {
            return "PEAK";
        }

        // 3. Post-Deload - Always return to cardio (build base first)
        if (manifest.phase === "DELOAD" && metrics.tsb > 0) {
            return "CARDIO_BUILD";
        }

        // 4. Phase Rotation - Based on primary goal
        const primaryGoal = manifest.goals[0]?.goal;

        if (this.isCardioGoal(primaryGoal)) {
            return this.shouldTransition(manifest, metrics)
                ? "STRENGTH_BUILD"  // Recovery rotation
                : "CARDIO_BUILD";
        }

        if (this.isStrengthGoal(primaryGoal)) {
            return this.shouldTransition(manifest, metrics)
                ? "CARDIO_BUILD"    // Recovery rotation
                : "STRENGTH_BUILD";
        }

        return "BALANCED";
    }

    /**
     * Hysteresis-based transition logic.
     * Requires 4+ weeks in phase AND progress stall.
     */
    static shouldTransition(
        manifest: WardensManifest,
        metrics: SystemMetrics
    ): boolean {
        if (manifest.phaseWeek < 4) return false; // Minimum phase duration
        if (metrics.consecutiveStalls >= 3) return true; // Plateau detected
        if (metrics.atl > metrics.ctl * 1.3) return true; // Overreaching
        return false;
    }

    static needsDeload(metrics: SystemMetrics): boolean {
        // Fallback: If critical data missing, use subjective fail-safes
        if (metrics.hrv === null || metrics.hrv === undefined || metrics.tsb === null || metrics.tsb === undefined) {
            return (
                metrics.soreness > 7 ||
                metrics.mood === "EXHAUSTED" ||
                metrics.sleepScore < 30
            );
        }

        // Relative Thresholds
        const hrvBaseline = metrics.hrvBaseline || 50;
        const isCrashing = metrics.hrv < (hrvBaseline * 0.75);
        const isTanked = metrics.tsb < -40;
        const isSpiked = metrics.acwr > 1.5;

        return isCrashing || isTanked || isSpiked;
    }

    static isCardioGoal(goal: TrainingGoal): boolean {
        return ["VO2MAX", "FTP_BIKE", "FTP_RUN", "ENDURANCE", "FITNESS"].includes(goal);
    }

    static isStrengthGoal(goal: TrainingGoal): boolean {
        return ["STRENGTH", "HYPERTROPHY", "BODY_COMP"].includes(goal);
    }

    private static getUpcomingDeadline(goals: WardensManifest['goals']): { date: Date, daysUntil: number } | null {
        const deadlines = goals
            .filter(g => g.deadline)
            .map(g => ({ date: g.deadline!, daysUntil: Math.ceil((new Date(g.deadline!).getTime() - Date.now()) / (1000 * 60 * 60 * 24)) }))
            .filter(d => d.daysUntil >= 0)
            .sort((a, b) => a.daysUntil - b.daysUntil);

        return deadlines[0] || null;
    }

    /**
     * Calculates concrete weekly targets based on phase and goals.
     */
    static calculateWeeklyTargets(
        manifest: WardensManifest,
        phase: MacroPhase,
        metrics: SystemMetrics
    ): WeeklyTargets {

        const allocation = PHASE_ALLOCATION[phase];
        const baseHours = this.getBaseHours(metrics); // ~6-12h/week

        // Base allocation
        let strengthHours = baseHours * allocation.strength;
        let cardioHours = baseHours * allocation.cardio;
        let mobilityHours = baseHours * allocation.mobility;

        // Apply bio-modifiers
        const modifier = this.getBioModifier(metrics);
        strengthHours *= modifier;
        cardioHours *= modifier; // Note: Specs says * modifier for both, let's stick to that.

        // Apply Tapering for PEAK phase
        if (phase === "PEAK") {
            const taperMult = Math.max(0.5, 1.0 - (manifest.phaseWeek * 0.1)); // Reduce 10% per week
            strengthHours *= taperMult;
            cardioHours *= taperMult;
            // Note: Mobility not tapered
        }

        return {
            strengthHours: Number(strengthHours.toFixed(1)),
            cardioHours: Number(cardioHours.toFixed(1)),
            mobilityHours: Number(mobilityHours.toFixed(1)),
            primaryFocus: manifest.goals[0]?.goal || "FITNESS",
            phaseDescription: allocation.description
        };
    }

    private static getBaseHours(metrics: SystemMetrics): number {
        // Simple logic based on CTL (Fitness)
        // CTL 0-40: 4h
        // CTL 40-70: 6h
        // CTL 70-100: 9h
        // CTL 100+: 12h
        if (metrics.ctl > 100) return 12;
        if (metrics.ctl > 70) return 9;
        if (metrics.ctl > 40) return 6;
        return 4;
    }

    private static getBioModifier(metrics: SystemMetrics): number {
        // Reduce volume if recovery is poor
        if (metrics.sleepScore < 50) return 0.8;
        if (metrics.soreness > 6) return 0.85;
        if (metrics.tsb < -20) return 0.9;

        // Increase if primed? 
        if (metrics.tsb > 10 && metrics.sleepScore > 85) return 1.1;

        return 1.0;
    }

    /**
     * Selects the optimal workout from the library based on phase, budget, and muscle gaps.
     */
    static selectWorkout(
        manifest: WardensManifest,
        phase: MacroPhase,
        budget: DailyResourceBudget,
        heatmap?: MuscleHeatmap, // Optional, primarily for STRENGTH workouts
        preferences?: {
            maxDuration?: number;
            preferredTypes?: string[];
            availableEquipment?: string[]; // Todo: Integrate with The Armory
        },
        allowBudgetOverride?: boolean
    ): WorkoutDefinition[] {

        // 1. Filter by phase priority
        let candidates = WORKOUT_LIBRARY.filter(w =>
            this.matchesPhase(w, phase)
        );

        // 2. Filter by preferences
        if (preferences) {
            if (preferences.maxDuration) {
                candidates = candidates.filter(w => w.durationMin <= preferences.maxDuration!);
            }
            if (preferences.preferredTypes && preferences.preferredTypes.length > 0) {
                candidates = candidates.filter(w => preferences.preferredTypes!.includes(w.type));
            }
        }

        // 3. Filter by budget (strict unless override)
        if (!allowBudgetOverride) {
            candidates = candidates.filter(w =>
                (w.resourceCost.CNS ?? 0) <= budget.cns &&
                (w.resourceCost.MUSCULAR ?? 0) <= budget.muscular &&
                (w.resourceCost.METABOLIC ?? 0) <= budget.metabolic
            );
        }

        // 4. Sort by "fit score" (higher = better match)
        candidates.sort((a, b) =>
            this.calculateFitScore(b, phase, heatmap, manifest) -
            this.calculateFitScore(a, phase, heatmap, manifest)
        );

        // Return top 3 recommendations
        return candidates.slice(0, 3);
    }

    private static matchesPhase(w: WorkoutDefinition, phase: MacroPhase): boolean {
        if (phase === 'CARDIO_BUILD') {
            return ['RUN', 'BIKE', 'SWIM'].includes(w.type);
        }
        if (phase === 'STRENGTH_BUILD') {
            return w.type === 'STRENGTH';
        }
        if (phase === 'DELOAD') {
            return w.intensity === 'LOW' || w.type === 'MOBILITY';
        }
        if (phase === 'PEAK') {
            // Peak logic: High intensity, lower volume? 
            // For now, allow everything but prioritize intensity in scoring
            return true;
        }
        return true; // BALANCED: all types allowed
    }

    private static calculateFitScore(
        w: WorkoutDefinition,
        phase: MacroPhase,
        heatmap: MuscleHeatmap | undefined,
        manifest: WardensManifest
    ): number {
        let score = 100;

        // Bonus for matching recommended path
        // Assuming current path is derived from phase or manifest goals? 
        // For now, let's just check if any recommended path matches a known user path preference if we had one.
        // Actually, manifest doesn't explicitly store "Path", but goals implicate it.
        // Let's rely on simple Phase matching for now.

        // Phase specific bonuses
        if (phase === 'PEAK' && w.intensity === 'HIGH') score += 20;
        if (phase === 'DELOAD' && w.intensity === 'LOW') score += 20;

        // Bonus for filling heatmap gaps (strength workouts only)
        if (w.type === 'STRENGTH' && w.exercises && heatmap) {
            const targetedMuscles = w.exercises.map(e => this.getMuscleForExercise(e.id));

            // Find unique muscles targeted that are in MV (Maintenance) or MEV (Min Effective)
            // We want to push them to MAV (Max Adaptive).
            const uniqueMuscles = Array.from(new Set(targetedMuscles)).filter(Boolean) as MuscleGroup[];

            let gapFillScore = 0;
            uniqueMuscles.forEach(m => {
                const status = heatmap[m]?.status;
                if (status === 'MV' || status === 'MEV') {
                    gapFillScore += 10;
                }
            });
            score += gapFillScore;
        }

        return score;
    }

    private static getMuscleForExercise(exerciseIdOrName: string): MuscleGroup | null {
        // Try direct map
        let muscle = HEVY_EXERCISE_MAP[exerciseIdOrName];

        // If not found, try generic matches (naive implementation)
        if (!muscle) {
            // Fallbacks for known IDs in workouts.ts if they differ from Hevy names
            return null;
        }

        // Map Hevy lowercase to MuscleGroup uppercase
        const mapping: Record<string, MuscleGroup> = {
            "quadriceps": "QUADS",
            "hamstrings": "HAMS",
            "glutes": "GLUTES",
            "chest": "CHEST",
            "upper_back": "BACK",
            "lats": "BACK", // or separate
            "lower_back": "BACK",
            "shoulders": "SHOULDERS",
            "biceps": "BICEPS",
            "triceps": "TRICEPS",
            "abdominals": "ABS",
            "calves": "CALVES",
            "forearms": "BICEPS" // Rough approx
        };

        return mapping[muscle] || null;
    }
}

import { calculateWeeklyVolume } from "../utils/volumeCalculator";
import { auditWeaknesses } from "../utils/weaknessAuditor";
import { StorageService } from "./storage";
import { AuditReport } from "../types/auditor";
import { getWeeklyMobilityLogs } from "@/actions/mobility/logMobilityAction";
import { auditMobility } from "./MobilityAuditor";

/**
 * Auditor Orchestrator
 * Coordinates data fetching, processing, and storage for the Weakness Auditor system.
 * 
 * DATA SOURCE: IronForge PostgreSQL (strength logs from IronMines)
 * NOTE: Hevy integration has been removed per data-source-reconciliation.md
 */

const HISTORY_DEPTH = 30; // Fetch last 30 workouts to ensure full week coverage

/**
 * Runs a full audit cycle:
 * 1. Fetches recent workout history from IronForge DB
 * 2. Calculates weekly volume per muscle group
 * 3. Analyzes weaknesses and imbalances
 * 4. Caches the report locally
 *
 * @param forceRefresh - If true, ignores cache and forces a new fetch
 * @returns The generated AuditReport
 */
export const runFullAudit = async (
  forceRefresh: boolean = false,
  userId?: string,
  prefetchedHistory?: any[],
): Promise<AuditReport> => {
  // 1. Check cache first
  if (!forceRefresh && !prefetchedHistory) {
    const cached = await StorageService.getLatestAuditorReport();
    if (cached) {
      const reportAge = Date.now() - new Date((cached as any).timestamp).getTime();
      if (reportAge < 1000 * 60 * 60) {
        console.log("Using cached Auditor Report");
        return cached as any as AuditReport;
      }
    }
  }

  let history: any[] = [];

  if (prefetchedHistory && prefetchedHistory.length > 0) {
    // Use injected history (e.g. from Demo Mode or already fetched)
    console.log("Orchestrator: Using prefetched history...");
    history = prefetchedHistory;
  } else if (userId) {
    // TODO: Implement getIronForgeStrengthLogs(userId, HISTORY_DEPTH)
    // This action should query the strength_logs table for the user's recent workouts
    // and transform them into the format expected by calculateWeeklyVolume.
    console.warn("Orchestrator: getIronForgeStrengthLogs not yet implemented. Returning empty history.");
    history = [];

    // Placeholder for future implementation:
    // const result = await getIronForgeStrengthLogs(userId, HISTORY_DEPTH);
    // history = result.workouts;
  } else {
    console.warn("Orchestrator: No userId provided and no prefetched history. Returning empty report.");
    history = [];
  }

  // 3. Transform Data
  console.log(
    `Orchestrator: Calculating volumes for ${history.length} workouts...`,
  );
  const volumes = calculateWeeklyVolume(history);

  // 4. Analyze
  console.log("Orchestrator: Running Auditor analysis...");
  const report = auditWeaknesses(volumes);

  // Mobility Audit
  if (userId) {
    try {
      const mobilityLogs = await getWeeklyMobilityLogs(); // Use implicit auth
      if (mobilityLogs.success && mobilityLogs.data) {
        const mobilityReport = auditMobility(mobilityLogs.data.logs);
        report.mobility = mobilityReport;
      }
    } catch (e) {
      console.error("Failed to audit mobility", e);
    }
  }

  // 5. Persist
  console.log("Orchestrator: Caching report...");
  // Only save if we are in a client environment capable of storage
  if (typeof window !== "undefined") {
    // @ts-ignore
    await StorageService.saveAuditorReport(report);
  }

  return report;
};

import { createClient } from "@supabase/supabase-js";
import { type ActiveSession, type SessionParticipant } from "@prisma/client";

// Initialize client safely (handle missing env vars in CI/Test)
const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL || "https://placeholder.supabase.co";
const supabaseKey = process.env.NEXT_PUBLIC_SUPABASE_ANON_KEY || "placeholder";
const supabase = createClient(supabaseUrl, supabaseKey);

export interface CoOpSession extends ActiveSession {
    participants: SessionParticipant[];
}

export const CoOpService = {
    /**
     * Get active session for user (restoration)
     */
    async getActiveSession(userId: string): Promise<CoOpSession | null> {
        // E2E Mock
        if (typeof window !== 'undefined' && (window as any).__mockCoOpSession) {
            return (window as any).__mockCoOpSession;
        }

        const { data, error } = await supabase
            .from("active_sessions")
            .select(`
                *,
                participants:session_participants(*)
            `)
            .eq("participants.user_id", userId)
            // Note: This query is a bit simplified; typically we query session_participants first
            .maybeSingle(); // Use maybeSingle to avoid 406 on multiple matches (though strict relation should prevent)

        // Better approach for Supabase: Query participants table then join
        if (!data) {
            const { data: partData } = await supabase
                .from("session_participants")
                .select("session_id")
                .eq("user_id", userId)
                .maybeSingle();

            if (partData) {
                const { data: sessionData } = await supabase
                    .from("active_sessions")
                    .select(`*, participants:session_participants(*)`)
                    .eq("id", partData.session_id)
                    .single();
                return sessionData as any as CoOpSession;
            }
            return null;
        }

        const sessionData = data as any;
        return {
            ...sessionData,
            hostId: sessionData.host_id,
            workoutName: sessionData.workout_name,
            maxParticipants: sessionData.max_participants,
            inviteCode: sessionData.invite_code,
            participants: (sessionData.participants || []).map((p: any) => ({
                ...p,
                userId: p.user_id,
                heroName: p.hero_name,
                sessionId: p.session_id,
                lastHeartbeat: p.last_heartbeat,
                joinedAt: p.joined_at
            }))
        } as CoOpSession;
    },

    /**
     * Create a new Co-Op session
     */
    async createSession(hostId: string, workoutName?: string): Promise<string | null> {
        // E2E Mock
        if (typeof window !== 'undefined' && (window as any).__mockInviteCode) {
            return "test-session-id";
        }

        // 1. Generate unique 6-char code
        const inviteCode = Math.random().toString(36).substring(2, 8).toUpperCase();

        const { data, error } = await supabase
            .from("active_sessions")
            .insert({
                host_id: hostId,
                status: "waiting",
                workout_name: workoutName,
                invite_code: inviteCode,
                max_participants: 4
            })
            .select("id")
            .single();

        if (error) {
            console.error("Failed to create session:", error);
            return null;
        }

        // Auto-join host
        await this.joinSession(data.id, hostId, "Host");

        return data.id;
    },

    /**
     * Join an existing session
     */
    async joinSession(sessionId: string, userId: string, heroName?: string) {
        // E2E Mock
        if (typeof window !== 'undefined' && (window as any).__mockCoOpSession) {
            return;
        }

        const { error } = await supabase
            .from("session_participants")
            .insert({
                session_id: sessionId,
                user_id: userId,
                hero_name: heroName,
                status: "active"
            });

        if (error) {
            console.error("Failed to join session:", error);
            throw error;
        }
    },

    /**
     * List available waiting sessions
     */
    async listSessions(): Promise<CoOpSession[]> {
        // E2E Mock
        if (typeof window !== 'undefined' && (window as any).__mockSessions) {
            return (window as any).__mockSessions;
        }

        const { data, error } = await supabase
            .from("active_sessions")
            .select(`
        *,
        participants:session_participants(*)
      `)
            .eq("status", "waiting")
            .order("created_at", { ascending: false });

        if (error) {
            console.error("Failed to list sessions:", error);
            return [];
        }

        // Type casting needed because Prisma types vs Supabase JSON result
        const raw = data as any[];
        return raw.map(s => ({
            ...s,
            hostId: s.host_id,
            workoutName: s.workout_name,
            maxParticipants: s.max_participants,
            inviteCode: s.invite_code,
            participants: (s.participants || []).map((p: any) => ({
                ...p,
                userId: p.user_id,
                heroName: p.hero_name,
                sessionId: p.session_id,
                lastHeartbeat: p.last_heartbeat,
                joinedAt: p.joined_at
            }))
        })) as CoOpSession[];
    },

    /**
     * Leave a session
     */
    async leaveSession(sessionId: string, userId: string) {
        // E2E Mock
        if (typeof window !== 'undefined' && (window as any).__mockCoOpSession) {
            return;
        }

        await supabase
            .from("session_participants")
            .delete()
            .match({ session_id: sessionId, user_id: userId });

        // If host leaves, arguably we should close the session or migrate host
        // For MVP 15/10: Just remove participant.
    },

    /**
     * Get realtime subscription channel
     */
    subscribeToSession(sessionId: string, onUpdate: (payload: any) => void) {
        // E2E Mock
        if (typeof window !== 'undefined' && (window as any).__mockCoOpSession) {
            return { unsubscribe: () => { } };
        }

        return supabase
            .channel(`session:${sessionId}`)
            .on(
                'postgres_changes',
                { event: '*', schema: 'public', table: 'session_participants', filter: `session_id=eq.${sessionId}` },
                (payload) => onUpdate(payload)
            )
            .subscribe();
    },

    // --- GHOST MODE ---

    /**
     * Broadcast a "Ghost Event" (Set Completion, Rep, etc.) to squad
     */
    broadcastGhostEvent(sessionId: string, event: GhostEvent) {
        if (typeof window !== 'undefined' && (window as any).__mockGhostEvents) return Promise.resolve();

        return supabase.channel(`ghost:${sessionId}`).send({
            type: 'broadcast',
            event: 'ghost_event',
            payload: event
        });
    },

    /**
     * Subscribe to Ghost Events in a Session
     */
    subscribeToGhostEvents(sessionId: string, onEvent: (event: GhostEvent) => void) {
        // E2E Mock - Dispatch mock events if present
        if (typeof window !== 'undefined' && (window as any).__mockGhostEvents) {
            // Dispatch events on a slight delay to simulate realtime
            setTimeout(() => {
                (window as any).__mockGhostEvents.forEach((e: GhostEvent) => onEvent(e));
            }, 500);
            return { unsubscribe: () => { } };
        }

        return supabase
            .channel(`ghost:${sessionId}`)
            .on('broadcast', { event: 'ghost_event' }, (payload) => {
                onEvent(payload.payload as GhostEvent);
            })
            .subscribe();
    }
};

// --- TYPES ---

export interface GhostEvent {
    type: 'SET_COMPLETE' | 'REP' | 'PR' | 'BERSERKER';
    userId: string;
    heroName: string;
    exerciseName?: string;
    weight?: number;
    reps?: number;
    damage?: number;
    timestamp: number;
}

import { Monster } from "@/types";
import {
    ChaseState,
    ChaseDifficulty,
    ChaseConfig,
    CHASE_DIFFICULTY_PRESETS,
} from "@/types/chase";
import { MONSTERS } from "@/data/gameData";

/**
 * ChaseEngine - Handles chase mode logic for cardio sessions
 *
 * The player runs to maintain distance from a pursuing monster.
 * If caught, combat is triggered. If escaped, rewards are given.
 */
export class ChaseEngine {
    /**
     * Get all monsters that have chase capability (chaseSpeedKph defined)
     */
    static getChaserMonsters(): Monster[] {
        return MONSTERS.filter((m) => m.chaseSpeedKph !== undefined);
    }

    /**
     * Get a random chaser monster
     */
    static getRandomChaser(): Monster | null {
        const chasers = this.getChaserMonsters();
        if (chasers.length === 0) {
            // Fallback: use any monster with a default chase speed
            const fallback = MONSTERS[0];
            if (fallback) {
                return { ...fallback, chaseSpeedKph: 8 };
            }
            return null;
        }
        return chasers[Math.floor(Math.random() * chasers.length)];
    }

    /**
     * Initialize a new chase session
     */
    static initializeChase(
        monster: Monster,
        difficulty: ChaseDifficulty = "normal"
    ): ChaseState {
        const config = CHASE_DIFFICULTY_PRESETS[difficulty];

        return {
            chaser: monster,
            distanceGapMeters: config.startingGapMeters,
            isCaught: false,
            hasEscaped: false,
            escapeDistanceMeters: config.escapeDistanceMeters,
            elapsedSeconds: 0,
            playerDistanceMeters: 0,
            chaserDistanceMeters: 0,
        };
    }

    /**
     * Update chase state based on player speed
     *
     * @param state Current chase state
     * @param playerSpeedKph Player's current speed in km/h
     * @param deltaSeconds Time elapsed since last update
     * @param difficulty Current difficulty for speed modifier
     * @returns Updated chase state
     */
    static updateChase(
        state: ChaseState,
        playerSpeedKph: number,
        deltaSeconds: number,
        difficulty: ChaseDifficulty = "normal"
    ): ChaseState {
        // Don't update if already resolved
        if (state.isCaught || state.hasEscaped) {
            return state;
        }

        const config = CHASE_DIFFICULTY_PRESETS[difficulty];
        const chaserBaseSpeed = state.chaser.chaseSpeedKph ?? 8;
        const chaserEffectiveSpeed = chaserBaseSpeed * config.chaserSpeedModifier;

        // Calculate distance traveled (km/h -> m/s -> meters)
        const playerDistanceIncrement =
            (playerSpeedKph / 3600) * deltaSeconds * 1000;
        const chaserDistanceIncrement =
            (chaserEffectiveSpeed / 3600) * deltaSeconds * 1000;

        // Update distances
        const newPlayerDistance =
            state.playerDistanceMeters + playerDistanceIncrement;
        const newChaserDistance =
            state.chaserDistanceMeters + chaserDistanceIncrement;

        // Calculate new gap
        const newGap =
            state.distanceGapMeters +
            (playerDistanceIncrement - chaserDistanceIncrement);

        // Check win/lose conditions
        const isCaught = newGap <= 0;
        const hasEscaped = newGap >= state.escapeDistanceMeters;

        return {
            ...state,
            distanceGapMeters: newGap,
            isCaught,
            hasEscaped,
            elapsedSeconds: state.elapsedSeconds + deltaSeconds,
            playerDistanceMeters: newPlayerDistance,
            chaserDistanceMeters: newChaserDistance,
        };
    }

    /**
     * Get danger level based on current gap
     * @returns 0-1 where 1 is maximum danger (about to be caught)
     */
    static getDangerLevel(state: ChaseState): number {
        if (state.isCaught) return 1;
        if (state.hasEscaped) return 0;

        // Danger increases as gap decreases
        // At 0m gap = 1.0, at escapeDistance = 0.0
        const normalizedGap = Math.max(
            0,
            Math.min(state.distanceGapMeters / state.escapeDistanceMeters, 1)
        );
        return 1 - normalizedGap;
    }

    /**
     * Get status message based on current state
     */
    static getStatusMessage(state: ChaseState): string {
        if (state.isCaught) {
            return `${state.chaser.name} caught you!`;
        }
        if (state.hasEscaped) {
            return `You escaped from ${state.chaser.name}!`;
        }

        const gap = Math.round(state.distanceGapMeters);
        if (gap < 25) {
            return "IT'S RIGHT BEHIND YOU!";
        }
        if (gap < 50) {
            return "You can feel its breath!";
        }
        if (gap < 100) {
            return "It's gaining on you!";
        }
        if (gap < 200) {
            return "Keep running!";
        }
        return "Maintain your pace!";
    }

    /**
     * Calculate required pace to escape
     * @returns Required speed in kph to outrun the chaser
     */
    static getRequiredPace(
        state: ChaseState,
        difficulty: ChaseDifficulty = "normal"
    ): number {
        const config = CHASE_DIFFICULTY_PRESETS[difficulty];
        const chaserBaseSpeed = state.chaser.chaseSpeedKph ?? 8;
        return chaserBaseSpeed * config.chaserSpeedModifier;
    }
}

/**
 * @fileoverview GameContextService - Unified Modifier Hub
 * 
 * This service aggregates all sources of player power:
 * - Archetype (JUGGERNAUT/PATHFINDER/WARDEN)
 * - Unlocked Skills (from skill tree)
 * - Oracle Decrees (daily buffs/debuffs)
 * - Equipped Items
 * - Territory Bonuses (future)
 * 
 * Returns a unified PlayerContext object that any system can consume.
 */

import prisma from "@/lib/prisma";
import { Archetype } from "@prisma/client";
import {
    PlayerContext,
    PlayerModifiers,
    DEFAULT_MODIFIERS,
    ActiveBuff,
    CombatStats,
    PlayerIdentity
} from "@/types/game";
import { PATH_MODIFIERS, PATH_INFO } from "@/data/builds";
import { SKILL_TREE_V2, getNodeById } from "@/data/skill-tree-v2";
import { SkillNodeV2 } from "@/types/skills";
import { TrainingPath } from "@/types/training";
import { OracleDecree } from "@/types/oracle";

// ============================================================================
// Archetype → TrainingPath Mapping
// ============================================================================

const ARCHETYPE_TO_PATH: Record<Archetype, TrainingPath> = {
    JUGGERNAUT: "JUGGERNAUT",
    PATHFINDER: "PATHFINDER",
    WARDEN: "WARDEN",
};

// ============================================================================
// Service
// ============================================================================

export class GameContextService {
    /**
     * Get the unified PlayerContext for a user.
     * This is the main entry point for any system needing player modifiers.
     */
    static async getPlayerContext(userId: string): Promise<PlayerContext> {
        // 1. Fetch all required data in parallel
        const [user, titan, unlockedSkills, equippedItems, pvpProfile] = await Promise.all([
            prisma.user.findUnique({
                where: { id: userId },
                select: {
                    id: true,
                    archetype: true,
                    level: true,
                    heroName: true,
                },
            }),
            prisma.titan.findUnique({
                where: { userId },
                select: {
                    name: true,
                    level: true,
                    dailyDecree: true,
                    currentBuff: true,
                    strength: true,
                    vitality: true,
                },
            }),
            prisma.userSkill.findMany({
                where: { userId, unlocked: true },
                select: { skillId: true },
            }),
            prisma.userEquipment.findMany({
                where: { userId, equipped: true },
                include: { item: true },
            }),
            prisma.pvpProfile.findUnique({
                where: { userId },
                select: {
                    rankScore: true,
                },
            }),
        ]);

        if (!user) {
            throw new Error(`User not found: ${userId}`);
        }

        // 2. Build Identity
        const archetype = user.archetype || Archetype.WARDEN;
        const path = ARCHETYPE_TO_PATH[archetype];
        const pathInfo = PATH_INFO[path];

        const identity: PlayerIdentity = {
            userId: user.id,
            archetype,
            archetypeName: pathInfo.name,
            level: titan?.level || user.level || 1,
            titanName: titan?.name || user.heroName || "Unnamed Titan",
        };

        // 3. Start with base modifiers
        const modifiers: PlayerModifiers = { ...DEFAULT_MODIFIERS };
        const activeBuffs: ActiveBuff[] = [];

        // 4. Apply Archetype Modifiers
        const archetypeModifiers = PATH_MODIFIERS[path];
        if (archetypeModifiers) {
            modifiers.attackPower *= archetypeModifiers.attackPower;
            modifiers.stamina *= archetypeModifiers.stamina;
            // Dodge isn't in our modifier system yet, but could be added

            activeBuffs.push({
                id: `archetype_${archetype}`,
                source: "ARCHETYPE",
                name: pathInfo.name,
                description: pathInfo.description,
                icon: pathInfo.icon,
                modifiers: {
                    attackPower: archetypeModifiers.attackPower,
                    stamina: archetypeModifiers.stamina,
                },
            });
        }

        // 5. Apply Skill Modifiers
        const unlockedSkillIds = unlockedSkills.map(s => s.skillId);
        for (const skillId of unlockedSkillIds) {
            const skillDef = getNodeById(skillId);
            if (skillDef?.effects) {
                const effects = skillDef.effects;

                // Map SkillEffect to PlayerModifiers
                if (effects.tpMultiplier) {
                    modifiers.xpGain *= effects.tpMultiplier;
                }
                if (effects.titanLoadMultiplier) {
                    modifiers.titanLoad *= effects.titanLoadMultiplier;
                }
                if (effects.recoveryRateMultiplier) {
                    modifiers.recoverySpeed *= effects.recoveryRateMultiplier;
                }
                if (effects.ksMultiplier) {
                    modifiers.goldGain *= effects.ksMultiplier; // KS maps to gold in this context
                }

                // Add as active buff for UI display
                activeBuffs.push({
                    id: `skill_${skillId}`,
                    source: "SKILL",
                    name: skillDef.title,
                    description: skillDef.description,
                    modifiers: {
                        xpGain: effects.tpMultiplier,
                        titanLoad: effects.titanLoadMultiplier,
                        recoverySpeed: effects.recoveryRateMultiplier,
                    },
                });
            }
        }

        // 6. Apply Oracle Decree
        let oracleDecreeType: "BUFF" | "DEBUFF" | "NEUTRAL" | undefined;
        if (titan?.dailyDecree) {
            const decree = titan.dailyDecree as unknown as OracleDecree;
            oracleDecreeType = decree.type;

            if (decree.effect?.xpMultiplier) {
                modifiers.xpGain *= decree.effect.xpMultiplier;

                activeBuffs.push({
                    id: "oracle_decree",
                    source: "ORACLE",
                    name: decree.label || "Oracle Decree",
                    description: decree.description || "The Oracle has spoken.",
                    modifiers: {
                        xpGain: decree.effect.xpMultiplier,
                    },
                });
            }

            // Handle debuffs
            if (decree.type === "DEBUFF" && decree.effect?.xpMultiplier) {
                // Already applied above, but could add specific debuff handling
            }
        }

        // 7. Apply Equipment Modifiers
        let totalItemPower = 0;

        for (const equip of equippedItems) {
            const item = equip.item;
            totalItemPower += item.power || 0;

            // Special Item Effects (Hardcoded for v1 based on Seed Data)
            if (item.name === "Exo-Skeleton Legs") {
                modifiers.strengthXp *= 1.2; // "Enhance squat max" interpreted as Strength XP boost
                activeBuffs.push({
                    id: `item_${item.id}`,
                    source: "EQUIPMENT",
                    name: item.name,
                    description: "Squat Max +20% (XP Boost)",
                    modifiers: { strengthXp: 1.2 }
                });
            }

            if (item.name === "Protein Synthesizer") {
                modifiers.recoverySpeed *= 1.1;
                activeBuffs.push({
                    id: `item_${item.id}`,
                    source: "EQUIPMENT",
                    name: item.name,
                    description: "Optimized Recovery",
                    modifiers: { recoverySpeed: 1.1 }
                });
            }

            if (item.name === "Heart Rate Monitor") {
                modifiers.cardioXp *= 1.1; // "Advanced biometrics"
            }
        }

        // 8. Apply PvP Modifiers (Rank-based)
        if (pvpProfile) {
            const rank = pvpProfile.rankScore || 1000;

            // Tier 1: Gladiator (1500+)
            if (rank >= 1500) {
                modifiers.attackPower *= 1.05;
                modifiers.critChance += 0.02; // +2% Crit

                activeBuffs.push({
                    id: "pvp_gladiator",
                    source: "PVP",
                    name: "Gladiator's Spirit",
                    description: "Rank 1500+ Bonus (+5% DMG, +2% Crit)",
                    modifiers: { attackPower: 1.05, critChance: 0.02 }
                });
            }

            // Tier 2: High Warlord (2000+)
            if (rank >= 2000) {
                modifiers.stamina *= 1.1;
                modifiers.lootLuck *= 1.2;

                activeBuffs.push({
                    id: "pvp_warlord",
                    source: "PVP",
                    name: "High Warlord's Command",
                    description: "Rank 2000+ Bonus (+10% Stamina, +20% Luck)",
                    modifiers: { stamina: 1.1, lootLuck: 1.2 }
                });
            }
        }

        // 9. Calculate Combat Stats
        // Base damage = 10 + Strength + Total Item Power
        const baseDamage = 10 + (titan?.strength || 10) + totalItemPower;
        const baseDefense = 5 + (titan?.vitality || 10); // Could also add Armor Power here if Items had defense

        const combat: CombatStats = {
            effectiveAttack: Math.round(baseDamage * modifiers.attackPower),
            effectiveDefense: Math.round(baseDefense * modifiers.defense),
            damagePerVolume: 0.1 * modifiers.attackPower, // 0.1 damage per kg volume
            critMultiplier: 1.5 + (modifiers.critChance * 0.5), // Base 1.5x, scales with crit
        };

        // 9. Return unified context
        return {
            identity,
            modifiers,
            activeBuffs,
            combat,
            raw: {
                unlockedSkillIds,
                equippedItemIds: equippedItems.map(e => e.equipmentId),
                oracleDecreeType,
            },
        };
    }

    /**
     * Calculate XP reward with all modifiers applied.
     * @param baseXp The base XP before modifiers
     * @param context The player context (or fetches if not provided)
     * @param exerciseType Optional: "strength" or "cardio" for specific multipliers
     */
    static calculateXpReward(
        baseXp: number,
        context: PlayerContext,
        exerciseType?: "strength" | "cardio"
    ): { finalXp: number; appliedMultiplier: number; appliedBuffs: string[] } {
        let multiplier = context.modifiers.xpGain;
        const appliedBuffs: string[] = [];

        // Apply type-specific multiplier
        if (exerciseType === "strength") {
            multiplier *= context.modifiers.strengthXp;
        } else if (exerciseType === "cardio") {
            multiplier *= context.modifiers.cardioXp;
        }

        // Track which buffs contributed
        for (const buff of context.activeBuffs) {
            if (buff.modifiers.xpGain && buff.modifiers.xpGain !== 1.0) {
                appliedBuffs.push(buff.name);
            }
        }

        const finalXp = Math.round(baseXp * multiplier);

        return {
            finalXp,
            appliedMultiplier: multiplier,
            appliedBuffs,
        };
    }

    /**
     * Calculate combat damage with all modifiers applied.
     */
    static calculateDamage(
        volume: number,
        context: PlayerContext
    ): { damage: number; isCrit: boolean } {
        const baseDamage = volume * context.combat.damagePerVolume;
        const isCrit = Math.random() < context.modifiers.critChance;
        const damage = isCrit
            ? Math.round(baseDamage * context.combat.critMultiplier)
            : Math.round(baseDamage);

        return { damage, isCrit };
    }
}

import prisma from "@/lib/prisma";
import { TerritoryType } from "@prisma/client";

export const CONTEST_COST_GOLD = 1000;

export class GuildTerritoryService {
    /**
     * Allows a guild to enter a contest for a territory.
     * Deducts gold from Guild bank (if implemented) or Leader (for now).
     * Actually, let's assume Guild has gold. Schema check: Guild has `xp`, but no `gold` field in schema provided earlier?
     * Checking schema provided in context... Guild model has `xp`, `level`, `powerRating`. No `gold`.
     * For Phase 1, we might waive the cost or charge the Leader.
     * Plan says "Contest Cost: 1000; // Guild gold".
     * Let's charge the LEADER for now as a proxy, or add gold to Guild later.
     * Spec says "Guild gold to contest". Let's assume we charge the accessing user (Leader/Officer) for now.
     */
    static async contestTerritory(guildId: string, territoryId: string, userId: string) {
        const territory = await prisma.territory.findUnique({ where: { id: territoryId } });
        if (!territory) throw new Error("Territory not found");

        // Check if already contesting this week
        const now = new Date();
        // get ISO week
        const weekNumber = getISOWeek(now);
        const year = now.getFullYear();

        const existingEntry = await prisma.territoryContestEntry.findUnique({
            where: {
                territoryId_guildId_weekNumber_year: {
                    territoryId,
                    guildId,
                    weekNumber,
                    year
                }
            }
        });

        if (existingEntry) throw new Error("Already contesting this territory this week");

        // Charge Leader (User) - Transaction
        return await prisma.$transaction(async (tx) => {
            const user = await tx.user.findUnique({ where: { id: userId } });
            if (!user || user.gold < CONTEST_COST_GOLD) {
                throw new Error(`Insufficient gold. Requires ${CONTEST_COST_GOLD} Gold.`);
            }

            await tx.user.update({
                where: { id: userId },
                data: { gold: { decrement: CONTEST_COST_GOLD } }
            });

            return await tx.territoryContestEntry.create({
                data: {
                    territoryId,
                    guildId,
                    weekNumber,
                    year,
                    workoutCount: 0,
                    totalVolume: 0,
                    xpEarned: 0,
                }
            });
        });
    }

    /**
     * Records activity from a guild member towards a territory.
     * Should be called when a workout finishes if the user is in a guild and in a territory being contested.
     */
    static async recordActivity(guildId: string, territoryId: string, metrics: { volume: number; xp: number }) {
        const now = new Date();
        const weekNumber = getISOWeek(now);
        const year = now.getFullYear();

        // Only update if there is an active contest entry
        // We don't throw if not found, just ignore (not contesting)
        const entry = await prisma.territoryContestEntry.findUnique({
            where: {
                territoryId_guildId_weekNumber_year: {
                    territoryId,
                    guildId,
                    weekNumber,
                    year
                }
            }
        });

        if (entry) {
            await prisma.territoryContestEntry.update({
                where: { id: entry.id },
                data: {
                    workoutCount: { increment: 1 },
                    totalVolume: { increment: metrics.volume },
                    xpEarned: { increment: metrics.xp }
                }
            });
        }
    }

    /**
     * Gets the current leaderboard for a territory contest (active week).
     */
    static async getContestLeaderboard(territoryId: string) {
        const now = new Date();
        const weekNumber = getISOWeek(now);
        const year = now.getFullYear();

        const entries = await prisma.territoryContestEntry.findMany({
            where: {
                territoryId,
                weekNumber,
                year
            },
            include: {
                // Include Guild info locally if needed, but schema relations might be minimal?
                // TerritoryContestEntry has guildId. We don't have a direct relation in schema shown in context?
                // "guildId String". "territory Territory @relation..."
                // Wait, schema check:
                // model TerritoryContestEntry { ... guildId String ... territory Territory ... }
                // It does NOT show a `guild Guild` relation in the provided schema snippet!
                // Line 733: "guildId String"
                // Line 743: "territory Territory..."
                // Missing `guild Guild @relation...`?
                // Let's rely on manual fetch or assume relation exists (it should).
                // Actually, checking schema provided:
                // Guild model (line 676) has `territories Territory[]`.
                // TerritoryContestEntry (line 730) has ONLY `territory`.
                // This suggests a missing relation in TerritoryContestEntry for `guild`.
                // I should use `guildId` to fetch guild details separately or fixing schema is out of scope for now?
                // I'll fetch entries then fetch guilds.
            },
            orderBy: {
                xpEarned: 'desc' // Default metric
            }
        });

        // Manually join Guild names
        const guildIds = entries.map(e => e.guildId);
        const guilds = await prisma.guild.findMany({
            where: { id: { in: guildIds } },
            select: { id: true, name: true, tag: true }
        });

        const guildMap = new Map(guilds.map(g => [g.id, g]));

        return entries.map(e => ({
            ...e,
            guildName: guildMap.get(e.guildId)?.name || 'Unknown Guild',
            guildTag: guildMap.get(e.guildId)?.tag || '???'
        }));
    }
}

// Helper for ISO Week
function getISOWeek(date: Date) {
    const d = new Date(Date.UTC(date.getFullYear(), date.getMonth(), date.getDate()));
    const dayNum = d.getUTCDay() || 7;
    d.setUTCDate(d.getUTCDate() + 4 - dayNum);
    const yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
    return Math.ceil((((d.getTime() - yearStart.getTime()) / 86400000) + 1) / 7);
}

import prisma from "@/lib/prisma";
import {
    calculatePowerRating,
    TrainingPath
} from "@/lib/powerRating";
import { TrainingContextService } from "@/services/data/TrainingContextService";
import { DayPlan } from "@/types";

/**
 * Service for calculating and managing Titan Power Ratings.
 * Combines Strength (Wilks), Cardio (Watts/kg), and Adherence (MRV/Consistency).
 */
export class PowerRatingService {

    /**
     * Helper to get target sessions from WeeklyPlan or Fallback defaults.
     */
    private static async getTargetSessions(userId: string, type: 'STRENGTH' | 'CARDIO'): Promise<number> {
        // Find the most recent WeeklyPlan
        const plan = await prisma.weeklyPlan.findFirst({
            where: { userId },
            orderBy: { weekStart: 'desc' }
        });

        if (plan && plan.plan) {
            // Parse the JSON plan
            const dayPlans = (plan.plan as unknown) as DayPlan[];

            // Count scheduled non-rest days
            // Note: This assumes the Oracle schedules mixed strength/cardio. A simplistic check for !isRestDay is a good proxy for "Activity Days".
            // A more advanced check would parse the recommendation title/type.
            const plannedDays = dayPlans.filter(d => !d.isRestDay).length;

            // For now, assume total activity is split 60/40 Strength/Cardio roughly, 
            // or just use total volume as adherence target if we don't distinguish types in the Plan JSON yet.
            // Since OracleRecommendation types are coarse (RECOVERY vs GRIND), let's estimate:

            if (plannedDays > 0) {
                // Fallback Logic based on type since Plan doesn't strictly tag "Cardio Day" vs "Leg Day" in types yet
                if (type === 'STRENGTH') return Math.max(2, Math.ceil(plannedDays * 0.6));
                if (type === 'CARDIO') return Math.max(1, Math.ceil(plannedDays * 0.4));
            }
        }

        // Default Fallback (MVP)
        return type === 'STRENGTH' ? 3 : 2;
    }

    /**
     * Calculate MRV Adherence based on recent exercise logs vs planned counts.
     * Returns 0.0 - 1.0 (capped)
     */
    static async calculateStrengthAdherence(userId: string): Promise<number> {
        // Look at last 14 days
        const twoWeeksAgo = new Date();
        twoWeeksAgo.setDate(twoWeeksAgo.getDate() - 14);

        const logs = await prisma.exerciseLog.findMany({
            where: {
                userId,
                date: { gte: twoWeeksAgo }
            },
            select: { date: true }
        });

        // Group by day to count unique sessions
        const sessionDates = new Set(logs.map(l => l.date.toISOString().split('T')[0]));
        const actualSessions = sessionDates.size;

        // Target: 2 weeks of volume
        const weeklyTarget = await this.getTargetSessions(userId, 'STRENGTH');
        const totalTarget = weeklyTarget * 2;

        return Math.min(1.1, actualSessions / totalTarget); // Allow small over-adherence buffer (110%)
    }

    /**
     * Calculate Cardio Adherence based on recent cardio logs.
     * Returns 0.0 - 1.0 (capped)
     */
    static async calculateCardioAdherence(userId: string): Promise<number> {
        // Look at last 14 days
        const twoWeeksAgo = new Date();
        twoWeeksAgo.setDate(twoWeeksAgo.getDate() - 14);

        const logs = await prisma.cardioLog.findMany({
            where: {
                userId,
                date: { gte: twoWeeksAgo }
            },
            select: { date: true }
        });

        const actualSessions = logs.length;

        // Target: 2 weeks of volume
        const weeklyTarget = await this.getTargetSessions(userId, 'CARDIO');
        const totalTarget = weeklyTarget * 2;

        return Math.min(1.1, actualSessions / totalTarget);
    }

    /**
     * Full recalculation of a user's Power Rating using live data.
     */
    static async syncPowerRating(userId: string) {
        // 1. Fetch User Data
        const user = await prisma.user.findUnique({
            where: { id: userId },
            include: {
                pvpProfile: true,
                titan: true,
            },
        });

        if (!user || !user.titan) {
            throw new Error("User or Titan not found");
        }

        // 2. Extract Metrics
        const wilks = user.pvpProfile?.highestWilksScore || 0;

        // Determine Cardio Metric (Watts/kg)
        let wkg = 0;
        if (user.ftpCycle && user.bodyWeight > 0) {
            wkg = user.ftpCycle / user.bodyWeight;
        } else if (user.ftpRun && user.bodyWeight > 0) {
            wkg = user.ftpRun / user.bodyWeight;
        }

        // 3. Determine Path
        const path = (user.activePath as TrainingPath) || 'WARDEN';

        // 4. Calculate Real Adherence
        const mrvAdherence = await this.calculateStrengthAdherence(userId);
        const cardioAdherence = await this.calculateCardioAdherence(userId);

        // 5. Calculate Power Rating
        const result = calculatePowerRating(
            wilks,
            wkg,
            path,
            mrvAdherence,
            cardioAdherence
        );

        // 6. Update Titan
        const updatedTitan = await prisma.titan.update({
            where: { userId },
            data: {
                powerRating: result.powerRating,
                strengthIndex: result.strengthIndex,
                cardioIndex: result.cardioIndex,
                mrvAdherence: 1.0 + (mrvAdherence * 0.15), // Storing the multiplier for UI display logic matching lib
                lastPowerCalcAt: new Date(),
            },
        });

        return {
            ...result,
            mrvAdherence,
            cardioAdherence,
            titan: updatedTitan
        };
    }
}

/**
 * Territory Service - Core business logic for Territory Conquest
 * 
 * Handles:
 * - Processing GPS tracks into tile conquests
 * - Control point calculations with diminishing returns
 * - Weekly settlement for ownership
 * - Passive income calculations
 */

import prisma from "@/lib/prisma";
import {
    coordsToTileId,
    getTilesFromGpsTrack,
    isWithinHomeZone,
    countConnectedTiles,
    tileIdToCoords,
} from "@/lib/territory/tileUtils";
import { calculateEffortScore, EffortInput } from "./EffortCalculator";
import { NotificationService } from "@/services/notifications";

// ============================================
// TYPES
// ============================================

export interface ConquestResult {
    tilesConquered: number;
    tilesReinforced: number;
    totalControlGained: number;
    newTileIds: string[];
}

export interface SettlementResult {
    tilesSettled: number;
    ownershipChanges: number;
}

export interface TerritoryStats {
    ownedTiles: number;
    contestedTiles: number;
    totalControlPoints: number;
    dailyGold: number;
    dailyXP: number;
    largestConnectedArea: number;
}

export interface Income {
    gold: number;
    xp: number;
}

// ============================================
// CONSTANTS
// ============================================

/** Base control points for first daily visit */
const BASE_CONTROL_FIRST_VISIT = 10;

/** Control points for subsequent visits (diminishing) */
const CONTROL_SECOND_VISIT = 7;
const CONTROL_THIRD_PLUS_VISIT = 4;

/** Maximum control points a tile can have */
const MAX_CONTROL_POINTS = 100;

/** Minimum control to "own" a tile */
const OWNERSHIP_THRESHOLD = 50;

/** Home zone bonus multiplier */
const HOME_ZONE_BONUS = 1.5;

/** Control lost when rival visits your tile */
const RIVAL_ATTACK_LOSS = 7;

/** Daily control loss cap per tile */
const DAILY_LOSS_CAP = 50;

/** Passive income per owned tile */
const GOLD_PER_TILE = 0.5;
const XP_PER_TILE = 1.0;

/** Maximum adjacency bonus */
const MAX_ADJACENCY_BONUS = 0.5;

// ============================================
// CONQUEST
// ============================================

/**
 * Process a GPS track and update territory control
 */
export async function conquestFromActivity(
    userId: string,
    gpsTrack: Array<{ lat: number; lng: number }>,
    effortInput: EffortInput
): Promise<ConquestResult> {
    const tileIds = getTilesFromGpsTrack(gpsTrack);
    if (tileIds.length === 0) {
        return {
            tilesConquered: 0,
            tilesReinforced: 0,
            totalControlGained: 0,
            newTileIds: [],
        };
    }

    // Get user for home zone calculation
    const user = await prisma.user.findUnique({
        where: { id: userId },
        select: { homeLatitude: true, homeLongitude: true },
    });

    const effortResult = calculateEffortScore(effortInput);
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    let tilesConquered = 0;
    let tilesReinforced = 0;
    let totalControlGained = 0;
    const newTileIds: string[] = [];

    for (const tileId of tileIds) {
        // Ensure tile exists
        await prisma.territoryTile.upsert({
            where: { id: tileId },
            create: { id: tileId },
            update: {},
        });

        // Get or create control record
        const existingControl = await prisma.tileControl.findUnique({
            where: {
                tileId_userId: { tileId, userId },
            },
        });

        // Calculate diminishing returns
        let dailyVisits = 1;
        let basePoints = BASE_CONTROL_FIRST_VISIT;

        if (existingControl) {
            // Check if we need to reset daily visits
            const lastReset = new Date(existingControl.lastDailyReset);
            lastReset.setHours(0, 0, 0, 0);

            if (lastReset.getTime() === today.getTime()) {
                // Same day - apply diminishing returns
                dailyVisits = existingControl.dailyVisits + 1;
                if (dailyVisits === 2) {
                    basePoints = CONTROL_SECOND_VISIT;
                } else {
                    basePoints = CONTROL_THIRD_PLUS_VISIT;
                }
            }
            // Different day - reset to full points
        }

        // Apply effort bonus
        let controlGain = basePoints + effortResult.controlBonus;

        // Apply home zone bonus
        if (user?.homeLatitude && user?.homeLongitude) {
            if (isWithinHomeZone(tileId, user.homeLatitude, user.homeLongitude)) {
                controlGain = Math.round(controlGain * HOME_ZONE_BONUS);
            }
        }

        // Update control record
        if (existingControl) {
            const newPoints = Math.min(
                existingControl.controlPoints + controlGain,
                MAX_CONTROL_POINTS
            );

            await prisma.tileControl.update({
                where: { id: existingControl.id },
                data: {
                    controlPoints: newPoints,
                    lastVisitAt: new Date(),
                    visitCount: existingControl.visitCount + 1,
                    dailyVisits: dailyVisits,
                    lastDailyReset:
                        dailyVisits === 1 ? today : existingControl.lastDailyReset,
                },
            });

            tilesReinforced++;
            totalControlGained += controlGain;
        } else {
            // New tile for this user
            await prisma.tileControl.create({
                data: {
                    tileId,
                    userId,
                    controlPoints: Math.min(controlGain, MAX_CONTROL_POINTS),
                    dailyVisits: 1,
                    lastDailyReset: today,
                },
            });

            tilesConquered++;
            totalControlGained += controlGain;
            newTileIds.push(tileId);
        }

        // Handle rival control reduction
        await reduceRivalControl(tileId, userId);
    }

    return {
        tilesConquered,
        tilesReinforced,
        totalControlGained,
        newTileIds,
    };
}

/**
 * Reduce control for other users when someone visits a tile
 */
async function reduceRivalControl(
    tileId: string,
    activeUserId: string
): Promise<void> {
    const rivalControls = await prisma.tileControl.findMany({
        where: {
            tileId,
            userId: { not: activeUserId },
        },
    });

    for (const rival of rivalControls) {
        // Apply loss with daily cap check (simplified - would need tracking)
        const newPoints = Math.max(0, rival.controlPoints - RIVAL_ATTACK_LOSS);

        if (newPoints !== rival.controlPoints) {
            await prisma.tileControl.update({
                where: { id: rival.id },
                data: { controlPoints: newPoints },
            });
        }
    }
}

