import { runFullAudit } from "./auditorOrchestrator";
import { OracleService } from "./oracle";
import { getWellness } from "../lib/intervals";
import { AnalyticsService } from "./analytics";
import { TrainingPath, InAppWorkoutLog, IntervalsActivity } from "../types";
import { WellnessData } from "../lib/intervals";

// Note: Hevy integration removed per data-source-reconciliation.md
// Strength data now comes from IronForge internal logs only.

/**
 * Server-Side Planner Service
 * Orchestrates data from DB and Intervals to generate weekly plans.
 * 
 * DATA SOURCES:
 * - Strength: IronForge PostgreSQL (internal logs from IronMines)
 * - Cardio: Garmin -> Intervals.icu
 * - Wellness: Garmin -> Intervals.icu
 */
export const PlannerService = {
  /**
   * Triggers the generation of a weekly plan for a user.
   * Can be called from Server Actions or Cron Jobs.
   */
  triggerWeeklyPlanGeneration: async (userId: string) => {
    console.log(`Planner: Generating plan for user ${userId}`);

    // 1. Fetch User Credentials & Settings
    const user = await prisma.user.findUnique({
      where: { id: userId },
      include: {
        exerciseLogs: {
          where: {
            date: {
              gte: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000), // Last 30 days for TTB/Audit
            },
          },
        },
        cardioLogs: {
          where: {
            date: {
              gte: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000),
            },
          },
        },
      },
    });

    if (!user) throw new Error("User not found");

    // 2. Fetch Strength Data (IronForge internal logs)
    // runFullAudit now uses userId to fetch from IronForge DB
    const auditReport = await runFullAudit(true, userId);

    // 3. Fetch Intervals Wellness
    let wellness: WellnessData = {
      date: new Date().toISOString(),
      ctl: 0,
      atl: 0,
      tsb: 0,
      id: "unknown",
      hrv: null,
      restingHR: null,
      readiness: null,
      sleepScore: null,
      sleepSecs: null,
      rampRate: null,
      vo2max: null,
      // Phase 2 fields
      avgSleepingHR: null,
      sleepQuality: null,
      hydration: null,
      hrvSDNN: null,
      baevskySI: null,
      stress: null,
      mood: null,
      fatigue: null,
      menstrualPhase: null,
      menstrualPhasePredicted: null,
      weight: null,
      spO2: null,
      respiration: null,
      bloodGlucose: null,
      injury: null,
      soreness: null,
      steps: null,
    };
    if (user.intervalsApiKey && user.intervalsAthleteId) {
      const today = new Date().toISOString().split("T")[0];
      const w = await getWellness(
        today,
        user.intervalsApiKey,
        user.intervalsAthleteId,
      );
      if (w && !Array.isArray(w)) wellness = w;
    }

    // 4. Map Data for Analytics (TTB Calculation)
    // Map Prisma CardioLogs to IntervalsActivity format expected by Analytics
    const activities: IntervalsActivity[] = user.cardioLogs.map((l) => ({
      id: l.intervalsId || undefined,
      start_date_local: l.date.toISOString(),
      type: l.type || undefined,
      moving_time: l.duration,
      icu_intensity: (l.averageHr || 140) > 160 ? 90 : 60, // Rough estimate if load not available
      icu_training_load: l.load || 0,
    }));

    // Use AnalyticsService for TTB
    // Note: Prisma ExerciseLog matches the shape roughly but we need to verify.
    // Analytics expects: { isEpic: boolean, date: Date/string }
    // Prisma: { isEpic: Boolean, date: Date }
    const ttb = AnalyticsService.calculateTTB(
      user.exerciseLogs as any[], // Keep as any[] for now as Prisma type might have extra fields, but it works for Analytics
      activities,
      wellness as any, // IntervalsWellness matches WellnessData loosely
    );

    // If auditor says we are neglecting something, that becomes lowest TTB
    if (auditReport.highestPriorityGap) {
      ttb.lowest = "strength"; // Weakness found
    }

    // 6. Generate Plan via Oracle (using consult as generateWeekPlan doesn't exist yet)
    const recommendation = await OracleService.consult(
      wellness as any,
      ttb,
      [], // events
      auditReport,
      undefined, // titanAnalysis
      null, // recoveryAnalysis
      (user.activePath as TrainingPath) || "HYBRID_WARDEN",
    );

    // Wrap recommendation into a week plan structure
    const plan = {
      id: `plan_${Date.now()}`,
      weekStart: new Date().toISOString(),
      days: [
        {
          recommendation,
          dayOfWeek: new Date().getDay(),
          date: new Date().toISOString(),
          isRestDay: false,
        },
      ],
      createdAt: new Date().toISOString(),
    };

    // 7. Save to DB
    await prisma.weeklyPlan.create({
      data: {
        userId: user.id,
        weekStart: new Date(plan.weekStart),
        plan: plan.days as unknown as any, // JSON field requirement
      },
    });

    console.log(`Planner: Plan generated and saved for user ${userId}`);
    return plan;
  },
};

"use server";

import { prisma } from "@/lib/prisma";
import { revalidatePath } from "next/cache";
import { TitanState, getAuthoritativeTitanState } from "./titan-state-schema";
import {
  recordTitanEvent,
  replayTitanEvents,
} from "./titan-event-sourcing";

// ============================================
// UNIFIED TITAN SOUL - STATE RECOVERY
// Rollback and recovery mechanisms
// ============================================

interface RecoveryCheckpoint {
  id: string;
  userId: string;
  state: TitanState;
  createdAt: Date;
  reason: string;
  version: number;
}

interface RecoveryResult {
  success: boolean;
  previousState?: TitanState;
  newState?: TitanState;
  message: string;
}

// In-memory checkpoint storage (in production: database table)
const checkpoints: RecoveryCheckpoint[] = [];

/**
 * Create a recovery checkpoint.
 */
export async function createRecoveryCheckpoint(
  userId: string,
  reason: string,
): Promise<RecoveryCheckpoint | null> {
  const state = await getAuthoritativeTitanState(userId);

  if (!state) {
    console.error("Cannot create checkpoint: no Titan state found");
    return null;
  }

  const checkpoint: RecoveryCheckpoint = {
    id: `chkpt-${Date.now()}-${Math.random().toString(36).slice(2, 8)}`,
    userId,
    state,
    createdAt: new Date(),
    reason,
    version: state.version,
  };

  checkpoints.push(checkpoint);

  // Keep only last 10 checkpoints per user
  const userCheckpoints = checkpoints.filter((c) => c.userId === userId);
  if (userCheckpoints.length > 10) {
    const oldest = userCheckpoints[0];
    const index = checkpoints.indexOf(oldest);
    if (index > -1) checkpoints.splice(index, 1);
  }

  console.log(`[RECOVERY] Checkpoint created for ${userId}: ${reason}`);
  await recordTitanEvent(userId, "STATE_RECOVERED", {
    action: "checkpoint_created",
    reason,
  });

  return checkpoint;
}

/**
 * Get available checkpoints for rollback.
 */
export async function getAvailableCheckpoints(
  userId: string,
): Promise<RecoveryCheckpoint[]> {
  return checkpoints
    .filter((c) => c.userId === userId)
    .sort((a, b) => b.createdAt.getTime() - a.createdAt.getTime());
}

/**
 * Rollback to a specific checkpoint.
 */
export async function rollbackToCheckpoint(
  userId: string,
  checkpointId: string,
): Promise<RecoveryResult> {
  const checkpoint = checkpoints.find(
    (c) => c.id === checkpointId && c.userId === userId,
  );

  if (!checkpoint) {
    return { success: false, message: "Checkpoint not found" };
  }

  const currentState = await getAuthoritativeTitanState(userId);

  try {
    // Apply checkpoint state
    const state = checkpoint.state;

    await prisma.titan.update({
      where: { userId },
      data: {
        strength: state.stats.strength,
        vitality: state.stats.vitality,
        endurance: state.stats.endurance,
        agility: state.stats.agility,
        willpower: state.stats.willpower,
        currentHp: state.resources.hp,
        maxHp: state.resources.maxHp,
        currentEnergy: state.resources.energy,
      },
    });

    await prisma.user.update({
      where: { id: userId },
      data: {
        level: state.level,
        totalExperience: state.resources.xp,
        gold: state.economy.gold,
      },
    });

    await recordTitanEvent(userId, "STATE_RECOVERED", {
      action: "rollback",
      checkpointId,
      checkpointDate: checkpoint.createdAt.toISOString(),
    });

    revalidatePath("/dashboard");

    console.log(
      `[RECOVERY] Rolled back ${userId} to checkpoint ${checkpointId}`,
    );

    return {
      success: true,
      previousState: currentState || undefined,
      newState: state,
      message: `Rolled back to checkpoint from ${checkpoint.createdAt.toLocaleString()}`,
    };
  } catch (error) {
    console.error("Rollback failed:", error);
    return { success: false, message: "Rollback failed" };
  }
}

/**
 * Rollback to a specific event version (using event sourcing).
 */
export async function rollbackToVersion(
  userId: string,
  targetVersion: number,
): Promise<RecoveryResult> {
  try {
    const reconstructedState = await replayTitanEvents(userId, targetVersion);
    const currentState = await getAuthoritativeTitanState(userId);

    // Apply reconstructed state
    if (reconstructedState.stats) {
      const stats = reconstructedState.stats as Record<string, number>;
      await prisma.titan.update({
        where: { userId },
        data: {
          strength: stats.strength,
          vitality: stats.vitality,
          endurance: stats.endurance,
          agility: stats.agility,
          willpower: stats.willpower,
        },
      });
    }

    if (reconstructedState.level) {
      await prisma.user.update({
        where: { id: userId },
        data: { level: reconstructedState.level as number },
      });
    }

    await recordTitanEvent(userId, "STATE_RECOVERED", {
      action: "version_rollback",
      targetVersion,
    });

    revalidatePath("/dashboard");

    return {
      success: true,
      previousState: currentState || undefined,
      message: `Rolled back to version ${targetVersion}`,
    };
  } catch (error) {
    console.error("Version rollback failed:", error);
    return { success: false, message: "Version rollback failed" };
  }
}

/**
 * Recover from corruption by resetting to last known good state.
 */
export async function recoverFromCorruption(
  userId: string,
): Promise<RecoveryResult> {
  const available = await getAvailableCheckpoints(userId);

  if (available.length === 0) {
    // No checkpoints - reset to defaults
    console.log(
      `[RECOVERY] No checkpoints for ${userId}, resetting to defaults`,
    );

    try {
      await prisma.titan.update({
        where: { userId },
        data: {
          strength: 10,
          vitality: 10,
          endurance: 10,
          agility: 10,
          willpower: 10,
          currentHp: 100,
          maxHp: 100,
          currentEnergy: 100,
        },
      });

      await recordTitanEvent(userId, "STATE_RECOVERED", {
        action: "reset_to_defaults",
      });
      revalidatePath("/dashboard");

      return { success: true, message: "Reset to default state" };
    } catch (_error) {
      return { success: false, message: "Recovery failed" };
    }
  }

  // Roll back to most recent checkpoint
  return rollbackToCheckpoint(userId, available[0].id);
}

/**
 * Validate current state integrity.
 */
export async function validateStateIntegrity(userId: string): Promise<{
  valid: boolean;
  issues: string[];
}> {
  const state = await getAuthoritativeTitanState(userId);
  const issues: string[] = [];

  if (!state) {
    return { valid: false, issues: ["No Titan state found"] };
  }

  // Check for invalid values
  if (state.level < 1) issues.push("Level below minimum");
  if (state.resources.hp < 0) issues.push("HP is negative");
  if (state.resources.hp > state.resources.maxHp) issues.push("HP exceeds max");
  if (state.economy.gold < 0) issues.push("Gold is negative");

  // Check stats
  for (const [stat, value] of Object.entries(state.stats)) {
    if (value < 1) issues.push(`${stat} is below minimum`);
    if (value > 9999) issues.push(`${stat} exceeds maximum`);
  }

  return { valid: issues.length === 0, issues };
}

/**
 * Auto-heal detected integrity issues.
 */
export async function autoHealIntegrityIssues(
  userId: string,
): Promise<RecoveryResult> {
  const { valid, issues } = await validateStateIntegrity(userId);

  if (valid) {
    return { success: true, message: "No issues to heal" };
  }

  console.log(`[RECOVERY] Auto-healing ${issues.length} issues for ${userId}`);

  // Create checkpoint before healing
  await createRecoveryCheckpoint(userId, "pre-auto-heal");

  const state = await getAuthoritativeTitanState(userId);
  if (!state) return { success: false, message: "No state to heal" };

  // Fix issues
  const fixes: Record<string, unknown> = {};

  if (state.resources.hp < 0) fixes.currentHp = 0;
  if (state.resources.hp > state.resources.maxHp)
    fixes.currentHp = state.resources.maxHp;

  if (Object.keys(fixes).length > 0) {
    await prisma.titan.update({
      where: { userId },
      data: fixes as Record<string, number>,
    });
  }

  if (state.economy.gold < 0) {
    await prisma.user.update({
      where: { id: userId },
      data: { gold: 0 },
    });
  }

  await recordTitanEvent(userId, "STATE_RECOVERED", {
    action: "auto_heal",
    issues,
  });
  revalidatePath("/dashboard");

  return { success: true, message: `Healed ${issues.length} issues` };
}

/**
 * AUTOREGULATORY PROGRESSIVE RESISTANCE (APRE) ENGINE
 * Calculates optimal load adjustments based on performance divergence.
 */

export interface ApreSuggestion {
  adjustment: number; // e.g., +2.5 or -5
  newWeight: number;
  reason: string;
  type: "INCREASE" | "DECREASE" | "KEEP";
}

export const calculateApre = (
  currentWeight: number,
  repsPerformed: number,
  rpe: number,
  targetRpe: number = 8,
): ApreSuggestion | null => {
  // 1. Calculate Estimated 1RM from this set
  // Epley Formula: w * (1 + r/30)
  // We adjust the RPE factor: Real Reps + Reps In Reserve (RIR)
  // RIR ~= 10 - RPE
  const rir = 10 - rpe;
  const projectedMaxReps = repsPerformed + rir;

  // 2. Logic: Compare RPE divergence
  // If RPE was > 1 point off target, suggest fix.
  const diff = targetRpe - rpe; // Positive = Too Easy, Negative = Too Hard

  if (Math.abs(diff) < 1) return null; // Accurate enough

  let adjustment = 0;
  let reason = "";
  let type: "INCREASE" | "DECREASE" | "KEEP" = "KEEP";

  // Simplistic Linear Adjustment based on RPE points
  // Typically 1 RPE point ~= 2-3% load change.
  const pctChange = diff * 0.025; // 2.5% per RPE point

  // Round to nearest 2.5kg (standard plates)
  adjustment = Math.round((currentWeight * pctChange) / 2.5) * 2.5;

  // Sanity caps
  if (adjustment > 10) adjustment = 10;
  if (adjustment < -10) adjustment = -10;

  if (adjustment === 0) return null;

  if (adjustment > 0) {
    reason = `RPE ${rpe} was too easy (Target ${targetRpe}).`;
    type = "INCREASE";
  } else {
    reason = `RPE ${rpe} was too high (Target ${targetRpe}).`;
    type = "DECREASE";
  }

  return {
    adjustment,
    newWeight: currentWeight + adjustment,
    reason,
    type,
  };
};
